// compute normal from gradient vector
vec3 getNormal(vec3 p){

	vec2 d = vec2(0.0,0.001);
    float mc = map(p);
    return -normalize(vec3(
        mc-map(p+d.yxx),
        mc-map(p+d.xyx),
        mc-map(p+d.xxy)
    ));

}

float getShadowFrac(vec3 lo,vec3 ld,float maxt){

    float res=1.;
    float t =0.1;
    float lds=0.;

    for(float i=0.;i<64.;i++){
        // trace forward
       lds =map(lo+t*ld);

       res=min(lds*128./t,res);

       t+=clamp(lds,0.0001,0.4);

       if(t>=maxt||t<0.0001){
          break;
       }

    }
    return res;
}

vec3 rayHit(Ray ray,vec3 lightPos){
    float t=1.;

    float nl =0.;

    vec3 p=vec3(0);
    vec3 l2p =vec3(0);

    float lp=0.;
    float isHit=0.;
    float hit =0.;

    for(float i=0.;i<FAR;i++){
       p=ray.o+t*ray.d;

       hit =map(p);

       l2p= p-lightPos;
       if(hit<=0.006){

           nl =clamp(dot(normalize(lightPos),getNormal(p)),0.2,1.);
           lp=getShadowFrac(lightPos,normalize(l2p),length(l2p)-0.3);
           nl*=lp;

           nl+=pow(abs(nl),20.); 
           isHit=1.;    
           break;
       }

       if(t>=20.){

          break;
       }
       t+=hit;
    }


    return vec3(nl,t/12.,isHit);
}


