    Ray ray; 
    ray.o= eye;
    ray.d =normalize(screen-ray.o);
    ray.o*=rotX(PI*0.02);
    ray.d*=rotX(PI*0.02);

    ray.d*=rotY(PI*0.1*sin(iTime*0.2));


    vec3 eyeHit= rayHit(ray,lightPos);
    if(eyeHit.z<=0.){
       drawBg(ray.o+ray.d*80.,col);
    }


    col  +=vec3(eyeHit.x);
    col= mix(col,vec3(0.4,0.8,1)*eyeHit.y,0.5);

