const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 600.0;
const float EPSILON = 0.0001;
const float NORMAL_DELTA = 0.1;
const float PI = 3.1415926535897932384626433832795;
const float MUSHROOM_RADIUS = 10.;
const float EARTH_RADIUS = 200.;



const int NO_HITS = -1;
const int THING = 1;
const int SKY = 3;
const int EARTH = 4;

struct Ray {
    vec3 eye;
    vec3 dir;
};

struct HitInfo {
    int object;
    float dist;
};

struct Material {
    vec3 ambient;
    vec3 diffuse;
    vec3 specular;
    float shininess;
};

struct Light {
    vec3 pos;
    vec3 color;
};

// ramp function with period = 2
float ramp(float x) {
    return 2. * mod(x, 1.) - 1.;
}

// square function with on/off values a & b, period 2
float square(float a, float b, float x) {
    return mix(a, b, step(ramp(x), 0.));
}

// http://www.iquilezles.org/www/articles/smin/smin.htm
float smin(float a, float b, float k) {
    float h = clamp(.5 + .5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

float smax(float a, float b, float k) {
    float h = clamp(.5 + .5 * (b - a) / k, 0., 1.);
    return mix(a, b, h) + k * h * (1. - h);
}

/**
 * Constructive solid geometry intersection operation on SDF-calculated distances.
 */
HitInfo intersectH(const in HitInfo a, const in HitInfo b) {
    if (a.dist > b.dist) return a;
    return b;
}

/**
 * Constructive solid geometry union operation on SDF-calculated distances.
 */
HitInfo unionH(const in HitInfo a, const in HitInfo b) {
    if (a.dist < b.dist) return a;
    return b;
}


HitInfo unionSH(const in HitInfo a, const in HitInfo b) {
    return HitInfo(a.dist < b.dist ? a.object : b.object, smin(a.dist, b.dist, .5));
}

HitInfo unionS1(const in HitInfo a, const in HitInfo b) {
    return HitInfo(a.dist < b.dist ? a.object : b.object, smin(a.dist, b.dist, 1.));
}

HitInfo unionS2(const in HitInfo a, const in HitInfo b) {
    return HitInfo(a.dist < b.dist ? a.object : b.object, smin(a.dist, b.dist, 2.));
}

/**
 * Constructive solid geometry difference operation on SDF-calculated distances.
 */
HitInfo differenceH(const in HitInfo a, const in HitInfo b) {
    if (a.dist > - b.dist) return a;
    return HitInfo(b.object, -b.dist);
}

HitInfo differenceSH(const in HitInfo a, const in HitInfo b) {
    return HitInfo(a.dist > - b.dist ? a.object : b.object, smax(a.dist, -b.dist, .5));
}

HitInfo differenceS1(const in HitInfo a, const in HitInfo b) {
    return HitInfo(a.dist > - b.dist ? a.object : b.object, smax(a.dist, -b.dist, 1.));
}

HitInfo differenceS2(const in HitInfo a, const in HitInfo b) {
    return HitInfo(a.dist > - b.dist ? a.object : b.object, smax(a.dist, -b.dist, 2.));
}


float sphereSDF(const in vec3 p) {
    return length(p) - 1.0;
}


float plateSDF(const in vec2 p, const in vec2 dim) {
    vec2 d = (abs(p) - dim);
    return max(d.x, d.y);
}

float cylinderSDF(const in vec3 p, const in vec2 h) {
    return plateSDF(vec2(length(p.xy), p.z), h);
}


vec3 rotateX(const in vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    return vec3(p.x, p.y*c - p.z*s, p.y*s + p.z*c);
}

vec3 rotateZ(const in vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    return vec3(p.x*c - p.y*s, p.x*s + p.y*c, p.z);
}

vec3 repeatE(const in vec3 p) {
    float a = mod(atan(p.y, p.z) + PI, PI / 8.) - PI / 16.;
    float r = length(p.yz);
    return vec3(p.x, r*sin(a), r*cos(a));
}


vec3 moveTo(const vec3 p, vec2 pos) {
    vec3 coe = vec3(0, 0, -EARTH_RADIUS);
    // 0: latitude, 1: longitude
    return repeatE(rotateX(rotateZ(p - coe, radians(pos[1])), radians(90. - pos[0]))) + coe;
}


HitInfo mushroom(in HitInfo ground, const in vec3 p, const in vec2 p0) {
    vec3 q;
    const float rob = MUSHROOM_RADIUS;
    const float rou = .5 * MUSHROOM_RADIUS;
    const float rol = .6 * MUSHROOM_RADIUS;
    const vec2 cdim = vec2(.5, 6.);

    q = moveTo(p, p0)  + vec3(.0, .0, .6 * rob - cdim[1]);
    HitInfo mc = HitInfo(THING, cylinderSDF(q, cdim));
    ground = unionS2(ground, mc);

    q += vec3(.0, .0, -cdim[1] + rou * .95);
    HitInfo mhu = HitInfo(THING, sphereSDF(q / rou) * rou);
    q += vec3(.0, .0, mix(1.5, 2.0, 1. + sin(2.*iTime)));
    HitInfo mhl = HitInfo(THING, sphereSDF(q / rol) * rol);

    return unionH(ground, differenceSH(mhu , mhl));

}


HitInfo ground(in HitInfo earth, const in vec3 p, const in vec2 p0) {
    vec3 q;
    const float rob = MUSHROOM_RADIUS;

    q = moveTo(p, p0)  - vec3(.0, .0, .4 * rob);
    HitInfo mb = HitInfo(EARTH, sphereSDF(q / rob) * rob);

    return differenceS2(earth, mb);
}

HitInfo sceneH(const in vec3 p) {
    vec3 q;

    vec3 coe = vec3(0., 0., -EARTH_RADIUS);

    vec2 p1 = vec2(70, 90) + iTime * vec2(-3.3, 3.4);
    vec2 p2 = vec2(70, -30) + iTime * vec2(3.9, -3.4);
    vec2 p3 = vec2(70, 60) + iTime * vec2(3.6, 3.2);

    q = p - coe;
    HitInfo earth = HitInfo(EARTH, sphereSDF(q / EARTH_RADIUS) * EARTH_RADIUS);

    earth = ground(earth, p, p1);
    earth = ground(earth, p, p2);
    earth = ground(earth, p, p3);

    earth = mushroom(earth, p, p1);
    earth = mushroom(earth, p, p2);
    earth = mushroom(earth, p, p3);

    float sky_radius = 2. * EARTH_RADIUS;
    HitInfo sky = HitInfo(SKY, - sphereSDF(p / sky_radius) * sky_radius);

    return unionH(sky, earth);

}

float sceneSDF(const in vec3 samplePoint) {
    HitInfo hit = sceneH(samplePoint);
    return hit.dist;
}

HitInfo raymarch(const in Ray ray, float start, float end) {
    float depth = start;
    HitInfo hit;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        hit = sceneH(ray.eye + depth * ray.dir);
        if (hit.dist < EPSILON) {
            return HitInfo(hit.object, depth);
        }
        depth += hit.dist;
        if (depth >= end) {
            return HitInfo(NO_HITS, 0.);
        }
    }
    return HitInfo(NO_HITS, 0.);
}




vec3 estimateNormal(vec3 p) {
    // assuming we are on the surface where sceneSDF ~ 0
    return normalize(vec3(
        sceneSDF(vec3(p.x + NORMAL_DELTA, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + NORMAL_DELTA, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z + NORMAL_DELTA))
    ));
}


vec3 getColorGeom(const in Light light, vec3 p, vec3 N, vec3 V, const in Material mat) {

    vec3 c = vec3(0., 0., 0.);

    vec3 L = normalize(light.pos - p);

    float d = dot(L, N);

    if (d < 0.) return c;

    c += d * light.color * mat.diffuse;

    // Blinn-Phong
    vec3 H = normalize(L + V);
    float k = dot(H, N);
    if (k < 0.) return c;

    c += pow(k, mat.shininess) * light.color * mat.specular;

    return c;
}



Material getMaterial(int object, vec3 p, vec3 n) {
    Material mat;
    mat.ambient = vec3(.1, .1, .1);
    mat.specular = vec3(.6, .6, .6);
    mat.shininess = 10.;
    if (object == THING) {
        mat.diffuse = square(1., 2., p.x * 10.) * square(.5, 1., p.z * 10.) * vec3(.2, .9, .1);
    } else if (object == SKY) {
        mat.diffuse = vec3(.1, .1, .3);
    } else if (object == EARTH) {
        mat.diffuse = square(.5, 1.4, p.x) * square(.5, .7, p.y) * vec3(.56, .09, .03);
    } else {
        mat.diffuse = vec3(.0, .0, .0);
    }

    return mat;
}

vec3 getSkyGradient(const in vec3 dir)
{
    const vec3 sky = .15 * vec3(13./255., 214./255., 208./255.);
    float blend = dir.z * 0.5 + 0.5;
    return mix(vec3(.0), sky, blend);
}

// use distance field to evaluate ambient occlusion
float getAmbientOcclusion(const in vec3 p, const in vec3 n)
{
    float occl = 1.;
    float dist = 0.;

    for (int i = 0; i < 5; i++) {
        dist += .1;
        float sdist = sceneSDF(p + n * dist);
        occl *= 1. - max(0., (dist - sdist) * .4 / dist);
    }
    return occl;
}

float getShadow(const in vec3 l, const in vec3 p) {
    float dist = length(p - l);

    Ray ray;
    ray.dir = (p - l) / dist;
    ray.eye = l;

    HitInfo hit = raymarch(ray, MIN_DIST, MAX_DIST);
    if (hit.object == NO_HITS) {
        return 0.;
    }

    return mix(.2, 1., step(dist - 0.01, hit.dist));
}



vec3 getColor(const in Ray ray, const in HitInfo hit) {

    if (hit.object == SKY) {
        vec3 c = getSkyGradient(ray.dir);
        return pow(10. * c, vec3(1.8));
    }

    Light light;
    vec3 baseColor = vec3(.8);

    vec3 p = ray.eye + hit.dist * ray.dir;
    vec3 n = estimateNormal(p);

    Material mat = getMaterial(hit.object, p, n);

    float ambientOcclusion = getAmbientOcclusion(p, n);
    vec3 c = getSkyGradient(n) * ambientOcclusion;

    c += getSkyGradient(reflect(ray.dir, n)) * ambientOcclusion;

    // light.pos = vec3(4*sin(iTime), 4*cos(iTime), 2);
    // c += getColorGeom(light, p, n, -ray.dir, mat);

    light.pos = vec3(4.*sin(.37 * iTime), 4.*cos(.37 * iTime), 20.);
    light.color = getShadow(light.pos, p) * baseColor;
    c += getColorGeom(light, p, n, -ray.dir, mat);

    return c;
}

mat4 windowToView(const in vec2 dim) {
	mat4 m = mat4(1.);

    const float ct = 1. / tan(radians(22.5));
    const float n = 1.;
    const float f = 500.;
    const float d = (n + f) / (n - f);
    const float e = 2. * n * f / (n - f);

    float a = dim.x / dim.y;

    m[0][0] = a / ct;
    m[1][1] = 1. / ct;
    m[2][2] = 0.;
    m[3][3] = d / e;
    m[3][2] = -1.;
    m[2][3] = 1. / e;

    return m;
}

mat4 viewToWorld(const in vec3 eye) {
    const vec3 up = vec3(0., -1., 1.);
    const vec3 center = vec3(0.);

    vec3 z = normalize(eye - center);
    vec3 y = normalize(up - dot(z, up) * z);
    vec3 x = cross(y, z);

    return mat4(vec4(x, 0.),
                vec4(y, 0.),
                vec4(z, 0.),
                vec4(eye, 1.));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    mat4 inv_proj = windowToView(iResolution.xy);
    vec4 p1 = vec4(2. * fragCoord / iResolution.xy - 1., 1., 1.);
    vec3 p2 = (inv_proj * p1).xyz;

    vec3 eye = vec3(50., 70., 10.);
    float zoom = .6;
    mat4 inv_cam = viewToWorld(zoom * eye);

    Ray ray;
    ray.dir = normalize((inv_cam * vec4(p2, .0)).xyz);
    ray.eye = inv_cam[3].xyz;

    HitInfo hit = raymarch(ray, MIN_DIST, MAX_DIST);

    if (hit.object == NO_HITS) {
        // Didn't hit anything
        fragColor = vec4(0., 0., .0, .0);
        return;
    }

    fragColor = vec4(getColor(ray, hit), 1);
}

#define SPHERE 0
#define TERRAIN 1
#define SEALEVEL 3
#define NO_INTERSECT 2
#define HELIX 5
#define CLOUD 4

#define LOD_NORM 9
#define LOD_RAY 4

#define SEALEVEL_HEIGHT 5.0
#define CLOUD_HEIGHT_MIN 70.0
#define CLOUD_HEIGHT_MAX 74.0

#define AA  0

// Data structure for raymarching results
struct PrimitiveDist {
    float dist;
    int primitive; // Can be SPHERE, TERRAIN, or NO_INTERSECT
};

float hash21(vec2 p) {
    float h = dot(vec2(p),vec2(127.1,311.7));
    return fract(sin(h)*43758.5453123) * 2.0 - 1.0;
}

float hash31(vec3 p) {
    p = 50.0*fract( p*0.3183099 + vec3(0.71,0.113,0.419));
    return -1.0+2.0*fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float valueNoise(vec2 p) {
    vec2 pi = floor(p);
    vec2 pf = fract(p);
    vec2 u = pf*pf*pf*(6.0*pf*pf - 15.0*pf + 10.0);

    float va = hash21(pi + vec2(0.0, 0.0));
    float vb = hash21(pi + vec2(1.0, 0.0));
    float vc = hash21(pi + vec2(0.0, 1.0));
    float vd = hash21(pi + vec2(1.0, 1.0));

    return mix(mix(va, vb, u.x), mix(vc, vd, u.x), u.y);
}

float valueNoise(vec3 p) {
    vec3 pi = floor(p);
    vec3 pf = fract(p);
    vec3 u = pf*pf*pf*(6.0*pf*pf - 15.0*pf + 10.0);

    float va = hash31(pi + vec3(0.0, 0.0, 0.0));
    float vb = hash31(pi + vec3(1.0, 0.0, 0.0));
    float vc = hash31(pi + vec3(0.0, 1.0, 0.0));
    float vd = hash31(pi + vec3(1.0, 1.0, 0.0));
    float ve = hash31(pi + vec3(0.0, 0.0, 1.0));
    float vf = hash31(pi + vec3(1.0, 0.0, 1.0));
    float vg = hash31(pi + vec3(0.0, 1.0, 1.0));
    float vh = hash31(pi + vec3(1.0, 1.0, 1.0));

    return mix(mix(mix(va, vb, u.x), mix(vc, vd, u.x), u.y),
               mix(mix(ve, vf, u.x), mix(vg, vh, u.x), u.y), u.z);
}

float fbmTerrain(vec2 p, int level) {
    float amp = 0.51;
    float freq = 0.03;
    float sum = 0.0;

    for (int i = 0; i < level; i++) {
        float vNoise = valueNoise(p * freq);
        sum += amp * vNoise;
        freq *= 2.01;
        amp *= -0.49;
    }
    sum = pow(sum, 2.0);
    return sum * 45.0;
}

float fbmCloud(vec3 p, int level) {
    float amp = 0.51;
    float freq = 0.03;
    float sum = 0.0;

    for (int i = 0; i < level; i++) {
        float vNoise = valueNoise(p * freq);
        sum += amp * vNoise;
        freq *= 2.01;
        amp *= -0.49;
    }

    sum = (sum + 1.) * .5;
    return sum;
}

float fbmSealevel(vec3 p, int level) {
    float amp = 0.51;
    float freq = 0.03;
    float sum = 0.0;

    for (int i = 0; i < level; i++) {
        float vNoise = valueNoise(p * freq);
        sum += amp * vNoise;
        freq *= 2.01;
        amp *= -0.49;
    }
    return sin(sum * 10.);
}

// Signed distance to the twisted sphere.
float sdTwistedSphere(vec3 p) {
    vec3 spherePosition = vec3(0.0, 2.0, 0.0);
    float radius = 1.5;
    float primitive = length(p - spherePosition) - radius;
    return primitive;
}

float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}

float mapTerrain(vec3 p, int level) {
	float dist = p.x*p.x + p.z*p.z;
    float delta = 0.;
    float h = fbmTerrain(p.xz, level);

    if (dist > 1600.) delta = - h * 1.0;
    else delta = - h + exp(-dist/500.-1.)*50.;


    return p.y -4. + delta;
}

float mapSealevel(vec3 p, int level) {
    return p.y - SEALEVEL_HEIGHT - fbmSealevel(vec3(p.xz * .1, iTime * .5) * 5., level);
}

const float theta = 3.14 * 2. / 16.;
const mat2 rotate2D = mat2(cos(theta), sin(theta), -sin(theta), cos(theta));

float mapHelix(vec3 p) {
    p.y = mod(p.y, 22.2);
    vec3 offset = vec3(7., 3., 0.);
    float dh = 1.2;
    vec3 dimen = vec3(2.5, .1, 1.);
    float res = 100000.0;
    for (int i=0; i<16; i++) {
        res = min(res, udBox(p - offset, dimen)); p.xz = rotate2D * p.xz; p.y -= dh;
    }
	return res;
}

float mapCloud(vec3 p) {
    if (p.y < CLOUD_HEIGHT_MAX && p.y > CLOUD_HEIGHT_MIN) return -1.;
	return min(abs(p.y - CLOUD_HEIGHT_MIN), abs(p.y - CLOUD_HEIGHT_MAX));
}

PrimitiveDist map(vec3 p) {
    float terrainDist = mapTerrain(p, LOD_RAY);
    float sealevelDist = mapSealevel(p, LOD_RAY - 1);
    float helixDist = mapHelix(p);
    //float cloudDist = mapCloud(p);
    PrimitiveDist res = PrimitiveDist(terrainDist, TERRAIN);
    float dist = res.dist;
    float which = float(res.primitive);
    //if (cloudDist < res.dist) res = PrimitiveDist(cloudDist, CLOUD);
    which = mix(float(SEALEVEL), which, step(dist, sealevelDist));
    dist = mix(sealevelDist, dist, step(dist, sealevelDist));
    which = mix(float(HELIX), which, step(dist, helixDist));
    dist = mix(helixDist, dist, step(dist, helixDist));

    //if (sealevelDist < res.dist) res = PrimitiveDist(sealevelDist, SEALEVEL);
    //if (helixDist < res.dist) res = PrimitiveDist(helixDist, HELIX);

    return PrimitiveDist(dist, int(which));

}

PrimitiveDist mapUnderSea(vec3 p) {
    float terrainDist = mapTerrain(p, LOD_RAY);
    float helixDist = mapHelix(p);

    PrimitiveDist res = PrimitiveDist(terrainDist, TERRAIN);

    if (helixDist < res.dist) res = PrimitiveDist(helixDist, HELIX);

    return res;
}

const float epsilon = 0.01;
vec2 e = vec2(epsilon, 0.0); // For swizzling
vec3 calcNormal(vec3 p, int which) {
    vec3 norm = vec3(0.);
    if (which == TERRAIN) {
        norm.x = mapTerrain(p + e.xyy, LOD_NORM) - mapTerrain(p - e.xyy, LOD_NORM);
        norm.y = mapTerrain(p + e.yxy, LOD_NORM) - mapTerrain(p - e.yxy, LOD_NORM);
        norm.z = mapTerrain(p + e.yyx, LOD_NORM) - mapTerrain(p - e.yyx, LOD_NORM);
    } else if (which == SEALEVEL){
        norm.x = mapSealevel(p + e.xyy, LOD_NORM) - mapSealevel(p - e.xyy, LOD_NORM);
        norm.y = mapSealevel(p + e.yxy, LOD_NORM) - mapSealevel(p - e.yxy, LOD_NORM);
        norm.z = mapSealevel(p + e.yyx, LOD_NORM) - mapSealevel(p - e.yyx, LOD_NORM);
    } else {
        norm.x = map(p + e.xyy).dist - map(p - e.xyy).dist;
        norm.y = map(p + e.yxy).dist - map(p - e.yxy).dist;
        norm.z = map(p + e.yyx).dist - map(p - e.yyx).dist;
    }

    return normalize(norm);
}


PrimitiveDist raymarchUnderSea(vec3 ro, vec3 rd, float maxDist, float marchSpeed) {
    float marchDist = 0.001;
    float boundingDist = maxDist;
    float threshold = 0.1;

    // Fill in the iteration count
    for (int i = 0; i < 100; i++) {
        // Fill in loop body
        vec3 pos = ro + rd * marchDist;
        PrimitiveDist near = mapUnderSea(pos);
        if (near.dist < threshold)
            return PrimitiveDist(marchDist, near.primitive);
        marchDist += near.dist * marchSpeed;
        if (marchDist > boundingDist) break;
    }

    return PrimitiveDist(-1.0, NO_INTERSECT);
}

PrimitiveDist raymarch(vec3 ro, vec3 rd, float maxDist, float marchSpeed) {
    float marchDist = 0.001;
    float boundingDist = maxDist;
    float threshold = 0.1;

    // Fill in the iteration count
    for (int i = 0; i < 200; i++) {
        // Fill in loop body
        vec3 pos = ro + rd * marchDist;
        PrimitiveDist near = map(pos);
        if (near.dist < threshold)
            return PrimitiveDist(marchDist, near.primitive);
        marchDist += near.dist * marchSpeed;
        if (marchDist > boundingDist) break;
    }

    if ((ro.y < CLOUD_HEIGHT_MIN && rd.y > 0.) || (ro.y > CLOUD_HEIGHT_MAX && rd.y < 0.)) 
        return PrimitiveDist(-1.0, CLOUD);
    return PrimitiveDist(-1.0, NO_INTERSECT);
}

vec3 generateTerrainColor(vec3 pos, vec3 norm, vec3 lig) {
    vec3 col = vec3(0.0);

    vec3 lightGreen = vec3(0.564, 0.833, 0.564);
    vec3 lawnGreen = vec3(0.498, 0.99, 0);
    vec3 springGreen = vec3(0.0, 1.0, 0.5);
    vec3 greenYellow = vec3(.678, 0.99, .184);
    vec3 olive = vec3(0.5, 0.5, 0.0);

    vec3 grass1 = vec3(.125, .365, .0);
    vec3 grass2 = vec3(.16, .274, .141);
    vec3 grass3 = vec3(.0, .2, .0);
    vec3 grass4 = vec3(.45, .6, .0);

    vec3 earth      = vec3(.824, .706, .549);
    vec3 calcaire   = vec3(.624, .412, .118);
    vec3 rocks      = vec3(.412, .388, .422);
    vec3 beach      = vec3( 1.0, .894, .710);

    float posNoise = valueNoise(pos.xz) + 1.0 * 0.5;
    float darkness = 1.;

    // Ambient
    float ambient = 0.2;
    // Diffuse
    float diffuse = clamp(dot(norm, lig), 0.0, 1.0);

    // Shadow
    /*
    if (dot(norm, lig) > 0.) {
        PrimitiveDist res = raymarch(pos + norm * .8, lig, 30., .8);
        vec3 shadPos = pos + res.dist * lig;
        if (res.primitive == HELIX || res.primitive == TERRAIN) {
            darkness = smoothstep(0., 30., res.dist) * .8;
        }
    }*/


    // base color
    col = mix (        beach,    earth, smoothstep(0.0 , 0.08 , pos.y) );
    col = mix ( col, calcaire, smoothstep(0.08, 0.3 , pos.y) );
    col = mix ( col,    rocks, smoothstep(0.3, 10.0  , pos.y) );

    return (ambient + diffuse * col) * darkness;
}

vec3 generateHelixColor(vec3 ro, vec3 rd, vec3 norm, vec3 lig) {
    float ambient = 0.1;
    float diffuse = clamp(dot(norm, lig), 0.0, 1.0);
    float specular = pow(clamp(dot(rd, reflect(lig, norm)), 0.0, 1.0), 16.);

    vec3 col = vec3(1., 1., .93);
    return ambient + col * (diffuse + specular);
}

vec3 generateSeaColor(vec3 ro, vec3 rd, vec3 norm, vec3 lig) {
    float ndotr = dot(norm, rd);
    float r0 = .6;
    r0 = min(r0 + .3 * max(valueNoise(vec3(ro.xz * 5., iTime * 10.)) - .2, 0.), 1.0);
    float fresnel = r0 + (1. - r0) * pow(1.0 - abs(ndotr), 5.);
    vec3 col = vec3(.292, .434, .729), reflCol, refrCol;
    float darkness = 1.;

    // Reflection
    vec3 reflD = reflect(rd, normalize(norm + vec3(.0, .3, .0)));
    PrimitiveDist res = raymarch(ro, reflD, 100., .5);
    vec3 reflPos = ro + res.dist * reflD;
    if (res.primitive == TERRAIN) {
        reflCol = generateTerrainColor(reflPos, calcNormal(reflPos, TERRAIN), lig);
    } else if (res.primitive == HELIX) {
    	reflCol = generateHelixColor(reflPos, reflD, calcNormal(reflPos, HELIX), lig);
    }

    // Refraction
    vec3 refrD = refract(rd, vec3(0., 1., 0.), .8);
    res = raymarchUnderSea(ro, refrD, 20., .8);
    vec3 refrPos = ro + res.dist * refrD;
    //refrCol = texture(iChannel1, refrPos.xz * 2.).xyz;
    if (res.primitive == TERRAIN) {
        refrCol = generateTerrainColor(refrPos, calcNormal(refrPos, TERRAIN), lig);
    } else if (res.primitive == HELIX) {
    	refrCol = generateHelixColor(refrPos, refrD, calcNormal(refrPos, HELIX), lig);
    }

    col = mix(col, fresnel * reflCol + (1. - fresnel) * refrCol, .8);

    // Shadow
    res = raymarch(ro + vec3(0, .15, 0.), lig, 30., .5);
    vec3 shadPos = ro + res.dist * lig;
    if (res.primitive == HELIX || res.primitive == TERRAIN) {
		darkness = 1. - 1. / (1. + res.dist * .1 + res.dist * .005);
    }

    float ambient = 0.2;
    float diffuse = clamp(dot(norm, lig), 0.0, 1.0);
    float specular = pow(clamp(dot(rd, reflect(lig, norm)), 0.0, 1.0), 32.);

    return (ambient + col * (diffuse + specular)) * darkness;
}

vec3 generateCloudColor(vec3 ro, vec3 rd) {
    vec3 bgc = vec3(0.3, .55, .8);
    vec4 sum = vec4(0.);
    float dist;
    if (ro.y < CLOUD_HEIGHT_MIN) {
    	dist = (CLOUD_HEIGHT_MIN - ro.y) / rd.y + .01;
        ro = ro + dist * rd;
    } else if (ro.y > CLOUD_HEIGHT_MAX) {
    	dist = -(ro.y - CLOUD_HEIGHT_MAX) / rd.y + .01;
        ro = ro + dist * rd;
    }

    vec3 cloud1 = vec3(1.0,0.95,0.8);
    vec3 cloud2 = vec3(0.25,0.3,0.35);

    //rd.y = max(rd.y, .2);
    rd.y = sqrt(abs(rd.y)) * sign(rd.y);
    //rd = normalize(rd);

    while ((sum.a < 1.0) && (ro.y > CLOUD_HEIGHT_MIN) && (ro.y < CLOUD_HEIGHT_MAX)) {
    	float density = fbmCloud(ro * rd.y, LOD_NORM) * .1;
        vec3 col = mix(cloud1, cloud2, density);
        sum.a += density;
        sum.rgb += col * density * density * 15.;
        ro = ro + rd * .5;
    }
    bgc = mix(bgc, sum.rgb, sum.a);
	return bgc;
}

vec3 render(vec3 ro, vec3 rd, float t, int which) {

    // Col is the final color of the current pixel.
    vec3 col = vec3(0.);
    vec3 pos = ro + rd * t;
    // Light vector
    vec3 lig = normalize(vec3(0.5,0.5,0.5));
    vec3 lightCol = vec3(1., 1., 1.);

    // Normal vector
    vec3 nor = calcNormal(pos, which);

    // TODO [Task 5] Assign different intersected objects with different materials
    // Make things pretty!
    vec3 material = vec3(0.0);
    if (which == TERRAIN) {
        //material = texCube(iChannel0, pos, nor);
        col = generateTerrainColor(pos, nor, lig);    
    } else if (which == HELIX) {
        col = generateHelixColor(pos, rd, nor, lig);
    } else if (which == SEALEVEL){
      	col = generateSeaColor(pos - rd * .05, rd, nor, lig);
    } else if (which == CLOUD) {
    	col = generateCloudColor(ro, rd);
    }

    // Applying the phong lighting model to the pixel.
    //col += vec3(((ambient + diffuse + specular) * darkness));
    //col += vec3(ambient + diffuse + specular);

    return col;
}

vec3 postProcess(vec3 col, vec2 uv) {
    // Rain
    vec2 rainUV = (uv * vec2(5., .3) + vec2(.8 * iTime - (uv.x + uv.y) * 1., .8 * iTime)) * 30. ;
    float rain = max(valueNoise(rainUV), 0.) * .5;
    col = mix(col, vec3(rain), .1);  
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float height = iTime * 2.;

    vec3 rayOrigin = vec3(20.0 * sin(iTime * .1), 20., 20.0 * cos(iTime * .1));

    float focalLength = 2.0;

    // The target we are looking at
    vec3 target = vec3(0.0, 10., 0.0);
    // Look vector
    vec3 look = normalize(rayOrigin - target);
    // Up vector
    vec3 up = vec3(0.0, 1.0, 0.0);

    // Set up camera matrix
    vec3 cameraForward = -look;
    vec3 cameraRight = normalize(cross(cameraForward, up));
    vec3 cameraUp = normalize(cross(cameraRight, cameraForward)); 

    // Construct the ray direction vector
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x = uv.x * iResolution.x / iResolution.y;

    vec3 col = vec3(0.);

    // AA
    if (AA == 1) {
        vec2 uvSize = 2. / iResolution.yx;

        for (int dx = -1; dx <= 1; dx += 2) {
            for (int dy = -1; dy <= 1; dy += 2) {
            	vec2 puv = uv + vec2(float(dx) * uvSize.x * .25, float(dy) * uvSize.y * .25);
                    vec3 rayDirection = vec3(puv, focalLength);
                    rayDirection = normalize(puv.x * cameraRight + puv.y * cameraUp + focalLength * cameraForward);

                    PrimitiveDist rayMarchResult = raymarch(rayOrigin, rayDirection, 500., .5);
                    vec3 tcol = vec3(0.3, .55, .8);
                    if (rayMarchResult.primitive != NO_INTERSECT) {
                      tcol = render(rayOrigin, rayDirection, rayMarchResult.dist, rayMarchResult.primitive);
                    }

                    // Postprocess
    				tcol = postProcess(tcol, uv);
                col += tcol;
            }
        }
        col *= .25;
    } else if (AA == 0) {
        vec3 rayDirection = vec3(uv, focalLength);
        rayDirection = normalize(uv.x * cameraRight + uv.y * cameraUp + focalLength * cameraForward);

        PrimitiveDist rayMarchResult = raymarch(rayOrigin, rayDirection, 500., .5);

        if (rayMarchResult.primitive != NO_INTERSECT) {
            col = render(rayOrigin, rayDirection, rayMarchResult.dist, rayMarchResult.primitive);
        }

        // Postprocess
        col = postProcess(col, uv);
    }

    fragColor = vec4(col, 1.0);
}

/*

	Mobius Weave
	------------

	A Mobius spiral with a pseudo random weave. The idea came from Fabrice's cool "Round Weaving" 
	example. Fabrice always comes up with clever little shaders, and I have a habit of viewing
	them, then going off on a tangent for a few hours. :) 

	His "Round Weaving" shader essentially involves applying something along the lines of a polar 
	transform to a standard cross-weave pattern. Although visually different, this is just an extension 
	of that with some window dressing applied. Instead of the standard over under weave, I've psuedo-
	randomized it, and have substituted the polar transform for a reasonably well known Mobius spiral 
	transform, which is polar in nature, but a little bit fancier.

	Inspired by:

    // Slightly different, but basically a polar transformed cross weave. As with a lot of Fabrice's
	// shaders, it's very concisely written - Roughly a couple of tweets.
	round weaving - FabriceNeyret2
	https://www.shadertoy.com/view/MtffzB

	Other related examples:

    // Very cool, much more complex (no pun intended) example. Mobius spirals are pretty easy;
	// Packing circles into them is less so. :)
	Doyle spirals - knighty
	https://www.shadertoy.com/view/4tffDH

	// A simplified circle packed spiral.
	Doyle spiral - ws
	https://www.shadertoy.com/view/MtffDn

	// Just the basics - for anyone who wants to make a Mobius spiral, or whatever they're
	// technically called.
	Logarithmic Mobius Transform - Shane
	https://www.shadertoy.com/view/4dcSWs

*/


// Standard 2D rotation formula - See Nimitz's comment.
mat2 r2(in float a){ float c = cos(a), s = sin(a); return mat2(c, s, -s, c); }


// Standard Mobius transform: f(z) = (az + b)/(cz + d). Slightly obfuscated.
vec2 Mobius(vec2 p, vec2 z1, vec2 z2){

	z1 = p - z1; p -= z2;
	//return vec2(dot(z1, p), z1.y*p.x - z1.x*p.y)/dot(p, p);
    // Equivalent to the line above. Fabrice tidied it up. I'd imagine the grouped
    // operations might make it a little quicker, but don't quote me on that. :)
    return mat2(z1, z1.y, -z1.x)*p/dot(p, p); 
}


// Standard spiral zoom.
vec2 spiralZoom(vec2 p, vec2 offs, float n, float spiral, float zoom, vec2 phase){

	p -= offs;
	float a = atan(p.y, p.x)/6.283 + iTime/32.;
	float d = length(p);
	//return vec2(a*n + log(d)*spiral, -log(d)*zoom + a) + phase;
    // Equivalent to the line above. Fabrice tidied this one up too. :)
    return mat2(n, 2, spiral,-zoom)*vec2(a, log(d)) + phase;

}

// This is a rewrite of IQ's original. It's self contained, which makes it much
// easier to copy and paste. I've also tried my best to minimize the amount of 
// operations to lessen the work the GPU has to do, but I think there's room for
// improvement.
//
float noise3D(vec3 p){

    // Just some random figures, analogous to stride. You can change this, if you want.
	const vec3 s = vec3(7, 157, 113);

	vec3 ip = floor(p); // Unique unit cell ID.

    // Setting up the stride vector for randomization and interpolation, kind of. 
    // All kinds of shortcuts are taken here. Refer to IQ's original formula.
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);

	p -= ip; // Cell's fractional component.

    // A bit of cubic smoothing, to give the noise that rounded look.
    p = p*p*(3. - 2.*p);

    // Standard 3D noise stuff. Retrieving 8 random scalar values for each cube corner,
    // then interpolating along X. There are countless ways to randomize, but this is
    // the way most are familar with: fract(sin(x)*largeNumber).
    h = mix(fract(sin(h)*43758.5453), fract(sin(h + s.x)*43758.5453), p.x);

    // Interpolating along Y.
    h.xy = mix(h.xz, h.yw, p.y);

    // Interpolating along Z, and returning the 3D noise value.
    return mix(h.x, h.y, p.z); // Range: [0, 1].

}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    // Screen coordinates.
	vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    // Rotation and scaling.
    uv = r2(3.14159/6.*sin(iTime/3.))*uv*1.2;    


/*  
	// Polar transformation. Not as fancy looking, but there for comparison.
    uv = r2(iTime/4.)*uv;
    uv = r2(atan(uv.y, uv.x)*1.)*uv*2.;
    float r = length(uv)*.35;
    vec2 p = uv*16.;
*/ 

/*   
	// Standard patterned weave. Comparitively speaking, not that exciting. :)
	// Comment out the four lines below this block first.
    uv += iTime/16.;
    float r = 1.;
    vec2 p = uv*8.;
*/

    // Shading over the two singularity points. It serves two purposes: One, to give depth,
    // but also to alleviate Moire patterns that tend to build up.
    float r = min(length(uv - vec2(.5, 0)), length(uv - vec2(-.5, 0)));
    //float r = length(uv - vec2(.5, 0))*length(uv - vec2(-.5, 0));


    // Transform the screen coordinates: For anyone not familiar with the order in which
    // you do this, transforms are performed prior to rendering the flat grid.
    //
    // The logarithmic Mobius spiral is one of the more interesting transformations, and 
    // just involves a little math. By the way, these transforms work independently, but tend 
    // to look better coupled together.
    uv = Mobius(uv, vec2(-1, -.5), vec2(.5, 0));
    // Logarithmic spiral zoom. Odd spiral numbers can sometimes put grid patterns out of
    // sync, depending on the situation. Just something to think about if yours seams
    // aren't matching up.
    uv = spiralZoom(uv, vec2(-.5), 4., 3.14159*.2, .5, vec2(-1, 1)*iTime*.125);


    // Scene scaling. 
    const float sc = 4.;
    vec2 p = uv*sc;


    // Overlapping cross construction. Most of it is common sense - Split space up into
    // grid cells. In each cell, draw a horizontal line, then overlap it with a vertical 
    // line. Apply shadows, decorative design, etc.


    // Cell's unique ID.
    vec2 ip = floor(p);

    // Partition space into cells. The line below is equivalent to "p = fract(p) - .5;."
    // It saves a "fract" call, which doesn't matter here, but it can be helpful inside
    // raymarching loops and so forth.
    p -= ip + .5;

    // Due to the nature of the transform, the pattern needs to wrap about the line
    // dividing the two singularity points, so true randomness isn't really possible.
    // However, it's possible use a symmetrical pattern that looks random... Kind of.
    //
    // By the way, I hacked the following together in a hurry, so there's probably a
    // more elegant way to write it. Although, I could probalby say that about most 
    // of my code. :)
    if(mod(ip.x*ip.y*.5 + (ip.x + ip.y + 1.)*.75, sc/2.)>.5) p.xy = p.yx;

    // It'd be nice to really randomize with the following, but artifacts are visible
    // about the line ividing the two singularity points. Hopefully, there's a
    // workaround, but I'm not sure what it is yet.
    //if(fract(sin(dot(ip, vec2(1.373, 113.937)))*43758.5453)>.5) p.xy = p.yx;


    // Horizontal line and vertical lines. Fabrices has cleverly used sinsusoids to
    // do it all, but I needed a little more precission to work with, so went the
    // 2D distance field route.
    float cx = abs(p.y) - .21;
    float cy = abs(p.x) - .21;

    // Rendering the decorated overlapping crosses. A lot of it was made up as I went
    // along. I could probably group a lot of it together, but relatively speaking, 
    // this all pretty easy for the GPU to handle.

    // The patterns that run over the stripes to give it that cheesy yarn-like look. :)
    float pat = clamp(cos((uv.x - uv.y)*6.283*24.)*.35 + .65, 0., 1.)*.8 + .2;
    vec2 uv2 = r2(-3.14159/4.)*uv;
    float pat3 = clamp(cos((uv2.x + uv2.y)*6.283*48.)*.75 + .75, 0., 1.)*.8 + .2;

    // The longitudinal and latitudinal stripes. One for the vertical line and another
    // for the horizontal one.
    float stripeX = clamp(cos(p.y*6.238*6.)*.6 + .57, 0., 1.);
    float stripeY = clamp(cos(p.x*6.238*6.)*.6 + .57, 0., 1.);

    // The background pattern - It's supposed to give the impression that there's some more
    // tightly woven yarn behind the main geometric pattern, or something to that effect.
    vec3 col = vec3(.25)*(1. - pat3*.9)*pat;

    // Rendering the vertical line.
    col = mix(col, vec3(0), (1. -smoothstep(0., .1, cx - .125))*.7); // Drop shadow.
    col = mix(col, vec3(0), 1. -smoothstep(0., .025, cx - .05)); // Sharper border line.
    // Main pattern.
    col = mix(col, vec3(.6)*(cos(p.y*6.283) + 1.)*stripeX*pat, 1. -smoothstep(0., .025, cx)); 
    // Darkening the center, just to tone it down a little.
    col = mix(col, vec3(0), (1. -smoothstep(0., .05, cx + .175))*.65);

    // Rendering the horizonal line, which is just a repeat of the above.
    col = mix(col, vec3(0), (1. -smoothstep(0., .1, cy - .125))*.7);
    col = mix(col, vec3(0), 1. -smoothstep(0., .025, cy - .05));
    col = mix(col, vec3(.6)*(cos(p.x*6.283) + 1.)*stripeY*pat, 1. -smoothstep(0., .025, cy));
    col = mix(col, vec3(0), (1. -smoothstep(0., .05, cy + .175))*.65);


    // Add a bit of noise to give the weave material more of an authentic look. 3D noise
    // was a bit of an indulgence, and not all that necessary, but I thought I'd supply
    // the fake depth information too.
    col *= noise3D(vec3(uv*256., r))*.75 + .75;


    // Artificial depth shading.
    //
    // Applying the shading to give the pattern a bit of depth, and to hide singularity
    // artifacts.
    vec3 fogCol = vec3(0); // Other - very dark - colors work too, but don't look convincing.
    col = mix(col, fogCol, 1./(1. + r*.25 + r*r*8.));
    // Extra fake fog to darken the horizon of those singularities a bit more.
    col = mix(col, fogCol, smoothstep(0., .99, .03/r));


    // Very mild sepia, almost charcoal. I did this to pay hommage to Fabrice's version. :)
    col *= vec3(1.1, 1, .95);


    // Apply a vignette.
    uv = fragCoord/iResolution.xy; 
    col *= pow(16.*uv.x*uv.y*(1. - uv.x)*(1. - uv.y), .25)*1.15;


    fragColor = vec4(sqrt(max(col, 0.)), 1);
}

