float lerp(float a,float b,float v)
{
	//clamp to 0 and 1
	if(v>1.0) v=1.0;
	else if(v<0.0) v=0.0;
	return a*(1.0-v)+b*v;
}

vec4 lerp(vec4 a,vec4 b,float v)
{
	float red = lerp(a.r,b.r,v);
	float green = lerp(a.g,b.g,v);
	float blue = lerp(a.b,b.b,v);
	float alpha = lerp(a.a,b.a,v);
	return vec4(red,green,blue,alpha);
}

