}

vec2 thingPosition(float t) { // from an old thing of mine here: https://www.shadertoy.com/view/Xl33D7
    return vec2(sin(2.2 * t) - cos(1.4 * t), cos(1.3 * t) + sin(-1.9 * t)) * 0.2;
}

