precision highp float;

#define MAX_ITER 128
#define STEP_SIZE .95
#define EPSILON .001

const vec4 red     = vec4 (1.0, 0.0, 0.0, 1.0);
const vec4 green   = vec4 (0.0, 1.0, 0.0, 1.0);
const vec4 blue    = vec4 (0.0, 0.0, 1.0, 1.0);

mat3 rotX (in float a) {float c = cos(a); float s = sin (a); return mat3 (vec3 (1., .0, .0), vec3 (.0, c, s), vec3 (.0, -s, c));}
mat3 rotY (in float a) {float c = cos(a); float s = sin (a); return mat3 (vec3 (c, .0, s), vec3 (.0, 1., .0), vec3 (-s, .0, c));}
mat3 rotZ (in float a) {float c = cos(a); float s = sin (a); return mat3 (vec3 (c, s, .0), vec3 (-s, c, .0), vec3 (.0, .0, 1.));}
mat2 rot2d (in float a) { float c = cos (a); float s = sin (a); return mat2 (vec2 (c, s), vec2 (-s, c)); }

vec4 gradient (float v) {
    float steps = 2.;
    float step = 1. / steps;
    vec4 col = green;

    if (v >= .0 && v < step) {
        col = mix (green, blue, v * steps);
    } else if (v >= step && v < 2.0 * step) {
        col = mix (blue, red, (v - step) * steps);
    }

    return col;
}

// basic sdf toolbox
vec3 opRepeat (in vec3 p, in vec3 size) {return mod (p, 2. * size) - size;}
float sdTorus (in vec3 p, in vec2 t) { vec2 q = vec2 (length (p.xz) - t.x, p.y); return length (q) - t.y; }

// one single truchet-cell with the three tori
// t.x -> torus radius
// t.y -> torus "thickness"
// t.z -> the offset by which the torus should be moved from the center
float sdTruchet (in vec3 p, in vec3 t)
{
    float offset = t.z;
    vec3 p1 = vec3 (p - vec3 (offset, offset, .0)) * rotX (radians (90.));
    vec3 p2 = vec3 (p - vec3 (.0, -offset, offset)) * rotZ (radians (90.));
    vec3 p3 = vec3 (p - vec3 (-offset, .0, -offset)) * rotY (radians (90.));

    float t1 = sdTorus (p1, t.xy);
    float t2 = sdTorus (p2, t.xy);
    float t3 = sdTorus (p3, t.xy);

    return min (t1, min (t2, t3));
}

float scene (in vec3 p)
{
    vec2 mouse = iMouse.xy;
    if (iMouse.xy == vec2(.0)) mouse.xy = vec2 (212., 192.);
    mat3 rot = rotX (radians (180. + mouse.y / iResolution.y * 360.)) * rotY (radians (-180. + mouse.x / iResolution.x * 360.));
	p *= rot;

    // "move the camera" (actually, we're moving space)
    p.x -= iTime * .3;

    vec3 cellParam = vec3 (.5, .07 + .04 * (.5 + .5 * cos (3.*iTime)), .5);

    // these random functionsand the used values are still major WTF's for me
    float selector = fract(sin(dot(floor(p) + 13.37, vec3(7., 157., 113.)))*43758.5453);

    // I _hate_ myself for having to peek into one of Shane's truchet-examples for getting a
    // clue about the cell-rotation and not coming up with coordinate-swiveling... it's so
    // simple and obvious... argl!
    if (selector > .75) {
        p = p;
    } else if (selector > .5) {
    	p = p.yzx;
    } else if (selector > .25) {
	    p = p.zxy;
    }

    float d = sdTruchet (opRepeat (p, vec3 (.5)), cellParam);

	return d;
}

float raymarch (in vec3 ro, in vec3 rd, out int iter)
{
    float t = .0;

    for (int i = 0; i < MAX_ITER; i++)
    {
        iter = i;
        vec3 p = ro + t * rd;
        float d = scene (p);
        if (abs (d) < EPSILON * (1. + .125*d)) break;
        t += d * STEP_SIZE;
    }

    return t;
}

vec3 normal (in vec3 p)
{
    vec3 e = vec3(.0001, .0, .0);
    float d = scene (p);
    vec3 n = vec3 (scene (p + e.xyy) - d, scene (p + e.yxy) - d, scene (p + e.yyx) - d);
    return normalize(n);
}

float shadow (in vec3 p, in vec3 n, in vec3 lPos)
{
    float distanceToLight = distance (p, lPos);
    int ignored = 0;
    float distanceToObject = raymarch (p + .01*n, normalize (lPos - p), ignored);
    bool isShadowed = distanceToObject < distanceToLight;
    return isShadowed ? .1 : 1.;
}

// blinn-phong shading... as much as I can remember it
vec3 shade (in vec3 ro, in vec3 rd, in float d)
{
    vec3 p = ro + d * rd;

    vec3 ambColor = vec3 (.1, .05, .05);
    vec3 diffColor = vec3 (1.9, 1.4, 1.2);
    vec3 specColor = vec3 (.95, .85, .85);
    float shininess = 120.;

    vec3 lightPos = ro + vec3 (cos (iTime) * .5, .5, sin (iTime) * .5);
    vec3 lightDir = lightPos - p;
    vec3 lightNDir = normalize (lightDir);
    vec3 nor = normal (p);
    vec3 h = normalize (lightDir - rd);

    float diffuse = max (dot (lightNDir, nor), .0);
    float specular = pow (max (dot (h, nor), .0), shininess);

    float sha = shadow (p, nor, lightPos);
    float distanceToLight = distance (p, lightPos);
    float attenuation = 1. / (distanceToLight*distanceToLight);

    vec3 specTerm = ((sha > .1) ? attenuation * specular * specColor : vec3 (.0));
    return ambColor + sha * attenuation * diffuse * diffColor + specTerm;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    // normalizing and aspect-correction
	vec2 uvRaw = fragCoord.xy / iResolution.xy;
	vec2 uv = uvRaw;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    // create origin and view-ray
    vec3 ro = vec3 (.0, .0, -.75);
    vec3 rd = normalize (vec3 (uv, 1.) - ro);

    // "shake the camera" around a bit
    rd.xy *= rot2d (cos (iTime) * .075);
    rd.xz *= rot2d (sin (iTime) * .15);

    // do the ray-march...
    int iter = 0;
    float d = raymarch (ro, rd, iter);
    float depth = float (iter) / float (MAX_ITER);
    vec3 cc = gradient (depth).rgb;
    float fog = 1. / (1. + d * d * .1);
    vec3 c = shade (ro, rd, d);

    // secondary/1st reflection-ray
    vec3 p = ro + d*rd;
    vec3 n = normal (p);
    vec3 refl = normalize (reflect (rd, n));
    float refd = raymarch (p + .01*n, refl, iter);
    vec3 refp = p + refd*refl;
    vec3 refcol = shade (p, refl, refd);

    // restricting (fresnel) reflections to grazing view-angles
	float fakeFresnel = pow (1. - max (dot (n, -rd), .0), 1.25);
    vec3 lPos = ro + vec3 (cos (iTime) * .5, .5, sin (iTime) * .5);
    float lDist = distance (lPos, p);
    float attenuation = 1. / (lDist*lDist);
    c += fakeFresnel*attenuation*.125*refcol;

    // fog, tonemapping, "gamma-correction", tint, vignette
    c *= fog;
	c = c / (1. + c);
    c = .2 * c + .8 * sqrt (c);
    c *= vec3 (.9, .8, .7);
    c *= .2 + .8 * pow (16. * uvRaw.x * uvRaw.y * (1. - uvRaw.x) * (1. - uvRaw.y), .3);

    if (iMouse.x / iResolution.x < .5) {
		fragColor = vec4(c, 1.);
    } else {
		fragColor = vec4(cc, 1.);
    }
}

#define pattern 6
#define PI 3.141

float random(in vec2 uv) {
    return fract(sin(dot(uv.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

float randomStars(in vec2 uv) {
    return fract(sin(dot(uv.xy, vec2(2.0, 30.0))) * 9999999999.0);
}

// Value noise
float noise(in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

// Gradient noise
vec2 hash( vec2 x ){
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float gnoise( in vec2 p ){
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

// Simplex noise
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,  // -1.0 + 2.0 * C.x
                        0.024390243902439); // 1.0 / 41.0
    vec2 i  = floor(v + dot(v, C.yy) );
    vec2 x0 = v -   i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod289(i); // Avoid truncation effects in permutation
    vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
        + i.x + vec3(0.0, i1.x, 1.0 ));

    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
    m = m*m ;
    m = m*m ;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
    vec3 g;
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}

#define OCTAVES 8
float fbm (in vec2 uv) {
    // Initial values
    float value = 0.0;
    float amplitud = .5;
    float frequency = 0.;

    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitud * noise(uv);
        uv *= 2.;
        amplitud *= .5;
    }
    return value;
}

mat2 rotate2D(float angle) {
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));  

	//return mat2(-sin(angle), cos(angle), -cos(angle), -sin(angle)); 
}

mat2 scale2D(vec2 value) {
    return mat2(value.x, 0.0, 0.0, value.y);
}

vec3 drawRectangle(vec2 uv, vec2 pos, float l, float w, float offset, vec3 color) {
    float value = step(pos.x, uv.x) - step(pos.x + l, uv.x);

    value *= step(pos.y, uv.y + offset) - step(pos.y + w, uv.y + offset);

    vec3 finalColor = mix(vec3(0.0), color, value);

    return finalColor;
}

vec3 drawRothko(vec2 uv, vec2 pos, float l, float w, vec3 color) {
    float value = smoothstep(pos.x + 0.2, pos.x, uv.x) - smoothstep(pos.x + l, pos.x + l - 0.2, uv.x);

    value *= smoothstep(pos.y + 0.2, pos.y, uv.y) - smoothstep(pos.y + w, pos.y + w - 0.2, uv.y);

    vec3 finalColor = mix(vec3(0.0), color, value);
    finalColor *= random(uv);

    return finalColor;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;

    if (pattern == 1) {
		uv *= 10.0;

    	float offset = step(1.0, mod(uv.y, 2.0));
    	uv.x += offset * iTime;
		uv.x -= (1.0 - offset) * iTime;

    	offset = step(1.0, mod(uv.x, 2.0));
    	uv.y += offset * iTime;
    	uv.y -= (1.0 - offset) * iTime;

        uv = fract(uv);

		vec3 red = vec3(1.0, 0.3, 0.3);
		vec3 blue = vec3(0.3, 0.3, 1.0);

		float offset1 = 0.9 * sin(iTime) * sin(iTime);
        float offset2 = -0.9 * sin(iTime) * sin(iTime);

        vec3 finalColor = drawRectangle(uv, vec2(0.0, 0.0), 0.5, 1.0, offset1, red);
        finalColor += drawRectangle(uv, vec2(0.5, 0.0), 0.5, 1.0, offset2, blue);

        fragColor = vec4(finalColor, 1.0);
    }

    else if (pattern == 2) {
        float zoom = 4.0;
   		float offset = gnoise(uv * zoom + iTime);
    	float value = smoothstep(0.0 - offset, 0.5 - offset, uv.x) - smoothstep(0.6 - offset, 1.0 - offset, uv.x);

		vec3 magma = vec3(5.0, 0.9, 0.0);
    	vec3 finalColor = value * magma;

		fragColor = vec4(finalColor, 1.0);
    }

    else if (pattern == 3) {    
        vec3 blue = vec3(0.3, 0.8, 1.0);
        vec3 yellow = vec3(1.0, 0.7, 0.3);
        vec3 red = vec3(1.0, 0.3, 0.2);

        vec3 finalColor = drawRothko(uv, vec2(-0.025, 0.0), 0.35, 1.0, blue);
        finalColor += drawRothko(uv, vec2(0.2, -0.1), 0.35, 1.2, yellow);
        finalColor += drawRothko(uv, vec2(0.425, -0.05), 0.6, 1.1, red);

        fragColor = vec4(finalColor, 1.0);
    }

    else if (pattern == 4) {
    	vec3 white = vec3(1.0, 1.0, 1.0);
        vec3 blue = vec3(0.3, 0.5, 1.0);

   		float offset = gnoise(uv + iTime / 6.0);

        vec3 clouds = vec3(fbm((uv) * 5.0)) * 0.3;
    	vec3 finalColor = mix(blue, white, smoothstep(0.0 - offset, 0.4 - offset, clouds) - smoothstep(0.5 - offset, 0.7 - offset, clouds));
		fragColor = vec4(finalColor, 1.0);
    }

    else if (pattern == 5) {
		uv *= 5.05;
    	uv = fract(uv);

        vec3 colorA = vec3(0.2, 0.1, 0.9);
        vec3 colorB = vec3(0.1, 0.4, 0.6);
        vec3 colorC = vec3(0.5, 0.0, 0.0);
        vec3 colorD = vec3(1.0, 1.0, 1.0);

        vec3 finalColor = drawRectangle(uv, vec2(0.0, 0.0), 0.05, 1.0, 0.0, colorC);
        finalColor += drawRectangle(uv, vec2(0.1, 0.0), 0.45, 0.925, 0.0, colorB);
        finalColor += drawRectangle(uv, vec2(0.55, 0.0), 0.4, 0.925, 0.0, colorB);
        finalColor += drawRectangle(uv, vec2(0.05, 0.0), 0.9, 0.45, 0.0, colorC);
        finalColor += drawRectangle(uv, vec2(0.05, 0.0), 0.05, 1.0, 0.0, colorD);
        finalColor += drawRectangle(uv, vec2(0.95, 0.0), 0.05, 1.0, 0.0, colorD);
        finalColor += drawRectangle(uv, vec2(0.05, 0.95), 1.0, 0.05, 0.0, colorD);
        finalColor += drawRectangle(uv, vec2(0.05, 0.0), 0.05, 1.0, 0.0, colorD);
        finalColor += drawRectangle(uv, vec2(0.5, 0.0), 0.05, 1.0, 0.0, colorB);

        finalColor *= random(uv);
        fragColor = vec4(finalColor,1.0);
    }

    else if (pattern == 6) {
        uv *= rotate2D(60.0 * PI / 180.0);

        float zoom = 2.0;
   		float offset = noise(uv * zoom - iTime);

        float stars = randomStars(uv * iTime);
    	float outer = smoothstep(0.0 - offset, 0.3 - offset, uv.x) - smoothstep(0.5 - offset, 0.7 - offset, uv.x);
        float inner = smoothstep(0.3 - offset, 0.4 - offset, uv.x) - smoothstep(0.7 - offset, 1.0 - offset, uv.x);
		float overlap = smoothstep(0.45 - offset, 0.7 - offset, uv.x) - smoothstep(0.8 - offset, 0.9 - offset, uv.x); 

        vec3 blue = vec3(0.0, 0.0, 0.175);
        vec3 purple = vec3(0.7, 0.4, 0.6);
        vec3 green = vec3(0.4, 1.0, 0.7);
        vec3 white = vec3(0.9, 1.0, 0.9);

        vec3 starColor = mix(blue, white, stars);
    	vec3 outerColor = mix(starColor, purple, outer);
        vec3 innerColor = mix(outerColor, green, inner);
        vec3 overlapColor = mix(innerColor, white, overlap);

		fragColor = vec4(overlapColor, 1.0);
    }

    else if (pattern == 7) {
        vec2 shapePos = vec2(0.5, -sin(iTime * 0.5) * 0.6);    
    	float dist = distance(shapePos, uv);

    	uv -= shapePos;

    	float angle = atan(uv.x, uv.y);

    	float radius = sin((angle - iTime / 2.0) * 8.0);
    	radius += sin((angle - iTime / 4.0) * 48.0);
    	radius += sin((angle - iTime / 8.0) * 128.0);

    	radius *= 0.1;

    	float sunRays = smoothstep(dist - 0.2, dist, radius) + smoothstep(dist - 0.7, dist + 0.7, 0.2);

   		float offset = gnoise(uv + iTime / 2.0);
        vec3 clouds = vec3(fbm((uv) * 5.0)) * 0.3;

        vec3 blue = vec3(-sin(iTime * 0.5) / 1.5, -sin(iTime * 0.5) / 1.5, -sin(iTime * 0.5) + 1.0);
        vec3 yellow = vec3 (sin(iTime / 2.0) + 1.9, 0.9, 0.2);
        vec3 white = vec3(1.0, 1.0, 1.0);

        vec3 sunColor = mix(blue, yellow, sunRays);
    	vec3 sky = mix(sunColor, white, smoothstep(0.0 - offset, 0.4 - offset, clouds) - smoothstep(0.5 - offset, 0.7 - offset, clouds));

    	fragColor = vec4(sky, 1.0);
    }

    else if (pattern == 8) {
    	uv.x *= ratio;

    	vec2 shapePos = vec2(0.5f * ratio, 0.5f);
    	float dist = distance(shapePos, uv);

    	uv -=shapePos;

    	float angle = atan(uv.y, uv.x);

    	float radius = sin((angle + gnoise(uv * 300.0 - iTime)) * 6.0);
    	radius += cos(angle + gnoise(uv * 5.0 - iTime) * 12.0f) * 0.5;
    	radius += sin((angle + gnoise(uv * 5.0 - iTime)) * 12.0);
    	radius += sin((angle + gnoise(uv * 50.0 - iTime)) * 64.0);
    	radius *= 0.1f;

    	float value = smoothstep(dist - 0.2, dist, radius);

    	vec3 finalColor = vec3(1.0) * value;

    	fragColor = vec4(finalColor, 1.0);
    }
}

/*
Infinity graph using particles. Bezier curves might be more performant?
Bonus is that it is easy to change the shape.
*/

vec2 dotCoordinates (float radius, vec2 offset, float t)
{
	vec2 pos;
    pos.x = offset.x + radius*cos(t);
    pos.y = offset.y + radius*sin(t)*cos(t);
    return pos;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(0, 0, 0, 1.0); // Initialize Black.

    int tailLength = 250; // Number of tail particles.
    float particleSize = 0.025; // Radius of the particles.

    for (int h = tailLength; h > 0; h--)
    {
        vec2 rightDot = dotCoordinates(0.45, vec2(0.5, 0.5), iTime - (0.02 * float(h)));
        vec2 leftDot = dotCoordinates(-0.45, vec2(0.5, 0.5), iTime - (0.02 * float(h))); 
        float d = distance(leftDot, uv);
        if (d < particleSize)
        {
            fragColor = vec4(uv.x, uv.y, 0.8 + 0.2*sin(iTime), 1.0) - float(h)/float(tailLength);   
        }
        d = distance(rightDot, uv);
        if (d < particleSize)
        {
            fragColor = vec4(uv.x, uv.y, 0.8 + 0.2*sin(iTime), 1.0) - float(h)/float(tailLength);   
        }
    }
}

