{
    // TODO: more accurate distance to ellipse function
    float sdist = length(vec2(pos.x * ry/rx, pos.y)) - ry;
    stroke(sdist, vec3(0,0.5,1), fragColor, 0.01, length(fwidth(pos)));
}

void renderEllipsePoints(float rx, float ry, float littleR, mat2 xfm, vec2 pos, inout vec3 fragColor)
{
    float sdistMin = 1e10;

    int numCircles = 20;
    for (int i = 0; i < numCircles; ++i)
    {
        float theta = float(i) / float(numCircles) * (2.0 * 3.141592654);
        vec2 center = xfm * vec2(rx * cos(theta), ry * sin(theta));
        float sdist = length(pos - center) - littleR;
        sdistMin = min(sdistMin, sdist);
    }

    vec3 dotColor = vec3(sin(pos.x)*0.6+0.3, sin(pos.y)*0.8+0.4, 1);
    fill(sdistMin, dotColor, fragColor, length(fwidth(pos)));
}

void renderHyperbolas(float r, vec2 pos, inout vec3 fragColor)
{
    // TODO: more accurate distance to hyperbola function
    float sdist = sqrt(abs(pos.y*pos.y - pos.x*pos.x)) - r;
    stroke(sdist, vec3(0,0.5,1), fragColor, 0.01, length(fwidth(pos)));
}

void renderHyperbolaPoints(float bigR, float littleR, mat2 xfm, vec2 pos, inout vec3 fragColor)
{
    float sdistMin = 1e10;

    for (int q = 0; q < 4; ++q)
    {
        float qx = float(abs(q - 2) - 1); // 1, 0, -1, 0
        float qy = float(1 - abs(q - 1)); // 0, 1, 0, -1
        vec2 qvec = vec2(qx, qy);
        vec2 qvec2 = ortho(qvec);

        int numCircles = 15;
        for (int i = -numCircles/2; i <= numCircles/2; ++i)
        {
            float omega = float(i) / float(numCircles/2) * 2.2;
            vec2 center = bigR * vec2(cosh(omega), sinh(omega));
            center = xfm * (center.x * qvec + center.y * qvec2);
            float sdist = length(pos - center) - littleR;
            sdistMin = min(sdistMin, sdist);
        }
    }

    vec3 dotColor = vec3(sin(pos.x)*0.6+0.3, sin(pos.y)*0.8+0.4, 1);
    fill(sdistMin, dotColor, fragColor, length(fwidth(pos)));
}

void renderParabola(float b, vec2 pos, inout vec3 fragColor)
{
    // TODO: more accurate distance to parabola function
    float sdist = pos.y - pos.x*pos.x - b;
    stroke(sdist, vec3(0,0.5,1), fragColor, 0.01, length(fwidth(pos)));
}

void renderParabolaPoints(float b, float littleR, mat3 xfm, vec2 pos, inout vec3 fragColor)
{
    float sdistMin = 1e10;

    int numCircles = 31;
    for (int i = -numCircles/2; i <= numCircles/2; ++i)
    {
        float x = float(i) / float(numCircles/2) * 1.7;
        vec2 center = vec2(x, x*x + b);
        center = (xfm * vec3(center, 1)).xy;
        float sdist = length(pos - center) - littleR;
        sdistMin = min(sdistMin, sdist);
    }

    vec3 dotColor = vec3(sin(pos.x)*0.6+0.3, sin(pos.y)*0.8+0.4, 1);
    fill(sdistMin, dotColor, fragColor, length(fwidth(pos)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float aspect = iResolution.x / iResolution.y;
	vec2 pos = ((fragCoord / iResolution.xy) - 0.5) * vec2(aspect, 1.0) * 1.2;
    vec2 posOrig = pos;

    int conic = int(iTime / 20.0) % 4;
    float timeConic = mod(iTime, 20.0);
    float fade = smoothstep(0.0, 0.5, timeConic) * smoothstep(20.0, 19.5, timeConic);

    float ellipseAspect = 0.8 / 0.5;
    mat2 xfm, xfmInv;
    mat3 xfm3, xfm3Inv;

    // TODO: mouse control?

    switch (conic)
    {
        case 0:
        {
            // (circle) rotate the coordinates
            float theta = iTime * 0.2;
            float cosTheta = cos(theta), sinTheta = sin(theta);
            xfm = mat2(cosTheta, -sinTheta, sinTheta, cosTheta);
            xfmInv = inverse(xfm);
            pos = xfm * pos;
            break;
        }

        case 1:
        {
            // (ellipse) scale, rotate, unscale the coordinates
            float theta = iTime * 0.3;
            float cosTheta = cos(theta), sinTheta = sin(theta);
            xfm = mat2(cosTheta, -sinTheta / ellipseAspect, sinTheta * ellipseAspect, cosTheta);
            xfmInv = inverse(xfm);
            pos = xfm * pos;
			break;
        }

		case 2:
        {
            // (parabola) shear and translate the coordinates
            float k = 1.5 * sin(iTime * 0.3);
            xfm3 = mat3(1, k, 0, 0, 1, 0, 0.5*k, 0.25*k*k, 1);
            xfm3Inv = inverse(xfm3);
            pos = (xfm3 * vec3(pos, 1)).xy;
            break;
        }

        case 3:
        {
            // (hyperbolas) apply Lorentz transform
            float omega = 1.3 * sin(iTime * 0.3);
            float coshOmega = cosh(omega), sinhOmega = sinh(omega);
            xfm = mat2(coshOmega, sinhOmega, sinhOmega, coshOmega);
            xfmInv = inverse(xfm);
            pos = xfm * pos;
            break;
        }
    }

    fragColor.a = 1.0;
	renderGrid(pos, fragColor.rgb);
    renderAxes(vec2(0), pos, fragColor.rgb);

    switch (conic)
    {
        case 0:
            renderCircle(0.5, posOrig, fragColor.rgb);
            renderCirclePoints(0.5, 0.015, xfmInv, posOrig, fragColor.rgb);
        	break;

        case 1:
            renderEllipse(0.5 * ellipseAspect, 0.5, posOrig, fragColor.rgb);
            renderEllipsePoints(0.5 * ellipseAspect, 0.5, 0.015, xfmInv, posOrig, fragColor.rgb);
        	break;

        case 2:
            renderParabola(-0.5, posOrig, fragColor.rgb);
            renderParabolaPoints(-0.5, 0.015, xfm3Inv, posOrig, fragColor.rgb);
        	break;

        case 3:
            renderHyperbolas(0.4, posOrig, fragColor.rgb);
            renderHyperbolaPoints(0.4, 0.015, xfmInv, posOrig, fragColor.rgb);
        	break;
    }

    fragColor.rgb *= fade;
}

