// From http://blog.saikoled.com/post/45630908157/implementing-arbitrary-color-correction-with-the
vec3 hsi2rgb(float H, float S, float I) {
	float r, g, b;
	float M_PI = radians(180.0);
	H = mod(H*360.0, 360.0); // cycle H around to 0-360 degrees
	H *= M_PI / 180.0; // Convert to radians.
	S = S > 0. ? (S < 1. ? S : 1.) : 0.; // clamp S and I to interval [0,1]
	I = I > 0. ? (I < 1. ? I : 1.) : 0.;
	// Math! Thanks in part to Kyle Miller.
	if(H < 2.09439) {
		r = I / 3. * (1. + S * cos(H) / cos(1.047196667 - H));
		g = I / 3. * (1. + S * (1. - cos(H) / cos(1.047196667 - H)));
		b = I / 3. * (1. - S);
	} else if(H < 4.188787) {
		H -= 2.09439;
		g = I / 3. * (1. + S * cos(H) / cos(1.047196667 - H));
		b = I / 3. * (1. + S * (1. - cos(H) / cos(1.047196667 - H)));
		r = I / 3. * (1. - S);
	} else {
		H -= 4.188787;
		b = I / 3. * (1. + S * cos(H) / cos(1.047196667 - H));
		r = I / 3. * (1. + S * (1. - cos(H) / cos(1.047196667 - H)));
		g = I / 3. * (1. - S);
	}
	return vec3(r, g, b);
}
// From http://blog.saikoled.com/post/45630908157/implementing-arbitrary-color-correction-with-the
vec4 hsi2rgbw_4(float H, float S, float I) {
	float r, g, b, w;
	float cos_h, cos_1047_h;
	float M_PI = radians(180.0);
	H = mod(H*360.0, 360.0); // cycle H around to 0-360 degrees
	H *= M_PI / 180.0; // Convert to radians.
	S = S > 0. ? (S < 1. ? S : 1.) : 0.; // clamp S and I to interval [0,1]
	I = I > 0. ? (I < 1. ? I : 1.) : 0.;
	if(H < 2.09439) {
		cos_h = cos(H);
		cos_1047_h = cos(1.047196667 - H);
		r = S * I / 3. * (1. + cos_h / cos_1047_h);
		g = S * I / 3. * (1. + (1. - cos_h / cos_1047_h));
		b = 0.;
		w = (1. - S) * I;
	} else if(H < 4.188787) {
		H -= 2.09439;
		cos_h = cos(H);
		cos_1047_h = cos(1.047196667 - H);
		g = S * I / 3. * (1. + cos_h / cos_1047_h);
		b = S * I / 3. * (1. + (1. - cos_h / cos_1047_h));
		r = 0.;
		w = (1. - S) * I;
	} else {
		H -= 4.188787;
		cos_h = cos(H);
		cos_1047_h = cos(1.047196667 - H);
		b = S * I / 3. * (1. + cos_h / cos_1047_h);
		r = S * I / 3. * (1. + (1. - cos_h / cos_1047_h));
		g = 0.;
		w = (1. - S) * I;
	}
	return vec4(r, g, b, w);
}
vec3 hsi2rgbw(float h, float s, float i) {
	vec4 col = hsi2rgbw_4(h,s,i);
	return col.rgb+ col.a;
}
// Spectrum with 6 distict peaks in hue and 3 in value
vec3 hsv2rgb_peak6(float h, float s, float v) {
	vec3 hueP = max(vec3(0),abs(mod(h* 12.+ vec3( 0, 8,4),12.)- 6.)- 5.);
	vec3 hue1 = max(vec3(0),abs(mod(h* 12.+ vec3(11, 7,3),12.)- 6.)- 5.);
	vec3 hue2 = max(vec3(0),abs(mod(h* 12.+ vec3( 2,10,6),12.)- 6.)- 5.);
	vec3 hue3 = max(vec3(0),abs(mod(h* 12.+ vec3( 1, 9,5),12.)- 6.)- 5.);
	vec3 hueF = mat3(3,1,0,0,3,1,1,0,3)* hue1*.25;
	vec3 hueC = mat3(1,0,1,1,1,0,0,1,1)* hue2*1.;
	vec3 hueL = mat3(3,0,1,1,3,0,0,1,3)* hue3*.25;
	vec3 hue = hueP+ hueF+ hueC+ hueL;
	return v* mix(vec3(1),hue,s);
}
// Constant brightness but desaturated
vec3 hsv2rgb_cosine15(float h, float s, float v) {
	vec3 hue = .5+ .5* cos((3.* h+ vec3(0,2,1))* radians(120.0));
	return v* mix(vec3(1),clamp(hue,0.,1.),s);
}
// Constant brightness but muddy mixes of primary colors
vec3 hsv2rgb_cosine10(float h, float s, float v) {
	vec3 hue = .5*cos(min(vec3(.5),1.5 *abs(fract(h+.5+ vec3(0,2,1)/3.)-0.5))*radians(360.))+ .5;
	return v* mix(vec3(1),clamp(hue,0.,1.),s);
}
