}
// Simplified FastLED spectrum implementation
vec3 hsv2rgb_spectrum(float h, float s, float v) {
	return v* mix(vec3(1),clamp(1.- abs(1.- mod(3.* h+ vec3(1,0,2), 3.)),0.,1.),s);
}
// Simplified FastLED rainbow implementation
vec3 hsv2rgb_rainbow(float h, float s, float v) {
	vec3 hue = vec3(0);
	float x = fract(h) * (8. / 3.) - 1.;
	if(x < 0.) {
		hue.x = (-x) + max(0., 1. / 3. - abs(x + (1. / 3.)));
		hue.y = 1. + x;
	} else if(x < 1. / 3.) {
		hue.y = 1. - x;
		hue.z = x;
	} else if(x < 2. / 3.) {
		hue.y = 1. - (x * 2. - (1. / 3.));
		hue.z = x * 2. - (1. / 3.);
	} else {
		hue.x = x - 1. + (1. / 3.);
		hue.z = 1. - (x - 1. + (1. / 3.));
	}
	return v* mix(vec3(1),clamp(hue,0.,1.),s);
}

// Official HSV to RGB conversion
vec3 hsv2rgb(float h, float s, float v) {
	return v* mix(vec3(1),clamp(abs(mod(h* 6.+ vec3(0,4,2),6.)- 3.)- 1.,0.,1.),s);
}
vec3 cubic(vec3 x) {
