vec3 hsv2rgb_raw_C(float hsvhue,float hsvsat,float hsvval) {
	// Convert hue, saturation and brightness ( HSV/HSB ) to RGB
	// "Dimming" is used on saturation and brightness to make
	// the output more visually linear.
	// Apply dimming curves
	int hue = uint8_t(hsvhue);
	int sat = uint8_t(hsvsat);
	int val = uint8_t(hsvval);
	int value = uint8_t(val);
	int saturation = uint8_t(sat);
	// The brightness floor is minimum number that all of
	// R, G, and B will be set to.
	int invsat = uint8_t(255- saturation);
	int brightness_floor = uint8_t((value* invsat)/ 256);
	// The color amplitude is the maximum amount of R, G, and B
	// that will be added on top of the brightness_floor to
	// create the specific hue desired.
	int color_amplitude = uint8_t(value- brightness_floor);
	// Figure out which section of the hue wheel we're in,
	// and how far offset we are within that section
	int section = uint8_t(hue/ 0x40); // 0..2
	int offset = uint8_t(hue% 0x40); // 0..63
	int rampUp = uint8_t(offset); // 0..63
	int rampDown = uint8_t((0x40- 1)- offset); // 63..0
	// We now scale rampUp and rampDown to a 0-255 range -- at least
	// in theory, but here's where architecture-specific decisions
	// come in to play:
	// To scale them up to 0-255, we'd want to multiply by 4.
	// But in the very next step, we multiply the ramps by other
	// values and then divide the resulting product by 256.
	// So which is faster?
	//   ((ramp * 4) * otherValue) / 256
	// or
	//   ((ramp    ) * otherValue) /  64
	// It depends on your processor architecture.
	// On 8-bit AVR, the "/ 256" is just a one-cycle register move,
	// but the "/ 64" might be a multi-cycle shift process. So on AVR
	// it's faster do multiply the ramp values by four, and then
	// divide by 256.
	// On ARM, the "/ 256" and "/ 64" are one cycle each, so it's
	// faster to NOT multiply the ramp values by four, and just to
	// divide the resulting product by 64 (instead of 256).
	// Moral of the story: trust your profiler, not your instincts.
	// Since there's an AVR assembly version elsewhere, we'll
	// assume what we're on an architecture where any number of
	// bit shifts has roughly the same cost, and we'll remove the
	// redundant math at the source level:
	//  // scale up to 255 range
	//  //rampUp *= 4; // 0..252
	//  //rampDown *= 4; // 0..252
	// compute color-amplitude-scaled-down versions of rampUp and rampDown
	int rampUp_amp_adj = uint8_t((rampUp* color_amplitude)/ (256/ 4));
	int rampDown_amp_adj = uint8_t((rampDown* color_amplitude)/ (256/ 4));
	// add brightness_floor offset to everything
	int rampUp_adj_with_floor = uint8_t(rampUp_amp_adj+ brightness_floor);
	int rampDown_adj_with_floor = uint8_t(rampDown_amp_adj+ brightness_floor);
	int rgbr,rgbg,rgbb;
	if(section!= 0) {
		if(section== 1) {
			// section 1: 0x40..0x7F
			rgbr = brightness_floor;
			rgbg = rampDown_adj_with_floor;
			rgbb = rampUp_adj_with_floor;
		} else {
			// section 2; 0x80..0xBF
			rgbr = rampUp_adj_with_floor;
			rgbg = brightness_floor;
			rgbb = rampDown_adj_with_floor;
		}
	} else {
		// section 0: 0x00..0x3F
		rgbr = rampDown_adj_with_floor;
		rgbg = rampUp_adj_with_floor;
		rgbb = brightness_floor;
	}
	return vec3(rgbr,rgbg,rgbb)/ 255.;
}
// Ported FastLED spectrum function
vec3 hsv2rgb_fl_spectrum(float hsvhue,float hsvsat,float hsvval) {
	int hsv2hue = scale8(uint8_t(hsvhue),191);
	return hsv2rgb_raw_C(float(hsv2hue)/ 255.,hsvsat,hsvval);
}
// Ported FastLED rainbow function
vec3 hsv2rgb_fl_rainbow(float hsvhue,float hsvsat,float hsvval) {
	// Yellow has a higher inherent brightness than
	// any other color; 'pure' yellow is perceived to
	// be 93% as bright as white.  In order to make
	// yellow appear the correct relative brightness,
	// it has to be rendered brighter than all other
	// colors.
	// Level Y1 is a moderate boost, the default.
	// Level Y2 is a strong boost.
	int Y1 = uint8_t(1);
	// G2: Whether to divide all greens by two.
	// Depends GREATLY on your particular LEDs
	int G2 = uint8_t(0);
	// gScale: what to scale green down by.
	// Depends GREATLY on your particular LEDs
	int gScale = uint8_t(0);
	int hue = uint8_t(hsvhue);
	int sat = uint8_t(hsvsat);
	int val = uint8_t(hsvval);
	int offset = uint8_t(hue& 0x1F); // 0..31
	// offset8 = offset * 8
	int offset8 = uint8_t(offset);
	{
		// On ARM and other non-AVR platforms, we just shift 3.
		offset8 <<= 3;
	}
	int third = uint8_t(scale8(offset8,(256/ 3))); // max = 85
	int r,g,b;
	if(!((hue& 0x80)!= 0)) {
		// 0XX
		if(!((hue& 0x40)!= 0)) {
			// 00X
			//section 0-1
			if(!((hue& 0x20)!= 0)) {
				// 000
				//case 0: // R -> O
				r = 255- third;
				g = third;
				b = 0;
			} else {
				// 001
				//case 1: // O -> Y
				if(Y1!= 0) {
					r = 171;
					g = 85+ third;
					b = 0;
				} else {
					r = 170+ third;
					int twothirds = uint8_t(scale8(offset8,((256* 2)/ 3))); // max=170
					g = 85+ twothirds;
					b = 0;
				}
			}
		} else {
			//01X
			// section 2-3
			if(!((hue& 0x20)!= 0)) {
				// 010
				//case 2: // Y -> G
				if(Y1!= 0) {
					int twothirds = uint8_t(scale8(offset8,((256* 2)/ 3))); // max=170
					r = 171- twothirds;
					g = 170+ third;
					b = 0;
				} else {
					r = 255- offset8;
					g = 255;
					b = 0;
				}
			} else {
				// 011
				// case 3: // G -> A
				r = 0;
				g = 255- third;
				b = third;
			}
		}
	} else {
		// section 4-7
		// 1XX
		if(!((hue& 0x40)!= 0)) {
			// 10X
			if(!((hue& 0x20)!= 0)) {
				// 100
				//case 4: // A -> B
				r = 0;
				int twothirds = uint8_t(scale8(offset8,((256* 2)/ 3))); // max=170
				g = 171- twothirds; //K170?
				b = 85+ twothirds;
			} else {
				// 101
				//case 5: // B -> P
				r = third;
				g = 0;
				b = 255- third;
			}
		} else {
			if(!((hue& 0x20)!= 0)) {
				// 110
				//case 6: // P -- K
				r = 85+ third;
				g = 0;
				b = 171- third;
			} else {
				// 111
				//case 7: // K -> R
				r = 170+ third;
				g = 0;
				b = 85- third;
			}
		}
	}
	// This is one of the good places to scale the green down,
	// although the client can scale green down as well.
	if(G2!= 0) g = g>> 1;
	if(gScale!= 0) g = scale8_video_LEAVING_R1_DIRTY(g,gScale);
	// Scale down colors if we're desaturated at all
	// and add the brightness_floor to r, g, and b.
	if(sat!= 255) {
		if(sat== 0) {
			r = 255;
			b = 255;
			g = 255;
		} else {
			if(r!= 0) r = scale8(r,sat);
			if(g!= 0) g = scale8(g,sat);
			if(b!= 0) b = scale8(b,sat);
			int desat = uint8_t(255- sat);
			desat = scale8(desat,desat);
			int brightness_floor = uint8_t(desat);
			r += brightness_floor;
			g += brightness_floor;
			b += brightness_floor;
		}
	}
	// Now scale everything down if we're at value < 255.
	if(val!= 255) {
		val = scale8_video_LEAVING_R1_DIRTY(val,val);
		if(val== 0) {
			r = 0;
			g = 0;
			b = 0;
		} else {
			if(r!= 0) r = scale8(r,val);
			if(g!= 0) g = scale8(g,val);
			if(b!= 0) b = scale8(b,val);
		}
	}
	return vec3(r,g,b)/255.;
}
vec4 total(vec3 f) {
	return vec4(f,f.x+f.y+f.z);
}
bool singleMode;
float gain(float i) {
	return .5* iResolution.y- 2.* borderW- (iMouse.x< 10. ? i* 2.5* lineW : 0.);
}
float off(float i) {
	return borderW+ (iMouse.x< 10. ? i* 2.5* lineW : 0.);
}
vec4 rgb;
vec3 combine(vec3 col, float n) {
	if(iMouse.x>= 10.) {
		if(singleMode) {
			n = 0.;
		} else if(n> 0.) {
			n = 2.;
		}
	}
	rgb *= pow(dim,n);
	vec3 cols = vec3(.2,.8,.2);
	col = mix(col,cols.zzz,rgb.a);
	col = mix(col,cols.xxy,rgb.b);
	col = mix(col,cols.yxx,rgb.r);
	col = mix(col,cols.xyx,rgb.g);
	rgb = vec4(0);
	return col;
}
vec2 resi,reso,frag;
float sat, val;
void satval(float x) {
	sat = 1.0- clamp(iTime,0.,1.)* clamp(sin(iTime* .99+ (frag.x+ x)*resi.x* 2.)+ .255,0.,1.);
	val = 1.0- clamp(iTime,0.,1.)* clamp(sin(iTime* .61+ (frag.x+ x)*resi.x* 2.)+ .125,0.,1.);
}
void plot(float x, float og, vec4 val, vec2 pos) {
	float o = off(og);
	float g = gain(og);
	vec4 y = o+g*val;
	float dr = distance(vec2(x,y.r),pos);
	float dg = distance(vec2(x,y.g),pos);
	float db = distance(vec2(x,y.b),pos);
	float da = distance(vec2(x,y.a),pos);
	vec4 res = clamp(lineW- vec4(dr,dg,db,da),0.,1.);
	rgb = max(rgb,res);
}
void plot(float x, float og, vec3 val, vec2 pos) {
	plot(x,og,total(val),pos);
}
bool shown(float which) {
	return iMouse.x< 10.
	|| floor(fract(iMouse.y/ iResolution.y* 2.)* num_f)== which
	|| !singleMode&& floor(iMouse.x/ iResolution.x* num_f)== which;
}
vec4 image(vec2 U) {
	reso = iResolution.xy;
	resi = 1./iResolution.xy;
	frag = U;
	singleMode = iMouse.y>= .5* iResolution.y;
	satval(0.);
	vec3 col;
#define FILL(NUM,FUNC) \
	if(frag.y* resi.y>= .5+ .5* (NUM)/ num_f&& shown(NUM)) {\
		col = FUNC(frag.x*resi.x,sat,val);\
	} else
	FILL(9.,hsv2rgb_cosine15   )
	FILL(8.,hsv2rgb_cosine10   )
	FILL(7.,hsv2rgb_fl_spectrum)
	FILL(6.,hsv2rgb_fl_rainbow )
	FILL(5.,hsi2rgb            )
	FILL(4.,hsi2rgbw           )
	FILL(3.,hsv2rgb_peak6      )
	FILL(2.,hsv2rgb_smooth     )
	FILL(1.,hsv2rgb            )
	FILL(0.,hsv2rgb_tricos     )
#undef FILL
	{col = vec3(0);}
	rgb = vec4(0);
#define PLOT(NUM,FUNC) {\
	if(shown(NUM)) {\
		for(float i = -lineW; i<= lineW; i++) {\
			satval(i);\
			plot(i+ frag.x,NUM,FUNC((frag.x+ i)* resi.x,sat,val),frag);\
		}\
		col = combine(col,NUM);\
	}}
	PLOT(9.,hsv2rgb_cosine15   )
	PLOT(8.,hsv2rgb_cosine10   )
	PLOT(7.,hsv2rgb_fl_spectrum)
	PLOT(6.,hsv2rgb_fl_rainbow )
	PLOT(5.,hsi2rgb            )
	PLOT(4.,hsi2rgbw           )
	PLOT(3.,hsv2rgb_peak6      )
	PLOT(2.,hsv2rgb_smooth     )
	PLOT(1.,hsv2rgb            )
	PLOT(0.,hsv2rgb_tricos     )
#undef PLOT
	return vec4(col,1);
}
void mainImage(out vec4 fragColor, vec2 fragCoord) {
	fragColor = image(fragCoord);
}

float random (in vec3 _st) {
    return fract(sin(dot(_st.xyz,
                         vec3(12.9898,78.233,82.19)))*
        43758.5453123);
}
float noise (in vec3 _st) {
    vec3 i = floor(_st);
    vec3 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec3(1.0, 0.0,0.0));
    float c = random(i + vec3(0.0, 1.0,0.0));
    float d = random(i + vec3(1.0, 1.0,0.0));

    float e = random(i + vec3(0.0, 0.0,1.0));
    float g = random(i + vec3(1.0, 0.0,1.0));
    float h = random(i + vec3(0.0, 1.0,1.0));
    float j = random(i + vec3(1.0, 1.0,1.0));
    f = (1.0-cos(f*3.1415))/2.0;
   // f = pow(abs(f-0.5)*2.0,vec3(2.0,2.0,2.0))*0.5*sign(f-0.5)+0.5;
   // f = (1.0-cos(clamp(f*2.0,0.0,1.0)*3.1415))/4.0+(1.0-cos(clamp(f*2.0-1.0,0.0,1.0)*3.1415))/4.0;

    // float a1 = mix(a, b, u.x) 
     //       (c - a)* u.y * (1.0 - u.x) +
     //(d - b) * u.x * u.y;
     float a1 = mix(a,b,f.x);
     float a2 = mix(c,d,f.x);
     float a3 = mix(e,g,f.x);
     float a4 = mix(h,j,f.x);

     float a5 = mix(a1,a2,f.y);
     float a6 = mix(a3,a4,f.y);


    return mix(a5,a6,f.z);
}

float fbm ( in vec3 _st) {
    float v = 0.0;
    float a = 0.5;
    vec3 shift = vec3(100.0,22.5,44.0);
    float r = 1.0;
    for (int i = 0; i < 4; ++i){
        v += a * noise(_st);
        r += a;
        _st =  shift + _st*2.0;
        _st = (sin(r)*_st+cos(r)*_st);
        a *= 0.5;
    }
    return v;
}
vec3 getflame(in vec3 a){
    vec3 acc = vec3(0.0);
    float vf = 50.0;
    for(float i = 0.0;i<vf;i++){
 float b = fbm(a+vec3(0.0,i/10.0,i/vf));
        float cv = 1.0-i/vf;
        float c = float(b>cv&&b<1.0);
        acc+=c*pow(vec3(c*0.35*(1.0-i/vf),0.0,b*0.3*(i/vf)),vec3(2.0,1.0,2.0));
    }
   return acc;

}

