float V;
float pi=radians(180.);
float sqrt2=sqrt(2.);

vec4 splat(vec2 D) {
    D /= V/180. ;            // splats radius
    return exp(-.5*dot(D,D) ) * vec4(1,.7,.4,0)*2.; // colored Gaussian
}

void mainImage( out vec4 Out, vec2 In )
{   
    float t=iTime+1.;
    vec2 R = iResolution.xy; vec2 P;
    V=max(R.x,R.y)/sqrt2;

    float M=floor(t);
    float K=t-M;
    for (float j=0.; j<14.; j++) {
        float F=2.*pi/(M+j);
        float U=V-(j-K)*V/14.;
        for (float i=0.; i<M+j; i++) {
            P.x=R.x/2.+cos(F*i)*U;
            P.y=R.y/2.+sin(F*i)*U;
            Out += splat(In-P);
        }
    }
}

vec3 colorA = vec3(0.349,0.241,0.812);
