void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = (fragCoord - .5 * iResolution.xy ) / iResolution.y;

    float r = length(st) * 2.0;
    float a = atan(st.y , st.x);

    float f = abs(cos(a * 9.5)) * abs(0.4 + 0.05 * sin(iTime)) + .3;

    vec3 color = mix(vec3( smoothstep(f + 0.22,f,  r) ), 
                vec3( smoothstep(f + 0.52,1.0 -f,  r)) * colorB,
        		colorA);

    color *=  smoothstep(0.0, 0.15, length(st)) + colorC;
    color *= 1.76 * smoothstep(0., 0.1, length(st)) + colorD;
    color *= smoothstep(0.55, 0.0, length(st)) + colorE;

    fragColor = vec4(color, 1.0);
}

#define OFFSET 1.3541
#define RADIUS 0.05
#define SIZE vec2(0.02,0.3)
#define TIME_SCALE 0.5

#define NORM vec2(iResolution.x/iResolution.y,1.0)

float frac(float c) {
	return c - floor(c);
}

float lerp(float a, float b, float t) {
	return a + (b-a) * t;
}

float pingpong(float t) {
	return abs(frac(t)-0.5)*2.0;
}

vec2 ball(float t) {
    return vec2(pingpong(t),pingpong(t*OFFSET));
}

float getPos(float t) {
    return clamp(lerp(ball(ceil(t)).y,ball(ceil(t+1.0)).y,frac(t)),SIZE.y*0.5,1.0-SIZE.y*0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

	float t = iTime*TIME_SCALE;

 	float a;

	a += step(length((uv-ball(t))*NORM),RADIUS);

    a += step(uv.x,SIZE.x) * step(abs(uv.y-getPos(t+0.5)),SIZE.y*0.5);

    a += step(1.0-SIZE.x,uv.x) * step(abs(uv.y-getPos(t-1.0)),SIZE.y*0.5);

	fragColor = vec4(vec3(a), 1.0);
}

