  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec3 Hashv3v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec3 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define RADIUS 0.9
#define COLOR vec4(0.45, 0.1, 0.1, 1.) 

const float twopi = 6.283185307179586;

// utility functions
float rand(in float x)
{
    return fract(sin(x)*398442.32);
}

float noise(in float x)
{
    float i = floor(x);
    float f = fract(x);
    //f = f*f*(3. - 2.*f);
    return mix(rand(i), rand(i+1.), f);
}

float tri(in float x)
{
    return abs(fract(x)*2. - 1.); 
}

mat2 rotmat(in float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat2(c, -s, s, c);
}

// evil goat mask
// thanks to FabriceNeyret2
// https://www.shadertoy.com/view/lsGyWW
#define S(v,e) smoothstep(3./iResolution.y,0.,abs(v)-(e))
float goatmask( in vec2 uv )
{
    uv.y += 0.06;
    uv = 1.8*uv; 
    float x = abs(uv.x), y=uv.y,z = abs(y-.2)+.05;;
    return clamp(
        1.
         - S(y-x+cos(6.*x)/6.,.72*exp(-3.*x)-.02*x)
         - S(x*x/6.+z*z*4.,.1)
         //- S(x*x+y*y/3.,.1);   // try also without this one
        , 0.,1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;
    float val = 0.;

    // space transformation
    uv.x += (2. * rand(uv.y+iTime*3.32) - 1.) * (4./iResolution.x);
    vec2 uvOrig = uv;
    uv *= rotmat(iTime * 0.3);

    // number of vertex
    int N = 7 + 2*int((tri(iTime*0.3)) * 5.99);
    int segpervert = N/2;

    //linewith
    float linewidth = mix(0.005, 0.025, noise(iTime*15.));

    //rotation matrix
    float theta = twopi / float(N);
    mat2 rot = rotmat(theta);

    float theta2 = theta * .5;
    mat2 rot2 = rotmat(theta2);


    // precompute length of segments
    float seglength[10];
    for(int i=0; i<segpervert; i++)
        seglength[i] = 2.* sin(theta2 * float((i+1))) * RADIUS;

    // Polygon plotting
    for(int i=0; i<N; i++){

        // transform space to plot lines at a given vertex
        vec2 vertuv = uv;
        vertuv.y -= RADIUS;

        vertuv *= rot2;
        for(int j=0; j < segpervert; j++){ 

            // distance function of horizontal line
        	float y = abs(vertuv.y);
            // only plot corresponding segment length
            y += smoothstep(0., -0.1, vertuv.x) + smoothstep(0., 0.1, vertuv.x-seglength[j]);

            // horizontal line and rotate
       		val += 1.-smoothstep(0.,linewidth,y);
            vertuv *= rot2;
        } 
        uv *= rot;  
    }

    //val = clamp(val, 0., 1.);

    // Output to screen
    fragColor = COLOR*val*(1. + 2.7*(1.-goatmask(uvOrig)));
}

