        q.x += dw1;
      } else {
        qq.xz = Rot2D (qq.xz, 2. * pi / cakSlc);
        db = max ((dw2 * cs.y - abs (qr.z)) * c, dot (vec2 (abs (qq.z), dw2 + qq.x), cs));
        q.x += dw2;
