    az += 0.03 * pi * tCur;
    el -= 0.12 * pi * sin (0.05 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, -0.05 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
