void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  drawdual = keypress(CHAR_D);
  invert = keypress(CHAR_I);
  if (invert) ccolor0 = vec3(0);
  if (keypress(CHAR_Z)) zoom = 0.25;
  if (keypress(CHAR_F)) {
      float pixelwidth = 4.0*scale/iResolution.y;
      pedge0 = pwidth-pixelwidth;
      //pedge1 = pwidth+pixelwidth;
      ledge0 = lwidth-pixelwidth;
      //ledge1 = lwidth+pixelwidth;
  }    
  configuration = int(keypress(CHAR_0+1)) + 2*int(!keypress(CHAR_0+2));
  vec3 p = map(fragCoord.xy);
  vec3 p0,p1,p2,p3,p4; // p4 is the movable point
  if (configuration == 0) {
    p0 = vec3(1,0,0); p1 = vec3(0,1,0);
    p2 = vec3(0,0,1); p3 = vec3(1,1,1);
    p4 = vec3(0.5,-1,1);
  } else if (configuration == 1) {
    p0 = vec3(0,0,1); p1 = vec3(1,0,1);
    p2 = vec3(0,1,1); p3 = vec3(1,1,1);
    p4 = vec3(0.5,-1,1);
  } else if (configuration == 2) {
    p0 = vec3(0,0,1);
    p1 = vec3(0,1,1);
    p2 = vec3(0.866,-0.5,1);
    p3 = vec3(-0.866,-0.5,1);
    p4 = vec3(0,0.618,1);
  } else {
    p0 = vec3(1,0,1);  p1 = vec3(0,1,1);
    p2 = vec3(-1,0,1); p3 = vec3(0,-1,1);
    p4 = vec3(0.5,-1,1);
  }
  p0 = transform(p0); p1 = transform(p1);
  p2 = transform(p2); p3 = transform(p3);
  if (iMouse.x != 0.0) {
    p4 = map(iMouse.xy);
  }
  if (keypress(CHAR_V)) {
     p4 = vec3(0,0,1);
  }
  vec3 p01 = join(p0,p1);
  vec3 p02 = join(p0,p2);
  vec3 p03 = join(p0,p3);
  vec3 p12 = join(p1,p2);
  vec3 p13 = join(p1,p3);
  vec3 p23 = join(p2,p3);

  // The line through A,B intersects line L at
  // (A.L)B - (B.L)A, whose conjugate is (A.L)B + (B.L)A
  vec3 s01 = dot(p4,p0)*p1 + dot(p4,p1)*p0;
  vec3 t01 = dot(p4,p0)*p1 - dot(p4,p1)*p0;
  vec3 s12 = dot(p4,p1)*p2 + dot(p4,p2)*p1;
  vec3 t12 = dot(p4,p1)*p2 - dot(p4,p2)*p1;
  vec3 s23 = dot(p4,p2)*p3 + dot(p4,p3)*p2;
  vec3 t23 = dot(p4,p2)*p3 - dot(p4,p3)*p2;
  vec3 s03 = dot(p4,p3)*p0 + dot(p4,p0)*p3;
  vec3 t03 = dot(p4,p3)*p0 - dot(p4,p0)*p3;

  vec3 s02 = dot(p4,p0)*p2 + dot(p4,p2)*p0;
  vec3 t02 = dot(p4,p0)*p2 - dot(p4,p2)*p0;
  vec3 s13 = dot(p4,p1)*p3 + dot(p4,p3)*p1;
  vec3 t13 = dot(p4,p1)*p3 - dot(p4,p3)*p1;

  vec3 color = vec3(0);

  // The diagonal lines of the quadrangle
  color = cmix(color,lcolor2,line(p,p01));
  color = cmix(color,lcolor2,line(p,p02));
  color = cmix(color,lcolor2,line(p,p03));
  color = cmix(color,lcolor2,line(p,p12));
  color = cmix(color,lcolor2,line(p,p13));
  color = cmix(color,lcolor2,line(p,p23));

  // The moving line
  color = cmix(color,lcolor1,line(p,p4));

  vec3 l0 = join(s01,s12);
  vec3 l1 = join(s23,s03);
  vec3 l2 = join(s12,s23);
  vec3 l3 = join(s03,s01);
  //vec3 l4 = join(s02,s23);
  //vec3 l5 = join(s01,s13);
  color = cmix(color,lcolor0,line(p,l0));
  color = cmix(color,lcolor0,line(p,l1));
  color = cmix(color,lcolor0,line(p,l2));
  color = cmix(color,lcolor0,line(p,l3));
  //color = cmix(color,lcolor0,line(p,l4));
  //color = cmix(color,lcolor0,line(p,l5));

  // The points of the quadrangle
  color = cmix(color,pcolor0,point(p,p0));
  color = cmix(color,pcolor0,point(p,p1));
  color = cmix(color,pcolor0,point(p,p2));
  color = cmix(color,pcolor0,point(p,p3));

  color = cmix(color,pcolor2,point(p,s01));
  color = cmix(color,pcolor2,point(p,t01));
  color = cmix(color,pcolor2,point(p,s12));
  color = cmix(color,pcolor2,point(p,t12));
  color = cmix(color,pcolor2,point(p,s23));
  color = cmix(color,pcolor2,point(p,t23));
  color = cmix(color,pcolor2,point(p,s03));
  color = cmix(color,pcolor2,point(p,t03));

  color = cmix(color,pcolor1,point(p,join(l0,l1)));
  color = cmix(color,pcolor1,point(p,join(l2,l3)));

  //color = cmix(color,pcolor1,point(p,join(l4,l5)));

  if (invert) color = 1.0 - color;
  fragColor = vec4(pow(1.0*color,vec3(0.4545)),1);
}

float NEAR_CLIPPING_PLANE=.001;
float FAR_CLIPPING_PLANE=100.;
int NUMBER_OF_MARCH_STEPS=400;
float EPSILON=.005;
float DISTANCE_BIAS=.3;
float t = 0.;

float sdSphere(vec3 p, float s)
{
	return length(p) - (s);
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float opU( float d1, float d2 )
{
    return min(d1,d2);
}

float opS( float d1, float d2 )
{
    return max(-d1,d2);
}


float opI( float d1, float d2 )
{
    return max(d1,d2);
}


void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float celli(in vec3 p){ p = fract(p)-.5; return dot(p, p); }

float cellTile(in vec3 p){
    vec4 d; 
    d.x = celli(p - vec3(.81, .62, .53));
    p.xy = vec2(p.y-p.x, p.y + p.x)*.7071;
    d.y = celli(p - vec3(.39, .2, .11));
    p.yz = vec2(p.z-p.y, p.z + p.y)*.7071;
    d.z = celli(p - vec3(.62, .24, .06));
    p.xz = vec2(p.z-p.x, p.z + p.x)*.7071;
    d.w = celli(p - vec3(.2, .82, .64));
    d.xy = min(d.xz, d.yw);
    return min(d.x, d.y)*2.66; 
}

float hex(vec2 p) {
    p.x *= 0.57735*2.;
	p.y += mod(floor(p.x), 2.)*.5;
	p = abs((mod(p, 1.) - .5));
	return abs(max(p.x*1.5 + p.y, p.y*2.) - 1.);
}

float cellTile2(in vec3 p){
    vec4 d; 
    d.x = celli(p - vec3(.81, .62, .53));
    p.xy = vec2(p.y-p.x, p.y + p.x)+hex(p.xy*0.2);
    d.y = celli(p - vec3(.39, .2, .11));
    p.yz = vec2(p.z-p.y, p.z + p.y)+hex(p.yz*0.2);
    d.z = celli(p - vec3(.62, .24, .06));
    p.xz = vec2(p.z-p.x, p.z + p.x)+hex(p.xz*0.2);
    d.w = celli(p - vec3(.2, .82, .64));
    d.xy = min(d.xz, d.yw);
    return min(d.x, d.y)*.5; 
}
float bump(vec3 pos) {
    float re = 0.;
	re += cellTile2(pos*.25) * cellTile2(pos*1.1) * 3. + cellTile2(pos*1.2) * cellTile2(pos*4.4) * .5;
    return re;
}
vec3 hit;


float scene(vec3 pos) {
//	rota(pos);
//    float of = .3*sin(pos.z*5.5);

    pR(pos.xz,t);
    float hull = sdRoundBox(pos, vec3(1.0,2.0,1.0), 0.01);
    float hullCut;

    // on 4 sides
    for (float a = 0.; a < 4.; a+=1.0) {
        pR(pos.xz,a*3.1415/2.);

        // recess

        float cut1 = sdRoundBox(pos+vec3(-0.45,0.20,1.45), vec3(0.4,1.7,0.5), 0.01);
        float cut2 = sdRoundBox(pos+vec3(0.45,0.20,1.45), vec3(0.4,1.7,0.5), 0.01);
	    hullCut = opS(cut1,hull);
        hullCut = opS(cut2,hullCut);

        // panels

        for (float y = 1.45; y > -1.5; y-=0.83) {
            cut1 = sdRoundBox(pos+vec3(-0.45,y,1.4), vec3(0.3,0.35,0.5), 0.01);
            hullCut = opS(cut1,hullCut);
            cut1 = sdRoundBox(pos+vec3(0.45,y,1.4), vec3(0.3,0.35,0.5), 0.01);
            hullCut = opS(cut1,hullCut);
        }

        hull = hullCut;

    }
	float hullFinal = hullCut;    

    return hullFinal;
}

float sceneb(vec3 pos) {
	return scene(pos);
}

vec2 raymarch(vec3 position, vec3 direction)
{
    float total_distance = NEAR_CLIPPING_PLANE;
    float acc = 0.;
    for(int i = 0 ; i < NUMBER_OF_MARCH_STEPS ; ++i)
    {
        vec3 pos = position + direction * total_distance;
        float result = scene(pos);
        acc+=cos(result*1.)*.05;

        if(result < EPSILON)
        {
            return vec2(total_distance, acc);
        }

        total_distance += result * DISTANCE_BIAS;


        if(total_distance > FAR_CLIPPING_PLANE)
            break;
    }
    return vec2(FAR_CLIPPING_PLANE, acc);
}

vec3 nr(vec3 n) {
	return normalize(n);
}

vec3 normal( in vec3 pos )
{
    vec3 eps = vec3(.3,0.,0.)*EPSILON;
	vec3 nor = vec3(
	    sceneb(pos+eps.xyy) - sceneb(pos-eps.xyy),
	    sceneb(pos+eps.yxy) - sceneb(pos-eps.yxy),
	    sceneb(pos+eps.yyx) - sceneb(pos-eps.yyx) );
	return nr(nor);
}



float orenNayarDiffuse(
  vec3 lightDirection,
  vec3 viewDirection,
  vec3 surfaceNormal,
  float roughness,
  float albedo) {

  float LdotV = dot(lightDirection, viewDirection);
  float NdotL = dot(lightDirection, surfaceNormal);
  float NdotV = dot(surfaceNormal, viewDirection);

  float s = LdotV - NdotL * NdotV;
  float t = mix(1., max(NdotL, NdotV), step(0., s));

  float sigma2 = roughness * roughness;
  float A = 1. + sigma2 * (albedo / (sigma2 + .13) + .5 / (sigma2 + .33));
  float B = .45 * sigma2 / (sigma2 + .09);

  return albedo * max(0., NdotL) * (A + B * s / t) / 3.14159;
}

