/* "Ellipse / quartic", by mattz

   License: Creative Commons Attribution ShareAlike 4.0
   https://creativecommons.org/licenses/by-sa/4.0/

   Visualizing the relationship between the ellipse distance
   problem and the resulting quartic.

   Colored points on the ellipse are where the white dotted
   lines are perpendicular to solid white tangents of ellipse.
   Each colored point corresponds to a root of the quartic
   (4th degree polynomial) function plotted below the ellipse.

   Note that the shortest white dotted line (i.e. the one 
   corresponding to the exact ellipse distance to the white
   point) is always perpendicular to the shaded isocontours
   of the ellipse distance function at the white point.

*/


//////////////////////////////////////////////////////////////////////
// closed-form solver from https://www.shadertoy.com/view/XdKyRR 
// but without special-case checks

bvec4 solve_quartic(in vec4 coeffs,
                    out vec4 roots) {

    float p = coeffs[0];
    float q = coeffs[1]; 
    float r = coeffs[2];
    float s = coeffs[3];

    ////////////////////////////////////////////////////////////
	// form resolvent cubic and solve it to obtain one real root

    float i = -q;
    float j = p*r - 4.*s;
    float k = 4.*q*s - r*r - p*p*s;

    // coefficients of normal form
    float a = (3.*j - i*i) / 3.;
    float b = (2.*i*i*i - 9.*i*j + 27.*k) / 27.;

    float delta1 = b*b / 4.;
    float delta2 = a*a*a / 27.;

    float delta = delta1 + delta2;

    float z1;

    if (delta >= 0.) {
        vec2 AB = -0.5*b + vec2(1,-1) * sqrt(max(delta, 0.));
        AB = sign(AB) * pow(abs(AB), vec2(1.0/3.0));
        z1 = AB.x + AB.y;
    } else {
        float phi = acos( -sign(b) * sqrt(delta1/-delta2) );
        z1 = 2. * sqrt(-a/3.) * cos( phi / 3.);
    }

    // shift back from normal form to root of resolvent cubic
    z1 -= i/3.;

    ////////////////////////////////////////////////////////////
	// now form quartic roots from resolvent cubic root

    float R2 = p*p/4. - q + z1; 

    bool R_ok = (R2 >= 0.);

    float R = sqrt(max(R2, 0.));

    float foo, bar;

    if (R == 0.) { 
        float z124s = z1*z1 - 4.*s;
        R_ok = R_ok && (z124s >= 0.);
        foo = 3.*p*p / 4. - 2.*q;
        bar = 2.*sqrt(max(z124s, 0.));
    } else {
        foo = 3.*p*p / 4. - R2 - 2.*q;
        bar = (4.*p*q - 8.*r - p*p*p) / (4.*R);
    }

    bool D_ok = R_ok && (foo + bar >= 0.);
    bool E_ok = R_ok && (foo - bar >= 0.);

    float D = sqrt(max(foo + bar, 0.));
    float E = sqrt(max(foo - bar, 0.));

    roots = vec4(-p/4.) + 0.5 * vec4(R+D, R-D, -(R-E), -(R+E));
    return bvec4(D_ok, D_ok, E_ok, E_ok);

}

//////////////////////////////////////////////////////////////////////
// construct unit vector from angle around circle

vec2 from_angle(float t) {
    return vec2(cos(t), sin(t));
}

//////////////////////////////////////////////////////////////////////
// construct unit vector from cosine of angle

vec2 from_cos(float u) {
    u = clamp(u, -1., 1.);
    return vec2(u, sqrt(1. - u*u));
}

//////////////////////////////////////////////////////////////////////
// construct 2D vector perpendicular to input 

vec2 perp(vec2 v) {
    return vec2(-v.y, v.x);
}

//////////////////////////////////////////////////////////////////////
// return whichever vector has smaller x-coordinate

vec2 smaller_x(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

//////////////////////////////////////////////////////////////////////

float ellipse_dist(in vec2 p, in vec2 ab, 
                   out vec4 coeffs,
                   out vec4 roots,
                   out bool is_circle) {

    // distance and sign
    vec2 ds = vec2(1e5, -1);

    is_circle = abs((ab.x - ab.y) / ab.x) < 1e-2;

    // circles need special-case handling because the quartic
    // degenerates into a quadratic.    
    if (is_circle) {

        // a and b are pratically equal, so treat this as a circle
        float dc = length(p) - ab.x;

        float u = normalize(p).x;



        roots = vec4(u, 0, 0, -u);
        coeffs = vec4(0, 1, 0, -u*u);

        ds = vec2(abs(dc), dc);

    } else {

        // general ellipse case


        // formulate the quartic polynomial that represents
        // this ellipse. we are solving for the position 
        // u = cos(theta) along the ellipse such that the
        // tangent at the point [a*u, b*sqrt(1-u^2)] is 
        // perpendicular to the displacement between p
        // and the point itself.
        //
        // there may be multiple valid solutions for this 
        // polynomial -- for a full ellipse we could just 
        // grab the maximal root but we actually need
        // to inspect each of them in the arc case.

        float l = ab.y*ab.y - ab.x*ab.x;

        float ax = ab.x*p.x / l;
        float by = ab.y*p.y / l;

        float a2x2 = ax*ax;
        float b2y2 = by*by;

        // vector of polynomial coefficients
        coeffs = vec4(2.*ax, (a2x2 + b2y2) - 1., -2.*ax, -a2x2);

        // solve for up to 4 roots 
        solve_quartic(coeffs, roots);

        // for each root
        for (int i=0; i<4; ++i) {

            // construct point on unit circle
            vec2 uv = from_cos(roots[i]);

            // need to check this point both above and below x-axis
            for (int j=0; j<2; ++j) {

                // get the absolute distance to the closest point on 
                // the ellipse, as well as its sign
                vec2 pc = ab*uv;
                ds = smaller_x(ds, vec2(length(p - pc), dot(p-pc, pc)));    

                // mirror the root point across the x-axis
                uv.y *= -1.;

            }
        }        

    }

    // combine the absolute distance with the sign of the 
    // distance, respecting original sign classification
    // before root finding
    return ds.x*sign(ds.y);

}

//////////////////////////////////////////////////////////////////////
// distance to (optionally dashed) line

float dline(vec2 p, vec2 a, vec2 b, float dash) {

    p -= a;
    b -= a;

    float l2 = dot(b,b);
    float l = sqrt(l2);

    float u = clamp(dot(p, b) / l2, 0., 1.);

    float d = length(p - u*b);

    if (dash != 0.) {
        float p = dash/6.28;
        d = max(d, -sin(u*l/p)*p);
    }

    return d;

}

//////////////////////////////////////////////////////////////////////
// evaluate a cubic polynomial with given coefficients

float poly3(vec4 p, float x) {
    return ((p[0]*x + p[1])*x + p[2])*x + p[3];
}

//////////////////////////////////////////////////////////////////////
// evaluate a quartic polynomial whose first coefficient is 1.

float poly4(vec4 p, float x) {
    return (((x + p[0])*x + p[1])*x + p[2])*x + p[3];
}

//////////////////////////////////////////////////////////////////////
// given point p, function value at p.x and function derivative at
// p.x, determine estimated distance to curve of function plot.

float dist_to_plot(vec2 p, vec2 fdf) {

    vec2 p0 = vec2(p.x, fdf.x);
    vec2 n = normalize(vec2(-fdf.y, 1));
    return abs(dot(p - p0, n));

}

//////////////////////////////////////////////////////////////////////
// our main function

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    ////////////////////////////////////////////////////////////
    // set up ellipse drawing

	float e_px_per_unit = iResolution.y / 3.;

    vec2 pctr = iResolution.xy * vec2(0.5, 0.65);

    // frag coords relative to ellipse
    vec2 p = (fragCoord.xy - pctr) / e_px_per_unit;

    // white dot
    vec2 q = vec2(0.5, 0.2);

    if (max(iMouse.x, iMouse.y) > 20.) {
        q = (iMouse.xy - pctr) / e_px_per_unit;
    }

    // ellipse radii
    float time = iTime * 0.25 + 4.4;
    vec2 ab = vec2(cos(time + 1.2)*.6 + .8, sin(time*0.7 - 0.5)*.2 + .6);

    // get initial ellipse distance for shading background and drawing ellipse
    vec4 coeffs, roots;
    bool is_circle;
    float d = ellipse_dist(p, ab, coeffs, roots, is_circle);

    ellipse_dist(q, ab, coeffs, roots, is_circle);

    ////////////////////////////////////////////////////////////
    // set up graph 

    float g_px_per_unit = iResolution.x / 2.75;

    // frag coords relative to graph
    vec2 v = (fragCoord.xy - vec2(0.5, 0.2)*iResolution.xy)/g_px_per_unit;

    // get derivative polynomial
    vec4 pder = vec4(4,3,2,1) * vec4(1, coeffs.xyz);

    // evaluate quartic & derivative
    vec2 fdf = vec2(poly4(coeffs, v.x), poly3(pder, v.x));

    if (is_circle) {

        // in circle case, quartic collapses down to a quadratic
        pder[0] = 0.;
        fdf = vec2(poly3(coeffs, v.x), poly3(pder, v.x));

    }

    ////////////////////////////////////////////////////////////
	// get distances for drawing

    // dist to ellipse & markers
    float dwhite = abs(d)*e_px_per_unit - .75;

    // dist to draggable dot
    float dq = length(p-q)*e_px_per_unit - 5.;

    // distances to colored points on ellipse/x-axis
    vec4 d_ellipse_root = vec4(1e5);
    vec4 d_axis_root = vec4(1e5);

    // distance to gray graph line
    float dplot = dist_to_plot(v, 20.*fdf)*g_px_per_unit;

    // distance to dark dot outlines by ellipse
    float ddot = length(p-q)*e_px_per_unit-7.;

    // for each potential root
    for (int i=0; i<4; ++i) {

        // create point on unit circle
        vec2 uv = from_cos(roots[i]);

        vec3 uvmin = vec3(1e5);

        // test against both pos & neg. y
        for (int j=0; j<2; ++j) {

            // ellipse point
            vec2 e = ab*uv;

            // tangent
            vec2 t = 0.5*(ab*perp(uv));

            vec3 uvd = vec3(uv, abs(dot(t, q-e)));
            if (uvd.z < uvmin.z) { uvmin = uvd; }

            // test other sign next
            uv.y = -uv.y;

        }

        // if we found a good tangent point
        if (uvmin.z < 1e-3) {

            // reconstruct unit circle point, ellipse point, tangent            
            uv = uvmin.xy;
            vec2 e = ab*uv;
            vec2 t = 0.5*(ab*perp(uv));

            // distance to point on ellipse
            float edst = length(p-e)*e_px_per_unit - 5.;
            d_ellipse_root[i] = min(d_ellipse_root[i], edst);

            // distance to root on x-axis
            float pdst = length(v - vec2(roots[i], 0.))*g_px_per_unit - 5.;
            d_axis_root[i] = min(d_axis_root[i], pdst);
            dplot = min(dplot, pdst-2.);

            ddot = min(ddot, edst-2.);
            dwhite = min(dwhite, dline(p, q, e, 10./e_px_per_unit)*e_px_per_unit - .25);
            dwhite = min(dwhite, dline(p, e-t, e+t, 0.)*e_px_per_unit - .25);

        }

    }

    ////////////////////////////////////////////////////////////
	// do actual drawing


    const vec3 axis_color = vec3(0.12, 0.15, 0.25);

    const vec3 root_colors[4] = vec3[4](
        vec3(1, 0, 0),
        vec3(1, 0.7, 0),
        vec3(0, 0.7, 0),
        vec3(0, 0, 1)
    );

    // bg color
    vec3 col = vec3(0.45, 0.4, 0.48);

    // ellipse isolines
    col *= 0.95 + 0.05*cos(60.*d);

    // squares
    vec2 g = abs(v - .25*floor(4.*v + 0.5));
    col = mix(col, axis_color, 0.5*smoothstep(1., 0., min(g.x, g.y)*g_px_per_unit));

    // x-axis 
    col = mix(col, axis_color, smoothstep(1., 0., abs(v.y)*g_px_per_unit-1.));

    // plot line
    col = mix( col, vec3(0.025), smoothstep(1., 0., dplot-1.));

    // roots on x-axis
    for (int i=0; i<4; ++i) {
        col = mix(col, root_colors[i], smoothstep(1., 0., d_axis_root[i]));
    }

    // slight dark shadow/glow for ellipse figure
    col = mix( col, vec3(0), 0.6*exp(-.5*dwhite) );

    // ellipse figure
    col = mix( col, vec3(1), smoothstep(1., 0., dwhite) );

    // dark dot outlines 
    col = mix( col, vec3(0.025), smoothstep(1., 0., ddot) );

    // roots on ellipse
    for (int i=0; i<4; ++i) {
        col = mix(col, root_colors[i], smoothstep(1., 0., d_ellipse_root[i]));
    }

    // draggable dot
    col = mix( col, vec3(0.025), smoothstep(1., 0., dq-2.) );
    col = mix(col, vec3(1), smoothstep(1., 0., dq)); 

    // gamma correction
    col = pow(col, vec3(1./2.2));

    fragColor = vec4(col, 1);

}

#define PI 3.14159265359
#define rot(a) mat2(cos(a+PI*vec4(0,1.5,0.5,0)))

// dave hoskins hash function
vec4 hash( in vec2 p ) {
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

// value noise
vec4 noise( in vec2 p ) {
    p*=200.0;
    vec2 i = floor( p );
    vec2 f = fract( p );
	vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

// domain warped noise
float liquid( in vec2 p ) {
    p += noise(vec2(0, iTime*0.0005)-(p*=rot(0.1)*vec2(2.5, 0.5))).rg*0.01;
    p += noise(vec2(0, iTime*0.0002)+(p*=rot(0.2)*2.5)).ba*0.01;
    p += noise(p*=6.5).rg*0.005;
    return noise(p*0.1).a;
}

// used for normal calculation
float height( in vec3 p ) {
    return p.z-liquid(p.xy)*0.001;
}

// normal from central differences
vec3 normal( in vec2 uv ) {
    const vec2 e = vec2(0.0, 0.0001);
    vec3 p = vec3(uv, 0);
    return normalize(vec3(height(p-e.yxx)-height(p+e.yxx),
                          height(p-e.xyx)-height(p+e.xyx),
                          height(p-e.xxy)-height(p+e.xxy)));
}

// custom cubemap
vec3 cubemap( in vec3 dir ) {
    vec3 color = cos(dir*vec3(1, 9, 2)+vec3(2, 3, 1))*0.5+0.5;
    color = (color * vec3(0.8, 0.3, 0.7)) + vec3(0.2);
    color *= dir.y*0.5+0.5;
    color += exp(6.0*dir.y-2.0)*0.05;
    color = pow(color, vec3(1.0/2.2));
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.x;
    vec3 dir = normalize(vec3(uv, 0.2));

    vec3 norm = normal(uv*0.02);
    dir = reflect(dir, norm);

	dir.xz *= rot(iTime*0.5);
    dir.yz *= rot(sin(iTime*0.2)*0.3);

    fragColor.rgb = cubemap(dir);

    fragColor.rgb = clamp(fragColor.rgb, vec3(0), vec3(1));
    fragColor.rgb = mix(fragColor.rgb, vec3(0), dot(uv, uv)*1.0);

    fragColor.a = 1.0;
}

// --- IQs Voronoi: https://www.shadertoy.com/view/MslGD8

vec2 hash( vec2 p ) { p=vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))); return fract(sin(p)*18.5453); }

vec2 voronoi( in vec2 x )
{
    vec2 n = floor( x );
    vec2 f = fract( x );

	vec3 m = vec3( 8.0 );
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2  g = vec2( float(i), float(j) );
        vec2  o = hash( n + g );
      	vec2  r = g - f + o;
		float d = dot( r, r );
        if( d<m.x )
            m = vec3( d, o );
    }

    return vec2( sqrt(m.x), m.y+m.z );
}

// --- Returns the stone tile height and id at the given pos

const float tileHeight = 0.4;

vec2 stone( vec2 uv )
{
    vec2 _uv = uv;

    uv.y = floor( uv.y * 10. * 1./tileHeight );

    vec2 c = voronoi( 5. * uv );
    float height = c.y * 0.7;

    return vec2( height, c.y );
}

