// our main function

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	float scl = 2.0 / iResolution.y;

    vec2 p = (fragCoord.xy - 0.5*iResolution.xy) * scl;

    vec2 ab = vec2(cos(iTime + 1.2)*.6 + .8, sin(iTime*0.1 - 0.5)*.2 + .6);
    vec2 alim = vec2(0.5*iTime, 7.*cos(0.3*iTime));

    if (max(iMouse.x, iMouse.y) > 20.) {

        ab = abs(iMouse.xy - 0.5*iResolution.xy) * scl;
        ab = max(ab, vec2(.125));    
        alim = 16. * iMouse.xy / iResolution.xy - 8.;

    }

    float d = ellipse_arc_dist(p, ab, alim);

    // iq's lovely color scheme
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

    fragColor = vec4(col, 1);

}

float sdCircle(vec2 p, float r) {
 	return length(p) - r;
}

vec2 cMov(vec2 p, vec2 trans) {
 	return p - trans;   
}

float asFilled(float d) {
    return (1.0 - smoothstep(0.,.01, d));
}    

float sMin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

    float objects = 300.;

    float ds = 1000.0;

    for(float i=0.; i<objects; ++i) {
        float r1 = rand(vec2(i));
        float r2 = rand(vec2(i*objects));
        vec2 loc = cMov(uv, vec2( cos(iTime*r1)*r1*1.5, cos(iTime*r2)*r2)*0.8);
        float dc = sdCircle(loc,0.1);
        ds = sMin(ds,dc,.19);
     }

    float d = asFilled(ds) * 0.4;

    fragColor = vec4(min(vec3(.0),vec3(.6,0.1,0.1) * 4.0 * ds) + vec3(0.9,0.1,0.1) * d, 1.0) ;

}

#define scale 0.1
#define pi 3.1415

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.0*(mod(fragCoord, iResolution.xy/2.) - .25*iResolution.xy)/iResolution.y;
 	float dUV = 2.*length(vec2(dFdx(uv.x), dFdy(uv.y)));
    vec2 polar = vec2(length(uv), atan(uv.x, uv.y));
    float l = mod(polar.x, scale);
    float ring = smoothstep(0.033,0.033 + dUV,l);
    ring *= 1.-smoothstep(0.053, 0.053 + dUV, l);
    ring = 1.-ring;

    float theta = polar.y+iTime;
    float blue = smoothstep(pi/16.-0.01, pi/16.+ dUV, mod(theta, pi/4.));
    blue *= 1.- smoothstep(3.*pi/16.-0.01, 3.*pi/16. + dUV, mod(theta, pi/4.));

    vec3 b = (1.-ring)*vec3(0.,0.,1.)*blue;

    fragColor = vec4(ring + b, 1.);
}

