  for (float k = 0.; k < 2.; k ++) {
    q = qq;
    if (k > 0.) q.xz = Rot2D (q.xz, pi / 6.);
    a = (length (q.xz) > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
    q.xz = Rot2D (q.xz, 2. * pi * (floor (6. * a + 0.5) / 6.));
    q.x += 0.2;
    q.y -= 0.05 + 0.005 * (1. - 2. * k) + 0.4 * dot (q.xz, q.xz);
    q.z *= 2.5;
