}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0005, -0.0005);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += min (0.05, 3. * h);
    if (sh < 0.001) break;
  }
  return 0.5 + 0.5 * sh;
}

float BgVal (vec3 rd)
{
  return 0.05 + 0.245 * (rd.y + 1.) * (rd.y + 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  vec2 vf;
  float dstObj, spec, sh, r;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vf = vec2 (0.);
    spec = 0.1;
    if (idObj == 1) {
      col = vec3 (0.1, 0.3, 0.9);
      r = length (ro.xz);
      if (r < 0.3) col *= 1. - 0.5 * smoothstep (0.15, 0.2, r) * step (0.007, qHit.z);
      spec = 0.05;
      vf = vec2 (128., 0.5);
    } else if (idObj == 2) {
      col = vec3 (0.1, 0.2, 0.5);
    } else if (idObj == 3) {
      r = length (qHit.xz + vec2 (0.4, 0.)) * (0.9 + 0.1 * SmoothBump (0.25, 0.75, 0.15,
         mod (32. * atan (qHit.z, - qHit.x) / (2. * pi), 1.)));
      col = mix (vec3 (1., 1., 0.2), vec3 (0.7, 0.7, 0.4), 
         SmoothBump (0.3, 0.7, 0.1, mod (8. * r + 0.5, 1.)));
      spec = 0.3;
      if (vn.y < 0.) col = mix (col, vec3 (Maxv3 (col)), 0.5);
      vf = vec2 (128., 0.5);
    } else if (idObj == 4) {
      col = mix (vec3 (0.4, 0.2, 0.1), vec3 (0.2, 0.7, 0.2), smoothstep (-0.2, 0.2, qHit.y));
      if (qHit.y > 1.3) col *= 1. - 0.3 * SmoothBump (0.45, 0.55, 0.02,
         mod (24. * atan (ro.z, - ro.x) / (2. * pi), 1.));
      vf = vec2 (64., 3. - 2. * smoothstep (-0.2, 0.2, qHit.y));
    } else if (idObj == 5) {
      col = mix (vec3 (0.5, 1., 0.5) * (0.5 + 0.5 * SmoothBump (0.2, 0.8, 0.05,
         mod (16. * qHit.y, 1.))), vec3 (1., 0., 0.), step (1.4, qHit.y));
      spec = 0.;
    }
    if (idObj == 1 || idObj == 3 && vn.y > 0.01) col *= BgVal (reflect (rd, vn));
    if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    sh = ObjSShadow (ro, sunDir);
    col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
       spec * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 64.);
  } else {
    col = vec3 (0.6, 0.6, 0.5) * BgVal (rd);
  }
  return pow (clamp (col, 0., 1.), vec3 (0.8));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvs, ori, ca, sa;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.2 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az -= 0.02 * pi * tCur;
    el -= 0.15 * pi * cos (0.05 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -20.);
  ro.y += 0.5;
  zmFac = 5.4 - 0.9 * abs (el);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  dstFar = 30.;
  sunDir = vuMat * normalize (vec3 (1., 2., -1.));
  col = ShowScene (ro, rd);
  uvs *= uvs * uvs;
  col *= mix (0.8, 1., pow (1. - 0.5 * length (uvs * uvs), 4.));
  fragColor = vec4 (col, 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

