mat3 m;
float k;
mat3 rot (vec3 s) {
	float 	sa = sin(s.x),	ca = cos(s.x),
			sb = sin(s.y),	cb = cos(s.y),
			sc = sin(s.z),	cc = cos(s.z);
	return mat3 (
			vec3(cb*cc, -cb*sc, sb),
			vec3(sa*sb*cc+ca*sc, -sa*sb*sc+ca*cc, -sa*cb),
			vec3(-ca*sb*cc+sa*sc, ca*sb*sc+sa*cc, ca*cb));
}

vec3 app (vec3 v) {
	for (int i = 0; i < 10; i++)
        v = abs(k*m*v/dot(v,v)*0.5-0.5)*2.-1.; 
    return v;
}

vec3 ap (vec3 v) {
	for (int i = 0; i < 10; i++)
        v = abs(k*m*v/dot(v,v)*0.5-0.5)*2.-1.; 
    return v;
}

vec3 norm (vec3 p) {
		vec2 e = vec2 (.05,0.);
    	vec3 g = ap(p);
		return normalize(vec3(
				ap(p+e.xyy).x - g.x,
				ap(p+e.yxy).y - g.y,
				ap(p+e.yyx).z - g.z));
}

float SCurve (float value, float amount, float correction) {

	float curve = 1.0; 

    if (value < 0.5)
    {

        curve = pow(value, amount) * pow(2.0, amount) * 0.5; 
    }

    else
    { 	
    	curve = 1.0 - pow(1.0 - value, amount) * pow(2.0, amount) * 0.5; 
    }

    return pow(curve, correction);
}

const float PI = 3.14159265359;

//----------------------------------------------------------------
void smallKoleidoscope(inout vec2 uv, float ka)
{
  float angle = abs (mod (atan (uv.x, uv.y), 2.0 * ka) - ka) + 0.01*iTime;
  uv = length(uv) * vec2(cos(angle), sin(-angle));
}
//----------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 uv = fragCoord.xy/iResolution.xy*2.-1.;
    uv.x *= iResolution.x/iResolution.y;

    vec2 mp = vec2(0.1, 0.1); //iMouse.xy / iResolution.xy;
    uv *= 0.2 + 10. * mp.x;
    float sideCount = -7.0; //1.0 + round(mp.y * 11.0);
    smallKoleidoscope (uv, PI / sideCount);

    float t = 0.015 * iTime;
    m = rot(t + vec3(2, 3, 5));
    k = 1.3 + 0.1 * sin(0.008 * iTime);
    vec3 v = (0.5 + 0.25 * sin(0.003 * iTime)) * m * vec3(2.0 * uv, 0);
    vec3 col = sin(app(v + iTime * 0.0001)) * 0.5 + 0.5;

    col = col * 0.8 + 0.2 * (sin(norm(v)) * 0.5 + 0.5);
    col = col * 0.7 + 0.1;


    	float gm = 1.0/2.2;
    	col = vec3(pow(col.r, gm), pow(col.g, gm), pow(col.b, gm));
    	col.r = SCurve(col.g * 0.3 + col.r * 0.7, 6.0, 2.2);
    	col.g = SCurve(col.g * 0.5 + col.b * 0.5, 4.0, 2.2);
    	col.b = SCurve(col.g * 0.3 + col.b * 0.7, 2.0, 2.2);

        col.g = col.g * 0.9 + 0.1;
        col.b = col.b * 0.7 + 0.3;


    fragColor = vec4(col,1.0);
}

