float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec3 HueToRgb (float c);

float vRot[4], wSlc[3], dstFar, tCur, spRad, nSlc, nSeg;
int idObj;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, dRot, az, r, s1, s2, c, dr;
  dMin = dstFar;
  dRot = 1.;
  for (int k = 0; k < 3; k ++) {
    q = p;
    q.xz = Rot2D (q.xz, - dRot * vRot[k]);
    az = (length (q.xz) > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
    c = cos (asin (q.y / length (q)));
    r = spRad - 0.2 * float (k);
    s1 = 0.2 * c;
    s2 = 0.25 / c;
    dr = 0.03 * max (SmoothBump (0.5 - s2, 0.5 + s2, 0.5 * s2, mod (q.y * nSlc / r +
       dRot * az, 1.)), SmoothBump (0.5 - s1, 0.5 + s1, 0.5 * s1, mod (nSeg * az, 1.)));
    d = abs (length (q) - r - dr) - 0.03 - dr;
    if (wSlc[k] > 0.) d = max (d, wSlc[k] - abs (2. * mod (nSlc * q.y / r + dRot * az +
       0.5, 1.) - 1.) / nSlc);
    DMIN (k + 1);
    dRot *= -1.;
  }
  dMin *= 0.5;
  d = length (q) - spRad + 0.6;
  DMIN (4);
  return dMin;  
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 200; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0002 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, ltDir, ltPos[4], sCol[4];
  float dstObj, dRot, aRot, r, az, el, s1, s2, c, dfSum, spSum, at;
  sCol[0] = HueToRgb (0.4);
  sCol[1] = HueToRgb (0.6);
  sCol[2] = HueToRgb (0.2);
  sCol[3] = HueToRgb (0.);
  ltPos[0] = 5. * vec3 (1., 1., 1.);
  ltPos[1] = 5. * vec3 (-1., 1., -1.);
  ltPos[2] = 5. * vec3 (1., -1., -1.);
  ltPos[3] = 5. * vec3 (-1., -1., 1.);
  spRad = 2.;
  nSlc = 8.;
  wSlc[0] = 0.9 * (spRad / (2. * nSlc)) * SmoothBump (0.15, 0.85, 0.05, mod (0.05 * tCur, 1.));
  wSlc[1] = 0.9 * ((spRad - 0.2) / (2. * nSlc)) * SmoothBump (0.27, 0.73, 0.05, mod (0.05 * tCur, 1.));
  wSlc[2] = 0.9 * ((spRad - 0.4) / (2. * nSlc)) * SmoothBump (0.39, 0.61, 0.05, mod (0.05 * tCur, 1.));
  vRot[0] = 0.2 * tCur;
  vRot[1] = 0.3 * tCur;
  vRot[2] = 0.4 * tCur;
  vRot[3] = 0.5 * tCur;
  nSeg = 32.;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    r = spRad - 0.2 * float (idObj - 1);
    dRot = (idObj == 1 || idObj == 3) ? 1. : -1.;
    for (int k = 0; k < 4; k ++) {
      if (k == idObj - 1) {
        col = sCol[k];
        aRot = vRot[k];
        break;
      }
    }
    if (idObj < 4 && length (ro) < r + 0.051) col *= 0.7;
    else {
      ro.xz = Rot2D (ro.xz, - dRot * aRot);
      az = (length (ro.xz) > 0.) ? atan (ro.z, - ro.x) / (2. * pi) : 0.;
      el = asin (ro.y / length (ro)) / (0.5 * pi);
      c = cos (0.5 * pi * el);
      s1 = 0.03 * c;
      s2 = 0.02 / c;
      col *= 1. - 0.2 * max (SmoothBump (0.5 - s1, 0.5 + s1, 0.5 * s1,
         mod (ro.y * nSlc / r + dRot * az, 1.)), (1. - smoothstep (0.93, 0.98, abs (el))) *
         SmoothBump (0.5 - s2, 0.5 + s2, 0.5 * s2, mod (nSeg * az, 1.)));
    }
    dfSum = 0.;
    spSum = 0.;
    for (int k = 0; k < 4; k ++) {
      ltDir = normalize (ltPos[k]);
      at = smoothstep (0.3, 0.7, dot (normalize (ltPos[k] - ro), ltDir));
      dfSum += at * max (dot (vn, ltDir), 0.);
      spSum += at * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    }
    col = col * (0.05 + 0.7 * dfSum) + 0.2 * spSum;
  } else {
    col = vec3 (1., 0.86, 0.8) * (0.2 + 0.1 * (rd.y + 1.) * (rd.y + 1.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvs, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    el -= 0.2 * pi * sin (0.03 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -15.);
  rd = vuMat * normalize (vec3 (uv, 6.5));
  dstFar = 30.;
  col = ShowScene (ro, rd);
  uvs *= uvs * uvs;
  col *= mix (0.8, 1., pow (1. - 0.5 * length (uvs * uvs), 4.));
  fragColor = vec4 (col, 1.);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec3 HueToRgb (float c)
{
  return clamp (abs (fract (c + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.);
}

