vec2 warpPositionToUse(vec2 center, vec2 current, float r, float R, float ratio, vec2 t)
{
    vec2 currentToUse = vec2(current.x, current.y * ratio + 0.5 - 0.5 * ratio);
    vec2 centerToUse  = vec2(center.x, center.y * ratio + 0.5 - 0.5 * ratio);
    vec2 tToUse = vec2(t.x, t.y / ratio);

    vec2  p 	= current;
    float L_c  	= distance(currentToUse, centerToUse);
    float L_e_c = distance(currentToUse - t, centerToUse);

    float x 	= currentToUse.x;
    float y 	= currentToUse.y;
    float tx 	= t.x;
    float ty	= t.y;

    float ax = x - R * tx / (R - r) - centerToUse.x;
    float ay = y - R * ty / (R - r) - centerToUse.y;

    float bx = tx / (R - r);
    float by = ty / (R - r);

    float A = bx * bx + by * by - 1.0;
    float B = 2.0 * (ax * bx + ay * by);
    float C = ax * ax + ay * ay;
    float G = sqrt(B * B - 4.0 * A * C);

    float d = (-B + G) / (2.0 * A);

    if (d < r)
    {
        d = (-B - G) / (2.0 * A);
    }


    float delta = 1.0;

    if (L_e_c < r)
    {
        p = current - tToUse;
    }
    else if (L_c < R)
    {
        p = p - ((R - d) / (R - r)) * tToUse;  
    }

    return p;
}

