// by Tomasz Dobrowolski' 2018

// Random Delaunay triangulation on regular grid.

// +LMB to see distance to Voronoi edges

// This is a response to Shane, who wrote:
// "I'm writing a wish list, an out-of-the-box way to cheaply produce
//  a geometric Delaunay triangulation of random 2D points"
// in https://www.shadertoy.com/view/Xsyczh

// I wouldn't describe my method "out of the box", as it is more or
// less straigh-forward implementation for heavily restricted
// point set on a regular grid.
// Nevertheless, enjoy!

// Also check out PolyCube's edition with panning/zooming:
// http://polycu.be/edit/?h=M963jR

#define FLIP_ANIMATION 1

// Hash variation by Shane,
// from https://www.shadertoy.com/view/4dSfzD
vec2 hashPt(vec2 p, float timeOffset) {
   float n = sin(dot(p, vec2(41, 289)));
   // Animated.
   p = fract(vec2(262144, 32768)*n);
   return sin( p*6.2831853 + timeOffset )*.5 + .5;
}

// We generate point in the cell center
// restricted in movement to box half the cell width/height,
// so Delaunay triangles behaves regularly.
vec2 cellPt(vec2 p) {
    return p + .5 + (hashPt(p, iTime) - .5)*.5;
}

// Fast distance to edge-Voronoi.
// Since seed positions are heavily restricted,
// 3x3 check is enough to search for a closest point
// as well as search for a pair of neighbours.
vec2 edgeVoronoi(vec2 p) {
   vec2 h, pH = floor(p);

   vec2 mh = cellPt(pH) - p;
   float md = 8.0;
   for (int j=-1; j<=1; ++j )
   for (int i=-1; i<=1; ++i ) {
      h = cellPt(pH + vec2(i,j)) - p;
      float d = dot(h, h);
      if (d < md) {
         md = d;
         mh = h;
      }
   }

   const float eps = .0001;
   float ed = 8.0;

   for (int j=-1; j<=1; ++j )
   for (int i=-1; i<=1; ++i ) {
      h = cellPt(pH + vec2(i,j)) - p;
      if (dot(h-mh, h-mh) > eps)
         ed = min( ed, dot( 0.5*(h+mh), normalize(h-mh) ) );
   }
   return vec2(sqrt(md),ed);
}

// Signed distance to a line crossing (p0, p1) segment.
float distLine( vec2 p0, vec2 p1 ) {
   vec2 e0 = p1 - p0;
   return dot( p0, normalize(vec2(e0.y,-e0.x)) );
}

// Use "parabolic lifting" method to calculate if two triangles are about to flip.
// This is actually more reliable than circumscribed circle method.
// The technique is based on duality between Delaunay Triangulation
// and Convex Hull, where DT is just a boundary of convex hull
// of projected seeds onto paraboloid.
// We project (h1 h2 h3) triangle onto paraboloid
// and return the distance of the origin
// to a plane crossing projected triangle.
float flipDistance(vec2 h1, vec2 h2, vec2 h3)
{
   // Projects triangle on paraboloid.
   vec3 g1 = vec3(h1, dot(h1,h1));
   vec3 g2 = vec3(h2, dot(h2,h2));
   vec3 g3 = vec3(h3, dot(h3,h3));
   // Return signed distance of (g1, g2, g3) plane to the origin.
   #if FLIP_ANIMATION
     return dot(g1, normalize(cross(g3-g1, g2-g1)));
   #else
     // If we don't do animation, we are only interested in a sign,
     // so normalization is unnecessary.
   	 return dot(g1, cross(g3-g1, g2-g1));
   #endif
}

// Find distance to closest Delaunay edge in (h0, h1, h2, h3) quad.
float delaunayQuad(vec2 h0, vec2 h1, vec2 h2, vec2 h3) {
   // Get distance to quad (note: in general it can be concave, but we don't care).
   float md = min(
      min(distLine(h0, h1), distLine(h1, h2)),
      min(distLine(h2, h3), distLine(h3, h0)));
   if (md < 0.0)
      return 8.0; // outside of the quad

   // Calculate flip distance relative to h2, but any other point would do.
   float dc = flipDistance(h0 - h2, h1 - h2, h3 - h2);

   #if FLIP_ANIMATION
     float f = clamp(dc*6. + .5, 0., 1.);
   #else
     float f = float(dc > 0.0);
   #endif
   // Flipping rotates diagonal from (h0 h2) to (h3 h1).
   // Calculate distance to diagonal (positive from both sides).
   return min(md, abs(distLine(mix(h0, h3, f), mix(h2, h1, f))));
}

// Final function visits 4 quads around the center cell.
float delaunayTriangulation(vec2 p) {
   vec2 pH = floor(p);

   vec2 o = cellPt(pH) - p;
   // Go clock-wise around center cell.
   vec2 h0 = cellPt(pH + vec2(-1, 0)) - p;
   vec2 h1 = cellPt(pH + vec2(-1,-1)) - p;
   vec2 h2 = cellPt(pH + vec2( 0,-1)) - p;
   vec2 h3 = cellPt(pH + vec2( 1,-1)) - p;
   vec2 h4 = cellPt(pH + vec2( 1, 0)) - p;
   vec2 h5 = cellPt(pH + vec2( 1, 1)) - p;
   vec2 h6 = cellPt(pH + vec2( 0, 1)) - p;
   vec2 h7 = cellPt(pH + vec2(-1, 1)) - p;

   float md =   delaunayQuad(h0,h1,h2,o);
   md = min(md, delaunayQuad(o,h2,h3,h4));
   md = min(md, delaunayQuad(h6,o,h4,h5));
   md = min(md, delaunayQuad(h7,h0,o,h6));
   return md;
}

// Do shading with pseudo-linear color space and fake anti-aliasing.
vec3 lin(vec3 col) { return col*col; }
vec3 gamma(vec3 col) { return sqrt(max(vec3(0),col-.03)); }
vec3 shade(float tri, float dist, vec2 ev, float ss) {
   ss *= .6;
   return
    mix(
     mix(
      mix(
       mix(
        mix(
         lin(vec3(.7,.5,1.))*(dist*2.2 + .1),
         lin(vec3(.7,.6,1.)),
         smoothstep(.03+ss,.03-ss,ev.y)*.3
        ),
        vec3(0), // edge shadow
        smoothstep(.05,.01+ss,tri)*.3
       ),
       lin(vec3(1.,.7,.3)),
       max(
        smoothstep(ss*2.,0.,abs(fract(dist*8.+.5)-.5)/8.)*.7*step(1./16.,dist),
        smoothstep(.01+ss,.01-ss,tri))
      ),
      vec3(0), // seed point shadow
      smoothstep(.08,min(.075,.05+ss),ev.x)*.7
     ),
     lin(vec3(1,.8,.5)),
     smoothstep(.05+ss,.05-ss,ev.x)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ss = .01;
    vec2 p = (fragCoord.xy - iResolution.xy*.5)*ss;
    vec2 ev = edgeVoronoi(p);
    float tri = delaunayTriangulation(p);
    float dist = (iMouse.z > 0.) ? ev.y : tri;
    vec3 col = shade(tri, dist, ev, ss);
    fragColor = vec4(gamma(col), 1);
}

#define MAXNORM
#define SHOW_POLYGON
#define NUM_CORNERS int(mod(iTime,6.))*2+4

const float eps = .000005;
const float zoom = 1.;
const float dot_size=.005;
const vec3 point_col=vec3(1,1,0);
const float pi = 3.1416;
const int max_corners=16;

vec2 parametric_cub_bezier(float t, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec2 a0 = (-p0 + 3. * p1 - 3. * p2 + p3);
	vec2 a1 = (3. * p0  -6. * p1 + 3. * p2);
	vec2 a2 = (-3. * p0 + 3. * p1);
	vec2 a3 = p0;

	return (((a0 * t) + a1) * t + a2) * t + a3;
}

void sort_roots3(inout vec3 roots){
	vec3 tmp;

	tmp[0] = min(roots[0],min(roots[1],roots[2]));
	tmp[1] = max(roots[0],min(roots[1],roots[2]));
	tmp[2] = max(roots[0],max(roots[1],roots[2]));

	roots=tmp;
}

// Modified from http://tog.acm.org/resources/GraphicsGems/gems/Roots3And4.c
// Credits to Doublefresh for hinting there
int solve_quadric(vec2 coeffs, inout vec2 roots){

    // normal form: x^2 + px + q = 0
    float p = coeffs[1] / 2.;
    float q = coeffs[0];

    float D = p * p - q;

    if (D < 0.){
		return 0;
    }
    else if (D > 0.){
		roots[0] = -sqrt(D) - p;
		roots[1] = sqrt(D) - p;

		return 2;
    }
}

//From Trisomie21
//But instead of his cancellation fix i'm using a newton iteration
int solve_cubic(vec3 coeffs, inout vec3 r){

	float a = coeffs[2];
	float b = coeffs[1];
	float c = coeffs[0];

	float p = b - a*a / 3.0;
	float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
	float p3 = p*p*p;
	float d = q*q + 4.0*p3 / 27.0;
	float offset = -a / 3.0;
	if(d >= 0.0) { // Single solution
		float z = sqrt(d);
		float u = (-q + z) / 2.0;
		float v = (-q - z) / 2.0;
		u = sign(u)*pow(abs(u),1.0/3.0);
		v = sign(v)*pow(abs(v),1.0/3.0);
		r[0] = offset + u + v;	

		//Single newton iteration to account for cancellation
		float f = ((r[0] + a) * r[0] + b) * r[0] + c;
		float f1 = (3. * r[0] + 2. * a) * r[0] + b;

		r[0] -= f / f1;

		return 1;
	}
	float u = sqrt(-p / 3.0);
	float v = acos(-sqrt( -27.0 / p3) * q / 2.0) / 3.0;
	float m = cos(v), n = sin(v)*1.732050808;

	//Single newton iteration to account for cancellation
	//(once for every root)
	r[0] = offset + u * (m + m);
    r[1] = offset - u * (n + m);
    r[2] = offset + u * (n - m);

	vec3 f = ((r + a) * r + b) * r + c;
	vec3 f1 = (3. * r + 2. * a) * r + b;

	r -= f / f1;

	return 3;
}

//Sign computation is pretty straightforward:
//I'm solving a cubic equation to get the intersection count
//of a ray from the current point to infinity and parallel to the x axis
//Also i'm computing the intersection count with the tangent in the end points of the curve
float cubic_bezier_sign(vec2 uv, vec2 p0, vec2 p1, vec2 p2, vec2 p3){

	float cu = (-p0.y + 3. * p1.y - 3. * p2.y + p3.y);
	float qu = (3. * p0.y - 6. * p1.y + 3. * p2.y);
	float li = (-3. * p0.y + 3. * p1.y);
	float co = p0.y - uv.y;

	vec3 roots = vec3(1e38);
	int n_roots = solve_cubic(vec3(co/cu,li/cu,qu/cu),roots);

	int n_ints = 0;

	for(int i=0;i<3;i++){
		if(i < n_roots){
			if(roots[i] >= 0. && roots[i] <= 1.){
				float x_pos = -p0.x + 3. * p1.x - 3. * p2.x + p3.x;
				x_pos = x_pos * roots[i] + 3. * p0.x - 6. * p1.x + 3. * p2.x;
				x_pos = x_pos * roots[i] + -3. * p0.x + 3. * p1.x;
				x_pos = x_pos * roots[i] + p0.x;

				if(x_pos < uv.x){
					n_ints++;
				}
			}
		}
	}

	vec2 tang1 = p0.xy - p1.xy;
	vec2 tang2 = p2.xy - p3.xy;

	vec2 nor1 = vec2(tang1.y,-tang1.x);
	vec2 nor2 = vec2(tang2.y,-tang2.x);

	if(p0.y < p1.y){
		if((uv.y<=p0.y) && (dot(uv-p0.xy,nor1)<0.)){
			n_ints++;
		}
	}
	else{
		if(!(uv.y<=p0.y) && !(dot(uv-p0.xy,nor1)<0.)){
			n_ints++;
		}
	}

	if(p2.y<p3.y){
		if(!(uv.y<=p3.y) && dot(uv-p3.xy,nor2)<0.){
			n_ints++;
		}
	}
	else{
		if((uv.y<=p3.y) && !(dot(uv-p3.xy,nor2)<0.)){
			n_ints++;
		}
	}

	if(n_ints==0 || n_ints==2 || n_ints==4){
		return 1.;
	}
	else{
		return -1.;
	}
}

float max_norm(vec2 vector){
	return max(abs(vector.x),abs(vector.y));
}

float hexagon_norm(vec2 vector){
	return max(dot(abs(vector),vec2(0.866025403784439,.5)),abs(vector.y));
}

float octagon_norm(vec2 vector){
	return max(dot(abs(vector),vec2(0.707106781186548)),max_norm(vector));
}

float decagon_norm(vec2 vector){
	vec2 dir1=vec2(0.587785252292473,0.809016994374947);
	vec2 dir2=vec2(0.951056516295154,0.309016994374947);

	return max(max(abs(vector.y),dot(abs(vector),dir1)),dot(abs(vector),dir2));
}

//only even numbers >=4 allowed
float regular_polygon_norm(vec2 vector, int n){
	float dir_step=(1.-2./float(n))*pi;

	float norm=0.;

	for(int i=0;i<max_corners/2;i++){
		if(i<n/2){
			float cur_dir=float(i)*dir_step;
			vec2 dir=abs(vec2(sin(cur_dir),cos(cur_dir)));

			norm=max(norm,dot(abs(vector),dir));
		}
	}

	return norm;
}

float cubic_bezier_max_norm_dis(vec2 uv, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec2 a3 = (-p0 + 3. * p1 - 3. * p2 + p3);
	vec2 a2 = (3. * p0 - 6. * p1 + 3. * p2);
	vec2 a1 = (-3. * p0 + 3. * p1);
	vec2 a0 = p0 - uv;

	vec2 b2 = 3.*a3;
	vec2 b1 = 2.*a2;
	vec2 b0 = a1;

	vec3 roots1 = vec3(1e38);
	vec3 roots2 = vec3(1e38);

	int num_roots1 = solve_cubic(vec3(a0.x+a0.y,a1.x+a1.y,a2.x+a2.y)/(a3.x+a3.y),roots1);
	int num_roots2 = solve_cubic(vec3(a0.x-a0.y,a1.x-a1.y,a2.x-a2.y)/(a3.x-a3.y),roots2);

	vec2 roots_drv_x = vec2(1e38);
	vec2 roots_drv_y = vec2(1e38);

	int num_roots_drv_x = solve_quadric(vec2(b0.x,b1.x)/b2.x, roots_drv_x);
	int num_roots_drv_y = solve_quadric(vec2(b0.y,b1.y)/b2.y, roots_drv_y);

	float d0 = 1e38;

	for(int i=0;i<3;i++){
		if(i<num_roots1){
			roots1[i]=clamp(roots1[i],0.,1.);
			vec2 point = parametric_cub_bezier(roots1[i],p0,p1,p2,p3);
			d0 = min(d0,max_norm(point-uv));
		}
		if(i<num_roots2){
			roots2[i]=clamp(roots2[i],0.,1.);
			vec2 point = parametric_cub_bezier(roots2[i],p0,p1,p2,p3);
			d0 = min(d0,max_norm(point-uv));
		}
		if(i<num_roots_drv_x){
			roots_drv_x[i]=clamp(roots_drv_x[i],0.,1.);
			vec2 point = parametric_cub_bezier(roots_drv_x[i],p0,p1,p2,p3);
			d0 = min(d0,max_norm(point-uv));
		}
		if(i<num_roots_drv_y){
			roots_drv_y[i]=clamp(roots_drv_y[i],0.,1.);
			vec2 point = parametric_cub_bezier(roots_drv_y[i],p0,p1,p2,p3);
			d0 = min(d0,max_norm(point-uv));
		}
	}

	return d0;
}

float cubic_bezier_regular_polygon_norm_dis(vec2 uv, int n, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec2 a3 = -p0 + 3. * p1 - 3. * p2 + p3;
	vec2 a2 = 3. * p0 - 6. * p1 + 3. * p2;
	vec2 a1 = -3. * p0 + 3. * p1;
	vec2 a0 = p0 - uv;

	float d0 = 1e38;

	float dir_step=(1.-2./float(n))*pi;

	for(int i=0;i<max_corners/2;i++){
		if(i<n/2){
			float cur_dir=dir_step*float(i);
			float next_dir=dir_step*float(i+1);

			vec2 dir=(vec2(sin(cur_dir),cos(cur_dir)));
			vec2 dir2=(vec2(sin(next_dir),cos(next_dir)));

			vec3 poly=vec3(dot(a0,dir+dir2),dot(a1,dir+dir2),dot(a2,dir+dir2))/dot(a3,dir+dir2);
			vec2 poly_drv=vec2(dot(a1,(dir)),2.*dot(a2,(dir)))/(3.*dot(a3,(dir)));

			vec3 roots=vec3(1e38);
			vec2 roots_drv=vec2(1e38);

			int num_roots = solve_cubic(poly,roots);
			int num_roots_drv = solve_quadric(poly_drv,roots_drv);

			for(int j=0;j<3;j++){
				if(j<num_roots){
					roots[j]=clamp(roots[j],0.,1.);
					vec2 point = parametric_cub_bezier(roots[j],p0,p1,p2,p3);
					d0 = min(d0,regular_polygon_norm(point-uv,NUM_CORNERS));
				}
				if(j<num_roots_drv){
					roots_drv[j]=clamp(roots_drv[j],0.,1.);
					vec2 point = parametric_cub_bezier(roots_drv[j],p0,p1,p2,p3);
					d0 = min(d0,regular_polygon_norm(point-uv,NUM_CORNERS));
				}
			}
		}
	}

	return d0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float border=2./iResolution.x;

	vec2 uv = fragCoord.xy / iResolution.xy;
	uv-=.5;
	uv.y *= iResolution.y / iResolution.x;

