	mouse-=.5;
	mouse.y *= iResolution.y / iResolution.x;

	border*=zoom;
	uv *= zoom;
	mouse *= zoom;

	float t0=mod(iTime*2.+1.5,24.*pi);

	vec2 p0 = vec2(-cos(t0 * 1./2.) * .2,sin(t0 * 1./3.) * .25);
	vec2 p1 = vec2(-cos(t0 * 2./3.) * .2,sin(t0 * 1./4.) * .1);
	vec2 p2 = vec2(cos(t0 * 1./4.) * .1,-sin(t0 * 2./3.) * .2);
	vec2 p3 = vec2(cos(t0 * 1./3.) * .25,-sin(t0 * 1./2.) * .1);

	//vec2 p0=vec2(-.3,-.1);
	//vec2 p1=vec2(-.5,.5);
	////vec2 p1=mouse;
	//vec2 p2=vec2(.1,-.2);
	//vec2 p3=vec2(.2,.15);

    float radius=1e38;

    //mouse condition copied from mattz (https://www.shadertoy.com/view/4dyyR1)
    if(max(iMouse.x, iMouse.y) > 20.){
        #ifdef SHOW_POLYGON

        #ifdef MAXNORM
		radius = cubic_bezier_max_norm_dis(mouse,p0,p1,p2,p3);
    	#else
    	radius = cubic_bezier_regular_polygon_norm_dis(mouse,NUM_CORNERS,p0,p1,p2,p3);
    	#endif

        #else
        p0=vec2(-.3,-.1);
        p1=mouse;
        p2=vec2(.1,-.2);
        p3=vec2(.2,.15);
        #endif
    }

	float d0 = 1e38;

    #ifdef MAXNORM
	d0 = min(d0,cubic_bezier_max_norm_dis(uv,p0,p1,p2,p3));
    #else
	d0 = min(d0,cubic_bezier_regular_polygon_norm_dis(uv,NUM_CORNERS,p0,p1,p2,p3));
    #endif

	float sgn = cubic_bezier_sign(uv,p0,p1,p2,p3);

	//iq's sd color scheme
	vec3 col = vec3(1.0) - sgn*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-8.0 * d0);
	col *= 0.8 + 0.2*cos(480.0*d0);
	col = mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(d0)));

	d0 = 1e38;

	d0 = min(d0,distance(p0,uv) - dot_size);
	d0 = min(d0,distance(p1,uv) - dot_size);
	d0 = min(d0,distance(p2,uv) - dot_size);
	d0 = min(d0,distance(p3,uv) - dot_size);

	col = mix(point_col,col,smoothstep(0.,border,d0));

	d0 = 1e38;

    #ifdef SHOW_POLYGON
    #ifdef MAXNORM
	d0 = min(d0,abs(max_norm(mouse-uv)-radius));
    #else
	d0 = min(d0,abs(regular_polygon_norm(mouse-uv,NUM_CORNERS)-radius));
    #endif
    #endif

	col = mix(vec3(0),col,smoothstep(0.,border,d0));

	fragColor = vec4(col,1);
}

