void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(.0);
    vec2 uv = st;


    st *= 8.;

    vec2 f = fract(st);
    vec2 i = floor(st);


    float time = iTime * 0.4;
    float time_i = floor(time);
    float time_f = fract(time);

    vec2 pos = vec2(randFloat(time_i)*iResolution.x/iResolution.y, randFloat(time_i+ 44.));
    vec2 posNext = vec2(randFloat(time_i+1.)*iResolution.x/iResolution.y, randFloat(time_i+45.));
    pos = mix(pos, posNext, time_f);

    //float distanceToMoving = distance(closestPoint/8., pos);
    float distanceToMoving = distance(st/8., pos);
    float m_dist = 1.0;  // minimun distance

    vec2 closestPoint = vec2(0.0,0.0);
    vec3 closestPointFactor = vec3(0.);
    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(x),float(y));

            // Random position from current + neighbor place in the grid
            vec2 point = random2(i + neighbor);
			vec2 randSeed=point;
			// Animate the point
            //point = 0.5 + 0.5*sin(iTime*(0.4 +point.y *0.9) + 6.2831*point);

			// Vector between the pixel and the point
            vec2 diff = neighbor + point - f;
			float factor = .38;

             // Distance to the point
            float dist = length(diff);
            for (int d= -1; d <= 1; d++) {
        		for (int j= -1; j <= 1; j++) {
            	vec2 neighborSecondIteration = vec2(float(d),float(j));

                vec2 seed = random2(i + neighbor);

            	float weight = seed.x;
                factor +=  dist *weight* float ((d==0)&&(j==0)) * (sin(iTime*4.1*(1.-seed.x )+seed.y*4.21321)*0.5+0.5 ) ;

        		}
    		}

            if(distanceToMoving*8.<2.){ 
                factor += (1.0-pow(smoothstep(0.,2.,distanceToMoving*8.), 2.))* 0.5;
            }

            // Keep the closer distance
            if(dist*factor<= m_dist){
                m_dist =dist *factor;
                closestPoint = point + i + neighbor;
                closestPointFactor.x =factor; 
                closestPointFactor.yz = randSeed.xy;
            }

        }
    }



    m_dist = min(distanceToMoving*8.*0.5, m_dist);
    // Draw the min distance (distance field)
    color += 1.0 -m_dist;



    color *= vec3(0.3,0.9,0.2);
    color.g *= 1.0- m_dist;
    color.g *= 1.0- m_dist;
    color.b *=  pow(m_dist,-0.328);

    // Debug Circle
    //DrawCircle(color,pos,uv,vec3(1.));
	//color = vec3(closestPointFactor.x);
    fragColor = vec4(color,1.0);
}

void mainImage(out vec4 O,vec2 I)
{
