#define AA  1   // optional antialiasing (0/1 - off/on)

float PrBoxDf (vec3 p, vec3 b);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrFlatCylDf (vec3 p, float rhi, float rlo, float h);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);

vec3 ltDir, qHit;
float tCur, dstFar;
int idObj;
bool isShad;
const float pi = 3.14159;

float rad, len1, len2, ang1, ang2, angT, angS;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, cRot, wFac;
  dMin = dstFar;
  p.xz -= -2.;
  cRot = 0.25 * pi - 1.5 * angT;
  wFac = isShad ? 10. : 1.;
  for (float n = 0.; n < 4.; n ++) {
    q = p;
    q.xz = Rot2D (q.xz, n * angT + cRot);
    q.y -= n * 2. * 0.02;
    q.z -= 0.5 * rad + 0.5;
    d = PrFlatCylDf (q.zxy, 0.5 * rad + 0.5, 0.2, 0.02);
    DMINQ (1);
    q.y = p.y - 0.1;
    q.z -= 0.5 * rad - 0.5;
    d = PrCapsDf (q.xzy, 0.05, 0.3);
    DMINQ (4);
  }
  for (float n = 0.; n < 3.; n ++) {
    q = p;
    q.xz = Rot2D (q.xz, n * angT + 2. * ang2 + cRot);
    q.y -= 0.17 + 2. * n * 0.01;
    q.z -= 0.5 * rad;
    d = max (PrFlatCylDf (q.zxy, 0.5 * rad, 0.15, 0.01 * wFac),
       0.03 - length (vec2 (q.x, q.z - 0.5 * rad)));
    DMINQ (2);
    q.y -= 0.03;
    q.z -= 0.5 * rad;
    d = max (PrCylDf (q.xzy, 0.05, 0.1), 0.03 - length (q.xz));
    DMINQ (4);
  }
  for (float n = 0.; n < 4.; n ++) {
    q = p;
    q.xz = Rot2D (q.xz, n * angT + cRot);
    q.y -= 0.24 + n * 2. * 0.012;
    q.z -= rad;
    q.xz = Rot2D (q.xz, - angS);
    q.z -= 0.5 * (len2 - len1);
    d = max (PrFlatCylDf (q.zxy, 0.5 * (len1 + len2), 0.1, 0.005 * wFac),
       0.03 - length (vec2 (q.x, abs (q.z) - 0.5 * (len1 + len2))));
    DMINQ (3);
  }
  for (float n = 0.; n < 3.; n ++) {
    q = p;
    q.xz = Rot2D (q.xz, n * angT + 2. * ang2 + cRot);
    q.y -= 0.252 + n * 2. * 0.012;
    q.z -= rad;
    q.xz = Rot2D (q.xz, angS);
    q.z -= 0.5 * (len2 - len1);
    d = max (max (PrFlatCylDf (q.zxy, 0.5 * (len1 + len2), 0.1, 0.005 * wFac),
       0.05 - length (vec2 (q.x, q.z - 0.5 * (len1 - len2)))),
       0.03 - length (vec2 (q.x, abs (q.z) - 0.5 * (len1 + len2))));
    DMINQ (3);
    q.z = abs (q.z) - 0.5 * (len1 + len2);
    d = max (PrCylDf (q.xzy, 0.05, 0.05), 0.02 - length (q.xz));
    DMINQ (4);
  }
  q = p;
  d = PrCapsDf (q.xzy, 0.08, 0.3);
  DMINQ (4);
  q = p;
  q.xz -= 2.5;
  q.y -= -0.3;
  d = PrBoxDf (q, vec3 (4., 0.03, 4.));
  DMINQ (5);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += min (0.05, 3. * h);
    if (sh < 0.001) break;
  }
  return 0.5 + 0.5 * sh;
}

float Trig (float x, float a)
{
  return atan (sin (x) / ((rad / len2) + cos (x))) +
     atan (sin (x) / ((rad / len1) - cos (x))) - 0.5 * a;
}

float SecSol (float a)
{
  float xk, xk1, xk2, fk, fk1, fk2;
  bool isOk;
  xk2 = 0.5 * a;
  xk1 = xk2 + 0.05;
  fk2 = Trig (xk2, a);
  fk1 = Trig (xk1, a);
  isOk = false;
  for (int nIt = 0; nIt < 6; nIt ++) {
    xk = xk1 - (xk1 - xk2) * fk1 / (fk1 - fk2);
    fk = Trig (xk, a);
    xk2 = xk1;
    xk1 = xk;
    fk2 = fk1;
    fk1 = fk;
    if (abs (xk1 - xk2) < 1e-5) {
      isOk = true;
      break;
    }
  }
  return isOk ? xk : 0.5 * a;
}

void SetupMech ()
{
  vec2 ts;
  float t, tp;
  t = mod (0.1 * tCur, 2.);
  if (t < 1.) {
    tp = t + 0.1;
    ts = vec2 (t, ((tp > 1.) ? t : tp));
  } else {
    tp = 2. - (t + 0.1);
    t = 2. - t;
    ts = vec2 (((tp < 0.) ? t : tp), t);
  }
  ts = 0.1 * floor (10. * ts);
  angT = (pi/3.) * (0.18 + 0.44 * mix (ts.x, ts.y, smoothstep (0.3, 0.7,
     (t - 0.1 * floor (10. * t)) / 0.1)));
  rad = 4.;
  len1 = 1.;
  len2 = 2.;
  angS = SecSol (angT);
  ang1 = sin (angS) / ((rad / len2) + cos (angS));
  ang2 = 0.5 * angT - ang1;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj, spec, sh, r;
  isShad = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    spec = 0.1;
    if (idObj == 1) {
      col = mix (vec3 (0.6, 0.3, 0.1), vec3 (0.5, 0.2, 0.),
         smoothstep (0.4, 0.6, Fbm2 (qHit.xz * vec2 (16., 4.))));
    } else if (idObj == 2) {
      col = vec3 (0.9, 0.8, 0.8);
      if (vn.y > 0.5) {
        col *= 1. - 0.1 * Noisefv2 (128. * qHit.xz);
        spec = 0.3;
      } else {
        col *= 0.5;
        spec = 0.;
      }
    } else if (idObj == 3) {
      col = vec3 (1., 1., 0.);
      if (vn.y > 0.5) {
        col *= 1. - 0.1 * Noisefv2 (128. * qHit.xz);
        spec = 0.3;
      } else {
        col *= 0.5;
        spec = 0.;
      }
    } else if (idObj == 4) {
      col = vec3 (0.6, 0.4, 0.1);
    } else if (idObj == 5) {
      col = vec3 (0.8, 0.85, 0.8);
      qHit.xz += 2.5;
      r = length (qHit.xz);
      if (vn.y > 0.99 && r > 1.49) {
        qHit.xz = Rot2D (qHit.xz, 2. * pi * (floor (72. *
           atan (qHit.z, - qHit.x) / (2. * pi) + 0.5) / 72.));
        col = mix (vec3 (0.5, 0.5, 1.), col, SmoothBump (0.03, 0.97, 0.01, mod (2. * r, 1.)) *
           smoothstep (0.01, 0.02, (abs (qHit.z))));
      }
      spec = 0.;
    }
    isShad = true;
    sh = ObjSShadow (ro, ltDir);
    col = col * (0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.)) +
       spec * sh * pow (max (dot (reflect (rd, vn), ltDir), 0.), 32.);
  } else {
    col = vec3 (0.6, 0.6, 1.) * (0.2 + 0.2 * (rd.y + 1.) * (rd.y + 1.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvd, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  SetupMech ();
  az = 0.;
  el = -0.2 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.03 * pi * tCur;
    el -= 0.1 * pi * sin (0.02 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, 0.4 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ltDir = vuMat * normalize (vec3 (1., 3., -1.));
  dstFar = 40.;
  ro = vuMat * vec3 (0., 0., -20.);
#if ! AA
  rd = vuMat * normalize (vec3 (uv, 5.));
  col = ShowScene (ro, rd);
#else
  col = vec3 (0.);
  for (int iy = 0; iy <= 1; iy ++) {
    for (int ix = 0; ix <= 1; ix ++) {
      uvd = (0.5 / canvas.y) * (2. * vec2 (float (ix), float (iy)) - 1.);
      rd = vuMat * normalize (vec3 (uv + uvd, 5.));
      col += ShowScene (ro, rd);
    }
  }
  col *= 0.25;
#endif
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., h * clamp (p.z / h, -1., 1.))) - r;
}

float PrFlatCylDf (vec3 p, float rhi, float rlo, float h)
{
  float d;
  d = length (p.xy - vec2 (rhi * clamp (p.x / rhi, -1., 1.), 0.)) - rlo;
  if (h > 0.) d = max (d, abs (p.z) - h);
  return d;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

vec4 pi = vec4(0,2,4,8)*atan(1.0);

vec2 cdiv (vec2 a,vec2 b){return vec2(dot(a,b),a.y*b.x-a.x*b.y)/dot(b,b);}
vec2 cmul (vec2 a,vec2 b){return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);}
vec2 cmulj(vec2 z){return vec2(-z.y,z.x);}
vec2 cexp (vec2 z){return exp( z.x)*sin(z.y+pi.yx);}
vec2 cexpj(vec2 z){return exp(-z.y)*sin(z.x+pi.yx);}
vec2 cpow (float a,vec2 z){return pow(a, z.x)*sin(z.y+pi.yx);}
vec2 cpowj(float a,vec2 z){return pow(a,-z.y)*sin(z.x+pi.yx);}
vec2 csqr (vec2 z){return vec2(z.x*z.x-z.y*z.y,2.0*z.y*z.x);}
vec2 cinv (vec2 z){return vec2(z.x,-z.y)/dot(z,z);}
vec2 cPow (vec2 z,float n){return pow(length(z),n)*sin(atan(z.y,z.x)*n+pi.yx);}
vec2 cexpc(vec2 a, vec2 b)
{
    return cmul(cpow(dot(a,a),0.5*b),cexpj(atan(a.y,a.x)*b));
    //(a+bi)^(c+di)=(aa+bb)^((c+id)/2)*e^(i(c+id)arg(a+ib))
}

vec3 hsv2rgb(in vec3 c)
{
	return c.z*mix(vec3(1.0),0.5+0.5*sin(c.x+pi.xzw/3.0),c.y);
}

vec3 getcol(float a)
{
	vec3 c = sin(pi.w*vec3(a, a + 0.333, a + 0.666)) * 0.5 + 0.5;
    c += 0.01;
    return c * 0.05;
}

vec4 collatz(vec4 z, vec2 shift)
{
    vec2 k = cexpj(pi.z*z.xy);
    vec2 zd = z.xy*1.25+vec2(0.5,0.0);
    return vec4(
          z.xy*1.75+vec2(0.5,0.0) - cmul(k,zd) + shift,
         cmul(z.zw,vec2(1.75,0.0) - cmul(k,zd*pi.z-vec2(0.0,1.25)))
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 7.0*(fragCoord-iResolution.xy*0.5)/iResolution.x;
    vec4 z = vec4(p,1.0,0.0);
    vec2 shift = 1.0 - 2.0 * iMouse.xy / iResolution.xy;
    if (iMouse.xy == iMouse.zw) shift += sin(iTime + vec2(0.0,pi*0.5));
    for (int i = 0; i < 18; i++)
    {
        z = collatz(z, shift);
    }
    float hue = atan(z.z,z.w);
    float val = 1.0/(1.0+0.0000000000001*dot(z.xy,z.xy));
    float sat = 1.0-1.0/(1.0+0.000000001*dot(z.zw,z.zw));
	fragColor = vec4(hsv2rgb(vec3(hue+iTime,sat,val)),1.0);
    //fragColor = vec4(pow(getcol(hue),vec3(val)),1.0);
}

#define EPS 0.0001
#define FAR 1000.0
#define STEPS 1028
#define PI acos( -1.0 )
#define TWOPI PI * 2.0

// Some little hacks but basiclly based on http://iquilezles.org/www/articles/menger/menger.htm


float hash( float n )
{

    return fract( sin( n ) * 45843.349 );

}

float noise( in vec3 x )
{

    vec3 p = floor( x );
    vec3 k = fract( x );

    k *= k * k * ( 3.0 - 2.0 * k );

    float n = p.x + p.y * 57.0 + p.z * 113.0; 

    float a = hash( n );
    float b = hash( n + 1.0 );
    float c = hash( n + 57.0 );
    float d = hash( n + 58.0 );

    float e = hash( n + 113.0 );
    float f = hash( n + 114.0 );
    float g = hash( n + 170.0 );
    float h = hash( n + 171.0 );

    float res = mix( mix( mix ( a, b, k.x ), mix( c, d, k.x ), k.y ),
                     mix( mix ( e, f, k.x ), mix( g, h, k.x ), k.y ),
                     k.z
    				 );

    return res;

}

float fbm( in vec3 p )
{

    float f = 0.0;
    f += 0.5000 * noise( p ); p *= 2.02;
    f += 0.2500 * noise( p ); p *= 2.03;
    f += 0.1250 * noise( p ); p *= 2.01;
    f += 0.0625 * noise( p );
    f += 0.0125 * noise( p );
    return f / 0.9375;

}


float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  float t = min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
  float sph = length( p ) - 1.0;
  p.y -= 0.8;
  float tim = sin( ( iTime * 0.2 ) + sin( iTime * 0.2 ) * 0.5 );
  float f = mix( t, sph, max( 0.2, min( 0.5, tim ) ) );
  return f;
}

float sdCross( in vec3 p )
{
  float da = sdBox(p.xyz,vec3((iTime * 0.2 ),1.0,1.0));
  float db = sdBox(p.yzx,vec3(1.0,(iTime * 0.2 ),1.0));
  float dc = sdBox(p.zxy,vec3(1.0,1.0,(iTime * 0.2 )));
  return min(da,min(db,dc));
}

vec3 map( in vec3 p )
{
   vec2 d = vec2( sdBox(p,vec3(1.0)), 0.0 );
   vec2 pla = vec2( p.y + 1.0, 1.0 );

   float s = 1.0;
   for( int m=0; m<5; m++ )
   {
       vec3 a = mod( p*s, 2.0 ) - 1.0;
       s *= 3.0;

       vec3 r = max( 1.0, sin( iTime ) ) - 3.0*abs(a);

       vec2 c = vec2( sdCross(r)/s, 0.0 );
       d = max( d, c );

   }

   if( d.x < pla.x ) pla = d;

   return vec3( pla, 0.0 );
}

