vec3 grad( vec3 p )
{

    vec2 e = vec2( 0.0, EPS );
    vec3 n = vec3( map( p + e.xyy ).x - map( p -  e.xyy ).x,
                   map( p + e.yxy ).x - map( p -  e.yxy ).x,
                   map( p + e.yyx ).x - map( p -  e.yyx ).x
                 );
    return n;


}

float softShadows( in vec3 ro, in vec3 rd )
{

    float res = 1.0;
    for( float t = 0.1; t < 8.0; ++t )
    {

        float h = map( ro + rd * t ).x;
        if( h < EPS ) return 0.0;
        res = min( res, 8.0 * h / t );
        t += h;


    }

    return res;

}

// https://www.shadertoy.com/view/4lSSRW

float calcOcc( in vec3 pos, in vec3 nor )
{
    const float h = 0.2;
	float ao = 0.0;
    for( int i=0; i<8; i++ )
    {
        vec3 dir = sin( float(i)*vec3(1.0,7.13,13.71)+vec3(0.0,2.0,4.0) );
        //dir = normalize(nor + dir);
        dir *= sign(dot(dir,nor));
        float d = map( pos + h*dir ).x;
        ao += max(0.0,h-d*2.0);
    }
    return clamp( 4.0 - 2.5*ao, 0.0, 1.0 )*(0.5+0.5*nor.y);
}

vec3 shade( vec3 ro , vec3 rd, float t )
{

    vec3 p = ro + rd * t;
    vec3 n = normalize( grad( p ) );
    vec3 lig = normalize( vec3( 1.0, 4.0, 0.6 ) );
    vec3 col;
    vec3 ref = reflect( ro, n );

    float amb = 0.5 + 0.5 * n.y;
    float dif = max( 0.0, dot( n, lig ) );
    float sha = softShadows( p, lig );
    float spe = pow( clamp( dot( lig, ref ), 0.0, 1.0 ), 16.0 );
    float rim = pow( 1.0 + dot(n, rd), 2.0 );
    float occ = calcOcc( p, n ); occ *= occ * occ;

    col += vec3( 1.0, 0.98, 0.8 ) * amb;
    col += dif * vec3( 1.0, 0.98, 0.8 ) * sha;
    col += 1.0 * spe;
    col += 0.1 * rim;
    col += 1.0 * occ;

    map( p ).y == 0.0 ? col *= mix( vec3( 0.0, 1.0, 0.0 ), vec3( 0.0, 0.0, 1.0 ), fbm( p + fbm( p + fbm ( p ) ) ) ) : vec3( 0.0 );
    map( p ).y == 1.0 ? col *= vec3( 1.0 ) : vec3( 0.0 );


    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( -iResolution.xy + 2.0 * fragCoord ) / iResolution.y;

    vec2 mou = iMouse.xy / iResolution.xy;

    vec3 ro = 2.5 * vec3( sin( sin( iTime * 0.2 ) ), 0.0, cos( iTime * 0.2 ) );

    if( mou.x == 0.0 ) ro = 2.5 * vec3( sin( sin( iTime * 0.2 ) ), 0.0, cos( iTime * 0.2 ) );

    if( mou.x != 0.0 ) ro = mou.y * vec3( sin( mou.x * TWOPI ), 0.0, cos( -mou.x * TWOPI ) );

    //vec3 rd = normalize( vec3( uv, -1.0 ) );
    //vec3 ro = vec3( 2.0, 0.0, 2.0 );
    vec3 ww = normalize( vec3( 0.0 ) - ro );
    vec3 uu = normalize( cross( vec3( 0.0, 1.0, 0.0 ), ww ) );
    vec3 vv = normalize( cross( ww, uu ) );
    vec3 rd = normalize( uv.x * uu + uv.y * vv + 1.5 * ww );


    float t = 0.0, d = EPS;
    for( int i = 0; i < STEPS; ++i )
    {

        d = map( ro + rd * t ).x;
        if( d < EPS || t > FAR ) break;
        t += d;

    }

    // Time varying pixel color
    vec3 col = d < EPS ? shade( ro, rd, t ) : mix( vec3( 0.5 ), vec3( 0.0 ), uv.y  );

    // Output to screen
    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Created 2018 by Matthew Arcus
//
// MÃ¶bius transformations
//
// Construct a MÃ¶bius transformation from reflection in a line and
// inversion in a circle.
//
// Domain coloring from https://www.shadertoy.com/view/MtjBzV by phi16
//
// Controls:
//
// Mouse x: select circle centre on x-axis
// Mouse y: select angle of inclination of the mirror line
// c: show construction line, circle and fixed points
// i: toggle inversion
// m: toggle reflection in mirror line
// r: toggle time rotation
// a: toggle alternative coloring
// b: map fixed points to 0 and infinity
//
// Algebraically, a MÃ¶bius transformation is of the form z -> (az+b)/(cz+d)
// but can also be constructed more geometrically as the composition of a
// number of spherical inversions, including inversion in a sphere of
// "infinite" radius, ie. reflection in a straight line - this
// definition also generalizes well to higher dimensions.
//
// Here we construct a MÃ¶bius transformation from a reflection in a line
// through the origin, and an inversion in a unit circle with its
// centre on the x-axis. This seems sufficient to construct any MÃ¶bius
// transformation that does not fix the point at infinity, up to
// scaling, translation and rotation of the final image.
//
// Every MÃ¶bius transformation has two, possibly coincident, fixed
// points. We show them here - if the circle and mirror line 
// intersect, then the fixed points are their points of intersection
// (or if line is tangent to the circle, the single point of
// tangency). If they do not intersect, the fixed points are on the
// line through the circle centre perpendicular to the mirror.
//
////////////////////////////////////////////////////////////////////////////////

const float scale = 2.2;
const float grid = 4.0;
const float clip = 2.0;
const float width0 = 0.02, width1 = 0.015;

const float PI	= 3.14159265359;
const float TWOPI = 2.0*PI;

vec2 rotate(vec2 p, float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec2 cmul(vec2 z, vec2 w) {
  return vec2(z.x*w.x-z.y*w.y,z.x*w.y+z.y*w.x);
}

vec2 cconj(vec2 z) {
  return vec2(z.x,-z.y);
}

vec2 cdiv(vec2 z, vec2 w) {
  return cmul(z,cconj(w))/dot(w,w);
}

// Invert p in circle, centre c, radius squared r2.
vec2 invert(vec2 p, vec2 c, float r2) {
    p -= c;
    p *= r2/dot(p,p);
    p += c;
    return p;
}

