#endif
}

const int CHAR_A = 65;
const int CHAR_B = 66;
const int CHAR_C = 67;
const int CHAR_I = 73;
const int CHAR_M = 77;
const int CHAR_R = 82;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool showconstruction = !keypress(CHAR_C);
    bool doinvert = !keypress(CHAR_I);
    bool doreflect = !keypress(CHAR_M);
    bool dorotate = !keypress(CHAR_R);

    vec2 z = (2.0*fragCoord-iResolution.xy) / iResolution.y;
    vec2 mouse = vec2(0);
    if (iMouse.x > 0.0) {
    	mouse = (2.0*iMouse.xy-iResolution.xy) / iResolution.y;
    } else {
      mouse = vec2(cos(0.2*iTime),0.1*iTime);
    }
    z *= scale;

    vec2 z0 = z; // Remember untransformed point

    float r = 0.5*PI*mouse.y; // y-coord of mouse sets mirror angle

    vec2 c = vec2(scale*mouse.x,0); // x-coord of mouse sets circle centre
    vec2 m = vec2(cos(r),sin(r));   // Normal to rotating line

    // Compute the fixed points, this involves solving a
    // quadratic, the fixed points are the (real or imaginary)
    // solutions. For a parabolic transformation (circle and line
    // are tangent), there is just one solution.

    // First, find the distance from the circle centre to the line
    float s = dot(c,m);
    // Now solve for circle centre 0, with mirror at x = -s
    // Fixed point equation is z^2 - 2sz + 1 = 0
    float d = s*s - 1.0;
    float k = sqrt(abs(d));
    vec2 w0,w1;
    if (d >= 0.0) {
      // Real solutions on y = 0
      w0 = vec2(-s+k,0);
      w1 = vec2(-s-k,0);
    } else {
      // Imaginary solutions on mirror line, x = -s
      w0 = vec2(-s,k);
      w1 = vec2(-s,-k);
    }
    // Move w0,w1 to correct frame of reference
    w0 = rotate(w0,r)+c;
    w1 = rotate(w1,r)+c;

    vec3 col;
  	if (doinvert) z = invert(z,c,1.0);
    if (doreflect) z = reflect(z,m);
	if (keypress(CHAR_B)) z = cdiv(z-w0,z-w1);
    if (!keypress(CHAR_A)) {
        float t = 0.2*iTime;      // rotation, mainly for show
    	if (dorotate) z = rotate(z,t);
    	// Domain coloring is from https://www.shadertoy.com/view/MtjBzV
    	float arg = atan(z.y,z.x);
    	float len = length(z);
    	vec3 hue = cos(vec3(0,1,-1)*2.0/3.0*PI + arg) * 0.5 + 0.5;
    	float lum = 1.0;
    	// Only show grid for -2 <= x,y <= 2
    	if (abs(z.x) <= clip && abs(z.y) <= clip) {
      		lum *= pow(-cos(z.x * grid * TWOPI) * 0.5 + 0.502, 0.06);
      		lum *= pow(-cos(z.y * grid * TWOPI) * 0.5 + 0.502, 0.06);
    	}
        col = lum*hue;
    } else {

        float t = 0.0;
        if (dorotate) t = 8.0*iTime;
    	float arg = atan(z.y,z.x);
	    float len = length(z);
    	vec3 hue = cos(vec3(0,1,-1)*2.0/3.0*PI+arg+t/16.0) * 0.5 + 0.5;
    	float lum = 1.0;
        float spiral = 10.0;
#if 1
    	lum *= pow(-cos(spiral*(log(len) - arg)) * 0.5, 0.1);
    	lum *= pow(-cos(spiral*(log(len) + arg - 0.5*iTime)) * 0.5, 0.1);
#else
        // log here make coloring invert
    	lum *= pow(-cos(log(len)*16.0 + t) * 0.5 + 0.502, 0.06);
    	lum *= pow(-cos(arg*16.0 + t) * 0.5 + 0.502, 0.06);
#endif
        col = lum*hue;
    }
    if (showconstruction) {
      // Now draw circle, mirror line and fixed points.
      float t = abs(dot(z0,m));
      t = min(t,abs(length(z0-c)-1.0));
      t = min(t,max(abs(length(z0-w0)),width0)-width0);
      t = min(t,max(abs(length(z0-w1)),width0)-width0);
      // Uncomment next 2 lines to show images of 0 and 1.
      //t = min(t,max(abs(length(z)),w0)-w0);
      //t = min(t,max(abs(length(z-vec2(1,0))),w0)-w0);
      float eps = width1; // 2.0*scale/iResolution.y;
      col = mix(0.6+0.4*col,col, smoothstep(width1-eps,width1+eps,t));
    }
    fragColor = vec4(col,1.0);
}

