float Bub(vec2 uv,float scale)
{
    float time = iTime*0.75;
    uv.x -= exp(0.5+(uv.y*1.5));
    uv.y-=time*2./scale;
    uv.x+=sin(uv.y+time*.95)*0.025;
	uv*=scale;
    vec2 s=floor(uv);
    vec2 f=fract(uv);
    float k=3.0;
	vec2 p =.5+.35*sin(11.*fract(sin((s+scale)*mat2(7.0,3.0,6.0,5.0))*5.))-f;
    float d=length(p);
    k=min(d,k);
	k=smoothstep(0.0,k,sin(f.x+f.y)*0.01);
   	return k;
}

vec3 Bubbles(vec2 uv,float head,float d)
{
	float c = Bub(uv,30.)*.3;
	c+=Bub(uv,20.)*.5;
	c+=Bub(uv,15.)*.8;
	c+=Bub(uv,10.);
	c+=Bub(uv,8.);
	c+=Bub(uv,6.);
	c+=Bub(uv,5.);
    vec3 background2 = vec3(1.0-d*d,0.3,0.35);
    float bub_brightness = 2.5+head+d;
    vec3 scol = background2 * bub_brightness;
    scol = mix(background2,scol,c);
    return scol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy - iResolution.xy)/ iResolution.y;

    float d = length(uv);
    float head = 1.0-length(uv);
    d+=(0.5+sin(iTime*2.0)*0.5)*0.25;
    uv *= 0.25;
    vec3 col = Bubbles(uv*(d+0.5), head,d);
    fragColor =vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Time interval
    float ti = 2.0;

    // Time normalize (from 0 to 1)
    float t = fract(iTime/ti);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Circle radius max
    float xMax = length(iResolution.xy) / iResolution.x;
    float yMax = length(iResolution.xy) / iResolution.y;

    // Move to center
    uv = uv - vec2(0.5, 0.5);

    // Scale uv
    uv = uv / vec2(xMax, yMax);

    // Circle radius current
    float r = clamp(t, 0.0, 1.0);

    // Color set
    float d = length(uv);
    vec3 col = vec3(1.0 * (r - d) / r, 0.2, .0) * 0.9;
    if (d > r)
        col = vec3(.0, .0, .0);

    // Output to screen
    fragColor.rgb = col;
    fragColor.a = 1.;
}

