// transition function
float	trans(float start, float time, float speed)
{
	return 1. - clamp(exp(-time*speed+start*speed), .0, 1. );
}

void mainImage(out vec4 c_out, in vec2 f)
{
    #ifdef NO_SCREEN
    screen_dist=2.;
    #else
    screen_dist = 1.+-2.*trans(.1, mod(iTime*.5, 12.), .51)+.0*.125*sin(iTime*2.)+ -55.*trans(10., mod(iTime*.5, 12.), .25);//(1. -clamp(exp(-iTime*.125+5.), .0, 1.) ) ;
    #endif
    vec3	col = vec3(0., 0., 0.);
	vec2 R = iResolution.xy,
          uv  = vec2(f-R/2.) / R.y;
	vec3	dir = camera(uv);
    vec3	pos = vec3(.0, .0, 0.0);

    vec2	inter = (march(pos, dir));
    if (inter.y >= FAR)
        ret_col = vec3(.90, .82, .70);
    else
        ret_col = vec3(.0,.0,.0);
    col.xyz = ret_col*(1.-inter.x*.005);
    col.xyz = max(col.xyz, h);
//    col = col - (col*col*col*(2.-3.*col)); // contrast

    c_out =  vec4(col*1.,1.0);
}

float	scene(vec3 p)
{  
    float	var;
    float	mind = 1e5;
    float displz = iTime*1.;
    #ifdef OUTSIDE
    p.y -= -5.25;
    p.x -= -7.;
    #endif
    //p.xy += vec2(cos(iTime), sin(iTime) )*.25;
    p.z -= displz;
    vec3 op = p;
    rotate(p.xy, .5*( sin(p.z*.125-iTime*.5)*3.06125+(p.z*.75)+iTime*.5) );
    //p.xy += vec2(cos(iTime*.5), sin(iTime*.5) )*1.1005;
    //p.xy += vec2(cos(p.z*.125)*2.3, sin(p.z*.125)*2.3);
    vec3 pp = p;
    pp = fract(pp)-.5;

    mind = min(mylength(pp.yz), min(mylength(pp.xy), mylength(pp.xz) ))-.051;

    mind = max(mind
               ,
               (mylength(p.xy)-1.570)
              );
    pp = p;

    mind = min(mind
               ,
               mylength(vec2(abs(pp.x)-.5, pp.y))-.051 // rails
              );
    float cadres = max(abs(abs(pp.x)-.2)-.2, max(abs(pp.y)-.2, abs(pp.z+-screen_dist+displz)-.01) );
    vec2 texcd = 2.*vec2(abs(pp.x) , (pp.y)-.61 )*1.22;
    if (cadres < E*1.)
    {
        vec3 texcol = texture(iChannel0, texcd).xyz;
        float key;

        #ifdef GLITCH
        // little glitch effect
        vec2 texcdg = texcd;
        vec2 texcdb = texcd;
        texcdg.x += sin(floor(iTime*300.)/10.+floor(texcd.y*50.)*.1 )*.05*floor(sin( floor(texcdg.y*500.)+iTime*100. ) );
        texcdb.x += sin(floor(iTime*300.)/10.+floor(texcd.y*50.)*.1 )*.05*floor(sin( floor(texcdb.y*500.)+iTime*100. +1.57) );
        vec3 tcg = texture(iChannel0, texcdg).xyz;
        vec3 tcb = texture(iChannel0, texcdb).xyz;
        texcol = (1.*texcol+tcg+tcb)*.33333;
        //texcol = vec3(texcol.x, tcg.y, tcb.z); // too ugly
        // end glitch
        #endif
        // keying
        key = ( (texcol.y) < length(texcol.xz) ) ? 1. : .0;
        texcol *= key;
        // end keying

        #ifdef SCANLINES
        // scanlines;
        texcol += .251*vec3(.1,.5,.1)*ceil(sin(texcd.y*150.)) * (1.-length(texcd.xy+vec2(-.3, .5) ));
        // end scanlines
        #endif

        #ifdef PIXELS
        // pixels
        texcol = texcol * abs(1.*cos(texcd.x*150.)*sin(texcd.y*150.) );
        texcol += vec3(.25,.91,.1)*abs(.125-abs(.125*cos(texcd.x*150.)*sin(texcd.y*150.) ));
        // end pixels
        #endif
    	h = (texcol*1.)*1./max(cadres*cadres , 1.);
    }
    mind = min(mind
               ,
               cadres
              );
    mind = min(mind
               ,
               mylength(vec2(mylength(vec2(abs(pp.x)-.182, abs(pp.y)-.02) )-.2, pp.z+-screen_dist+displz ))-.0251
               );
    mind = min(mind
               ,
               max(-(mylength(vec2(abs(pp.x)-.45,pp.y) )-.025), mylength(vec3(abs(pp.x)-.45,pp.y, pp.z+.75*.0-screen_dist+displz) )-.05)
               );
    h += vec3(.75, .45, .1)*1./max(mind*mind*30000.+10., 10.001);

    float ecrous = 1e5;
    pp = p;
    pp.x = abs(pp.x)-.5;
    pp.z = mod(pp.z, .0625)-.0625*.5;

    ecrous = mylength(vec2(length(pp.xz)-.051, pp.y ))-.01251;

    mind = min(mind
               ,
               ecrous
               );

    float bgd = 1e5;

    bgd = length(p.xy)-3.1;
    bgd = max(bgd, -(length(p.xy)-3.010) );
    h += vec3(.2, .27, .74)*1./max(bgd*bgd*30000.+60., .001);
    bgd = max(abs(bgd), .002);

    float mine = 1e5;
    pp = op;
    rotate(pp.xy, ( (p.z+.5)*-.030 ) +iTime*-.40);
    op = pp;
    pp = fract(pp-.5)-.5;
    rotate(pp.yx, floor( (p.z+.5)*-20. )/20. +iTime*-1.0);
    mine = mylength(pp.xyz*.15)-0.01468*3.5;
    mine = max(mine, -(mylength(op.xy)-4.5) );
    mine = max(mine, mylength(op.xy)-5.4999);
    h += 1.*vec3(.1, .7, .10)*1./max(mine*mine*100000000.+250., .001);

    mind = min(mind, mine);
    mind = min(mind, bgd);
    return (mind);
}

vec2	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec2	s = vec2(0.0, 0.0);

	    for (float i = -1.; i < I_MAX; ++i)
	    {
            vec3 dirr;
            dirr = dir;
            rotate(dirr.xz, dist.y*.02510+iTime*.0+1.0*sin(iTime*1.)*.2528);
            //rotate(dirr.zy, -dist.y*.0510+iTime*.0+1.0*sin(iTime*1.)*.125);
	    	p = pos + dirr * dist.y;
	        dist.x = scene(p);
	        dist.y += dist.x;
	        if (dist.x < E || dist.y > FAR)
            {
                break;
            }
	        s.x++;
    }
    s.y = dist.y;
    return (s);
}

// Utilities

float	mylength(vec2 p)
{
	float	ret;

    ret = max( abs(p.x), abs(p.y) );

    return ret;
}

float	mylength(vec3 p)
{
	float	ret;

    ret = max(abs(p.x), max(abs(p.y), abs(p.z)));
    return ret;
}

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

vec3	camera(vec2 uv)
{
    float		fov = 1.-.25*abs(sin(iTime*1.125));
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

vec3 hsluv_intersectLineLine(vec3 line1x, vec3 line1y, vec3 line2x, vec3 line2y) {
    return (line1y - line2y) / (line2x - line1x);
}

vec3 hsluv_distanceFromPole(vec3 pointx,vec3 pointy) {
    return sqrt(pointx*pointx + pointy*pointy);
}

vec3 hsluv_lengthOfRayUntilIntersect(float theta, vec3 x, vec3 y) {
    vec3 len = y / (sin(theta) - x * cos(theta));
    if (len.r < 0.0) {len.r=1000.0;}
    if (len.g < 0.0) {len.g=1000.0;}
    if (len.b < 0.0) {len.b=1000.0;}
    return len;
}

float hsluv_maxSafeChromaForL(float L){
    mat3 m2 = mat3(
         3.2409699419045214  ,-0.96924363628087983 , 0.055630079696993609,
        -1.5373831775700935  , 1.8759675015077207  ,-0.20397695888897657 ,
        -0.49861076029300328 , 0.041555057407175613, 1.0569715142428786  
    );
    float sub0 = L + 16.0;
    float sub1 = sub0 * sub0 * sub0 * .000000641;
    float sub2 = sub1 > 0.0088564516790356308 ? sub1 : L / 903.2962962962963;

    vec3 top1   = (284517.0 * m2[0] - 94839.0  * m2[2]) * sub2;
    vec3 bottom = (632260.0 * m2[2] - 126452.0 * m2[1]) * sub2;
    vec3 top2   = (838422.0 * m2[2] + 769860.0 * m2[1] + 731718.0 * m2[0]) * L * sub2;

    vec3 bounds0x = top1 / bottom;
    vec3 bounds0y = top2 / bottom;

    vec3 bounds1x =              top1 / (bottom+126452.0);
    vec3 bounds1y = (top2-769860.0*L) / (bottom+126452.0);

    vec3 xs0 = hsluv_intersectLineLine(bounds0x, bounds0y, -1.0/bounds0x, vec3(0.0) );
    vec3 xs1 = hsluv_intersectLineLine(bounds1x, bounds1y, -1.0/bounds1x, vec3(0.0) );

    vec3 lengths0 = hsluv_distanceFromPole( xs0, bounds0y + xs0 * bounds0x );
    vec3 lengths1 = hsluv_distanceFromPole( xs1, bounds1y + xs1 * bounds1x );

    return  min(lengths0.r,
            min(lengths1.r,
            min(lengths0.g,
            min(lengths1.g,
            min(lengths0.b,
                lengths1.b)))));
}

float hsluv_maxChromaForLH(float L, float H) {

    float hrad = radians(H);

    mat3 m2 = mat3(
         3.2409699419045214  ,-0.96924363628087983 , 0.055630079696993609,
        -1.5373831775700935  , 1.8759675015077207  ,-0.20397695888897657 ,
        -0.49861076029300328 , 0.041555057407175613, 1.0569715142428786  
    );
    float sub1 = pow(L + 16.0, 3.0) / 1560896.0;
    float sub2 = sub1 > 0.0088564516790356308 ? sub1 : L / 903.2962962962963;

    vec3 top1   = (284517.0 * m2[0] - 94839.0  * m2[2]) * sub2;
    vec3 bottom = (632260.0 * m2[2] - 126452.0 * m2[1]) * sub2;
    vec3 top2   = (838422.0 * m2[2] + 769860.0 * m2[1] + 731718.0 * m2[0]) * L * sub2;

    vec3 bound0x = top1 / bottom;
    vec3 bound0y = top2 / bottom;

    vec3 bound1x =              top1 / (bottom+126452.0);
    vec3 bound1y = (top2-769860.0*L) / (bottom+126452.0);

    vec3 lengths0 = hsluv_lengthOfRayUntilIntersect(hrad, bound0x, bound0y );
    vec3 lengths1 = hsluv_lengthOfRayUntilIntersect(hrad, bound1x, bound1y );

    return  min(lengths0.r,
            min(lengths1.r,
            min(lengths0.g,
            min(lengths1.g,
            min(lengths0.b,
                lengths1.b)))));
}

float hsluv_fromLinear(float c) {
    return c <= 0.0031308 ? 12.92 * c : 1.055 * pow(c, 1.0 / 2.4) - 0.055;
}
vec3 hsluv_fromLinear(vec3 c) {
    return vec3( hsluv_fromLinear(c.r), hsluv_fromLinear(c.g), hsluv_fromLinear(c.b) );
}

float hsluv_toLinear(float c) {
    return c > 0.04045 ? pow((c + 0.055) / (1.0 + 0.055), 2.4) : c / 12.92;
}

vec3 hsluv_toLinear(vec3 c) {
    return vec3( hsluv_toLinear(c.r), hsluv_toLinear(c.g), hsluv_toLinear(c.b) );
}

float hsluv_yToL(float Y){
    return Y <= 0.0088564516790356308 ? Y * 903.2962962962963 : 116.0 * pow(Y, 1.0 / 3.0) - 16.0;
}

float hsluv_lToY(float L) {
    return L <= 8.0 ? L / 903.2962962962963 : pow((L + 16.0) / 116.0, 3.0);
}

vec3 xyzToRgb(vec3 tuple) {
    const mat3 m = mat3( 
        3.2409699419045214  ,-1.5373831775700935 ,-0.49861076029300328 ,
       -0.96924363628087983 , 1.8759675015077207 , 0.041555057407175613,
        0.055630079696993609,-0.20397695888897657, 1.0569715142428786  );

    return hsluv_fromLinear(tuple*m);
}

vec3 rgbToXyz(vec3 tuple) {
    const mat3 m = mat3(
        0.41239079926595948 , 0.35758433938387796, 0.18048078840183429 ,
        0.21263900587151036 , 0.71516867876775593, 0.072192315360733715,
        0.019330818715591851, 0.11919477979462599, 0.95053215224966058 
    );
    return hsluv_toLinear(tuple) * m;
}

vec3 xyzToLuv(vec3 tuple){
    float X = tuple.x;
    float Y = tuple.y;
    float Z = tuple.z;

    float L = hsluv_yToL(Y);

    float div = 1./dot(tuple,vec3(1,15,3)); 

    return vec3(
        1.,
        (52. * (X*div) - 2.57179),
        (117.* (Y*div) - 6.08816)
    ) * L;
}


vec3 luvToXyz(vec3 tuple) {
    float L = tuple.x;

    float U = tuple.y / (13.0 * L) + 0.19783000664283681;
    float V = tuple.z / (13.0 * L) + 0.468319994938791;

    float Y = hsluv_lToY(L);
    float X = 2.25 * U * Y / V;
    float Z = (3./V - 5.)*Y - (X/3.);

    return vec3(X, Y, Z);
}

vec3 luvToLch(vec3 tuple) {
    float L = tuple.x;
    float U = tuple.y;
    float V = tuple.z;

    float C = length(tuple.yz);
    float H = degrees(atan(V,U));
    if (H < 0.0) {
        H = 360.0 + H;
    }

    return vec3(L, C, H);
}

vec3 lchToLuv(vec3 tuple) {
    float hrad = radians(tuple.b);
    return vec3(
        tuple.r,
        cos(hrad) * tuple.g,
        sin(hrad) * tuple.g
    );
}

vec3 hsluvToLch(vec3 tuple) {
    tuple.g *= hsluv_maxChromaForLH(tuple.b, tuple.r) * .01;
    return tuple.bgr;
}

vec3 lchToHsluv(vec3 tuple) {
    tuple.g /= hsluv_maxChromaForLH(tuple.r, tuple.b) * .01;
    return tuple.bgr;
}

vec3 hpluvToLch(vec3 tuple) {
