vec2 cash2(int x, int y, int seed) {

    int intensity = cash(x, y, seed);
    float i1 = float(intensity&0xffff);
    float i2 = float(intensity>>16 & 0xffff);
    return vec2(i1,i2)/float(0xffff);
}
vec4 cash4(int x, int y, int seed) {
    vec2 xy = cash2(x, y, seed);
    vec2 zw = cash2(x+30185, y-18513, seed*4815);
    return vec4(xy,zw);
}
float smoothstp(float x) { 
  // Evaluate polynomial
  return x * x * (3. - 2. * x);
    //
}
vec4 mix_custom(vec4 edge0, vec4 edge1, float x) {
    return mix(edge0, edge1, smoothstp(x));
}
vec2 mix_custom(vec2 edge0, vec2 edge1, float x) {
    return mix(edge0, edge1, smoothstp(x));
}
float mix_custom(float edge0, float edge1, float x) {
    return mix(edge0, edge1, smoothstp(x));
}
vec4 cash4(int x, int y, float seed) {
    vec4 a = cash4(x, y, int(floor(seed)));
    vec4 b = cash4(x, y, int(ceil(seed)));
    vec4 res = mix(a, b, mix(fract(seed), smoothstp(fract(seed)), 0.5));

    vec4 hsluv_a = rgbToHsluv(res);
    vec4 hsluv_b = rgbToHsluv(res);
    vec4 hsluv_res = rgbToHsluv(res);
    hsluv_res.y = mix(hsluv_a.y, hsluv_b.y, fract(seed))/2.0 + 50.0;
    hsluv_res.z = mix(hsluv_a.z, hsluv_b.z, fract(seed))/2.0 + 25.0;
    return vec4(hsluv_toLinear(hsluvToRgb(hsluv_res).xyz), res.w);
}
