vec2 grid(vec2 letterspace) 
{
	return ( vec2( (letterspace.x / 2.) * .65 , 1.0-((letterspace.y / 2.) * .95) ));
}

float when_lt(float x, float y) 
{
	return max(sign(y - x), 0.0);
}


