// UV warping 
vec2 kaleido(vec2 uv)
{
	float th = atan(uv.y, uv.x);
	float r = pow(length(uv), .9);
	float f = 3.14159 / 3.5;

	th = abs(mod(th + f/4.0, f) - f/2.0) / (1.0 + r);
	float th2 = sin(th * 6.283 / f);

    th += th2;

	return vec2(cos(th), sin(th)) * r * .1;
}

// for kaleidoscope
vec2 transform(vec2 at)
{
	vec2 v;
	float th = .02 * iTime;
	v.x = at.x * cos(th) - at.y * sin(th) - .2 * sin(th);
	v.y = at.x * sin(th) + at.y * cos(th) + .2 * cos(th);
	return v;
}

// Lens distortion around edges
vec2 lens_distortion( vec2 uv, float k, float kcube )
{
    vec2 t = uv;
    float r2 = t.x * t.x + t.y * t.y;
	float f = 0.;

    if( kcube == 0.0){
        f = 1. + r2 * k;
    }else{
        f = 1. + r2 * ( k + kcube * sqrt( r2 ) );
    }

    vec2 nUv = f * t;
    nUv.y = nUv.y;

    return nUv;   
}

float getText( vec2 coord )
{
    float tex = .0;

    if( iTime < 3. ) 
    {
        t_time = iTime;
        newline(); 
        tex += H(r(coord)); add(); 
        tex += I(r(coord)); space();
        tex += T(r(coord)); add();
        tex += H(r(coord)); add();
        tex += E(r(coord)); add();
        tex += R(r(coord)); add();
        tex += E(r(coord)); add();
        newline();
    }

    if( iTime > 3. && iTime < 8. ) 
    {
        t_time = iTime - 3.;
        newline(); 
        tex += E(r(coord)); add(); 
        tex += N(r(coord)); add();
        tex += T(r(coord)); add();
        tex += E(r(coord)); add();
        tex += R(r(coord)); space();
        tex += M(r(coord)); add();
        tex += Y(r(coord)); add();
        newline();
        tex += L(r(coord)); add();
        tex += A(r(coord)); add();
        tex += I(r(coord)); add();
        tex += R(r(coord)); add();
        tex += STOP(r(coord)); add();
        tex += STOP(r(coord)); add();
        tex += STOP(r(coord)); add();
    }

    // Logo
    if( iTime > 8. )
    {
        t_time = iTime - 8.;
        newline();
        tex += S1(r(coord)); add(); 
        tex += S2(r(coord)); add(); 
        tex += S3(r(coord)); add(); 
        tex += S4(r(coord)); add();
        newline(); 
        tex += UL(r(coord)); add(); 
        tex += UM(r(coord)); add(); 
        tex += UM(r(coord)); add(); 
        tex += UR(r(coord)); add();
    }
    return tex;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /**
     * basic setup
     */
	vec2 uv = fragCoord.xy / iResolution.xy;	
	uv.x = mix(-1.0, 1.0, uv.x);
	uv.y = mix(-1.0, 1.0, uv.y);
	uv.y *= iResolution.y / iResolution.x;

    float distSpeed = .05;
    float k = 1.0 * sin( iTime * distSpeed * .9 );
    float kcube = .5 * sin( iTime * distSpeed );
    float off = .1 * sin( iTime * distSpeed * .5 );

    /**
     * kaleidoscope
     */
    //vec2 lensedUV = lens_distortion( uv, k + off, kcube );
    //vec2 kaleidUV = transform(kaleido(lensedUV)) * 4.0;
    vec2 kaleidUV = transform(kaleido(uv)) * 4.0;
    vec2 lensedUV = lens_distortion( kaleidUV, k + off, kcube );
    vec4 kaleTex = texture(iChannel0, lensedUV); // < update here uv

    /**
     * dithering
     */
    vec3 rgb = kaleTex.rgb;
    vec3 oldcolor = rgb + (rgb * texture(iChannel1, (mod(fragCoord, 8.0) / 8.0)).rgb);
    vec3 dithered = floor(oldcolor);
    dithered.g = .0;
    dithered.b = .261;

    /**
     * render text
     */
	float d = 0.;
	caret = caret_origin;

	// Build up the text
	d = getText(fragCoord);

    // Vertical pixel lines
    d = clamp(d* (.75+sin(fragCoord.x*PI*.5-iTime*4.3)*.5), 0.0, 1.0);

    dithered += vec3(d*.5, d, d*.85);

    /**
     * vignette fx
     */
	vec2 xy = fragCoord.xy / iResolution.xy;
	dithered *= vec3(.4, .4, .4) + 0.5*pow(100.0*xy.x*xy.y*(1.0-xy.x)*(1.0-xy.y), .4 );

    fragColor = vec4(dithered, 1.0);
}

