#define TWOPI 2.*acos(-1.)

float rect(vec2 uv, vec2 bl, vec2 tr, float z) {
 	float b = 0.005+.01*z*z;
    float r = smoothstep(bl.x-b, bl.x+b, uv.x);
    r *= smoothstep(bl.y-b, bl.y+b, uv.y);
    r *= 1.-smoothstep(tr.x-b, tr.x+b, uv.x);
    r *= 1.-smoothstep(tr.y-b, tr.y+b, uv.y);
	return r;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .5;
    uv *= 2.;
    uv.x *= iResolution.x/iResolution.y;

    int numSquares = min(500, int(2.*iTime));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    for (int square = 0; square < numSquares; square++) {
        float xShift = .8*sin(float(square)*TWOPI/float(numSquares));
        float yShift = .8*cos(-iTime+float(square)*TWOPI/float(numSquares));
        float zShift = .7+(sin(-iTime))*cos(iTime+float(square)*TWOPI/float(numSquares));
    	vec2 bl = vec2(-.1+xShift,-.1+yShift);
    	vec2 tr = vec2(.1+xShift,.1+yShift);
        float angle = iTime;
        vec2 sq_uv = mat2(cos(angle),-sin(angle), sin(angle), cos(angle))*uv;
        float r = .5+.5*sin(6.*iTime+float(square)*TWOPI/float(numSquares));
        float g = .5+.5*cos(.9*iTime+float(square)*TWOPI/float(numSquares));
        float b = .5+.5*sin(iTime+float(square)*TWOPI/float(numSquares));
        vec3 squareColour = vec3(r, g, b)*(1.-zShift)+1.*length(tr);
    	col = mix(col, squareColour, zShift*rect(sq_uv*zShift, bl, tr, zShift));
        xShift *= -1.5;
        yShift *= -1.;
        bl = vec2(-.1+xShift,-.1+yShift);
    	tr = vec2(.1+xShift,.1+yShift);
        squareColour = vec3(r+g, g, b)*(1.-zShift)+1.*length(tr);
        col = mix(col, squareColour, zShift*rect(sq_uv*zShift, bl, tr, zShift));

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

