// Building on techniques by @iq, @alteredq, @mrkishi and others
// Thanks to @cacheflowe & @reinder for advice on ray marching

mat2 r2d(float a) {
    float sa=sin(a);
    float ca=cos(a);
    return mat2(ca,sa,-sa,ca);
}

float soc(vec3 p) {
    vec3 n = normalize(sign(p+1e6));
    return min(min(dot(p.xy, n.xy), dot(p.yz, n.yz)), dot(p.xz, n.xz));
}

vec2 amod(vec2 p, float m) {
    float a=mod(atan(p.x,p.y), m)-m*.5;
    return vec2(cos(a), sin(a))*length(p);
}

float map(vec3 p) {
    float t = iTime + 240.;
    float k = (t * 4.)+ sin(t * .5) * 4.;
    float r = 14.7072541 + sin(t * .4) * 6.;
    float d = 1.0; vec3 o = p;
    float a = mod(o.y+5.0, (20.))-10.; a = abs(o.y);
    p.zy *= r2d(sign(a)*0.3998);
    p.xz *= r2d(sign(a)*k * .25 - sin(t) * .4);
    p.xz = amod(p.xz, 0.785398163375  - sin(t * .35) * .5);
    p.xz = max(abs(p.xz)-(0.732119777+ sin(t) * .7), (-0.73844485 - sin(t * .3) * 1.2));
    p.z = mod(p.z, 1.00980358)-0.50490179;
    p.x = mod(p.x, r)-(r *.5);
    p.y = mod(p.y+k, 15. + sin(iTime) * .014)-5.;
    d = min(d, soc(max(abs(p)-0.183095666, 0.096382810)));
    return (length(p*-0.34201)-1.3970756)*(0.2746 - sin(t * 1.) * .15) - (d * -2.);
} 

vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.5;
    return normalize(
        e.xyy * map(p + e.xyy) +
        e.yyx * map(p + e.yyx) +
        e.yxy * map(p + e.yxy) +
        e.xxx * map(p + e.xxx));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = (fragCoord.xy/iResolution.xy)*2.5-1.;
    st.x *= 1.7;
    vec3 ro = vec3(st, 12.5022423);
    vec3 rd = normalize(vec3(st+vec2(0.), -0.41422437));
    vec3 mp; mp = ro; float md;
    for (int i=0; i<50; i++) {
        md = map(mp); if (md <.001) break;
        mp += (rd*.480739274206)*md*.838429808073;
    }
    vec3 c1 = vec3(-0.3 - (length(ro-mp) * .02496077) * -1.19102289690);
    vec3 c2; vec3 light = vec3(0,0,0); vec3 p = ro + rd * (mp); vec3 normal = calcNormal(p);
    float dif = clamp(dot(normal, normalize(light - p)), 0., 1.);
    dif *= 5. / dot(light - p, light - p);
    c2 = vec3(pow(dif, .4545)); 
    c2 = vec3(normal.z * .75, 0., normal.y * .25);
    c2 -= vec3(normal.y * 3., 1., 0.);
    fragColor = vec4((c1 + max(c1, c2 * -0.5708 + sin(iTime * .34) * .25)), 1.0);
}

