//The Collatz sequence diverges for most of the complex plane
//So how about look at the direction that it diverges in?
//That's how this fractal is constructed

#define derivative
//#define ratio

#define interactive
#define sensitivity 2.0
//#define grid
#define gridThickness 0.001

#define scale 1.0//exp(-5.5 - 5.5*sin(0.1*iTime))
#define yShift 0.0
#define xShift 0.0

#define AA 2
#define fractalIters 55
const float pi = 3.14159;

vec2 cexp(vec2 z){return exp(z.x)*vec2(cos(z.y), sin(z.y));}
vec2 cexpj(vec2 z){return exp(-z.y)*vec2(cos(z.x), sin(z.x));}
vec2 cmul(vec2 a, vec2 b){return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x );}

vec2 m(float t){
    return iMouse.xy == vec2(0) ? -0.25*vec2(cos(t), 1.0 - sin(t))
    : sensitivity*(2.0*iMouse.xy - iResolution.xy)/iResolution.y;
}

vec2 collatz(vec2 z){
    #ifdef interactive
    return 1.75*z + 2.0*m(iTime) - cmul(cexpj(pi*z), 1.25*z + vec2(0.5, 0));
    #else
    return 1.75*z + vec2(0.5, 0) - cmul(cexpj(pi*z), 1.25*z + vec2(0.5, 0));    
    #endif
}

vec2 dCollatz(vec2 z){
    return vec2(1.75, 0) - cmul(1.25*pi*vec2(-z.y, z.x) + vec2(1.25, 0) + vec2(0.5*pi, 0), cexpj(pi*z));
}

vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 render(vec2 xy){
    #ifdef grid
    vec2 f = fract(xy);
    if(f.x < gridThickness || f.y < gridThickness) return vec3(0);
    #endif
    vec2 z = xy;
    vec2 dz = vec2(1, 0);
    for(int i = 0; i < fractalIters; i++){
        dz = cmul(dz, dCollatz(z));
        z = collatz(z);
    }
    #ifdef ratio
    return vec3(length(z)/length(dz));
    #endif
    #ifdef derivative
    float arg = atan(dz.y, dz.x);
    #else
    float arg = atan(z.y, z.x);
    #endif
    //return vec3(z == z);
    //return vec3(abs(arg - 0.25*pi));
    return hsv2rgb(0.5 + 0.5*sin(arg - vec3(0, 0.666666*pi, 1.333333*pi))).bgr;
}    

vec2 rect(vec2 frag){
    return scale*(2.0*frag - iResolution.xy)/iResolution.y + vec2(xShift, yShift);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        vec2 px = rect(fragCoord + vec2(m, n)/float(AA));
    	col += render(px);    
    }
    col /= float(AA*AA);
#else

    col = render(rect(fragCoord));
#endif            

	fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 O, in vec2 u )
{
	float w1 = sin(u.x * .02 + iTime * .5);
	float w2 = sin(u.x * .03 + iTime / .5) * .6;
	float w3 = sin(u.x * .04 + iTime / .1) * .3;


    vec3 waveColor = vec3(.5 + (w1 + w2 + w3) / 3.8);

    O = vec4(waveColor,1.0);
}

// Author: ocb
// Title: Burial for Aliasing

/************************************************************************************************************/
// This is an attempt to get rid of aliasing in raycasting, maintaining good perfos.
// Only 1 ray is used (no super sampling).
// Primitives are modified + spÃ©cific process.
//
// I add textures for fun, but it's better to de-activate texture to have a better
// view of the AA. (#define in the header line 46)
// 
// There are three parts that need to be anti-aliased (AA):
// 1- the visual outside contour of an object
// 2- the line created by the intersection of 2 objects
// 3- the internal lines created by a broken surface in a single object
//    typically a cube, or a cone/cylinder with a cap.
//
// Case 1- the visual outside contour of an object:
// The max depth reach by the ray into an object is used as a transparency factor.
// This allow to mix the front object color with the backgroud object.
// For simplified process and better perfos, only the first (closest) background object is consider.
// It is a kind of "first order anti aliasing".
// This may generate some artifacts, but not realy noticeable as AA involve only 2 or 3 pixels width.
//
// Case 2- the line created by the intersection of 2 objects:
// Distance between the front and background surface of each interconnected object is the mixing factor.
//
// Case 3- Internal lines created by 2 surfaces (cube):
// This must be done inside the primitive calculation.
// The hit surface and the closest surface from the hit must be found.
// The 2 normals are mixed related to the distance between the 2 surfaces.
// Then the shadowing goes smoothly from a face to another.
//
// The objective is to do simple calculation for perfos. Then some aliasing or artifacts remain.
// Overall smooth image is finally obtained in 90% of time.
//
// Shadows are just widely anti-aliased to obtain soft shadows using the same transparency factor.
//
// Code and algo can surely be optimized!
//
// Ok, now I must go back on my old shader for AA improvement...
/***************************************************************************************************************/

/***************************  PROGRAM OPTIONS  ***********************/

// - Increase anti-aliasing thickness to enlarge AA
// this allow to see the "mixing" process.
// TH should be tuned to obtain 2 or 3 pixels width of AA
//
// - AA_AREA set in vivid color the AA involved area.
// (Where d < 1)
//
// - SPLIT to split screen. 

/******  show Anti-aliased area  ********/
//#define AA_AREA
	const vec3 AREAcol = vec3(0.,1.,0.);

/******   Anti-aliasing thickness  ******/
#define TH 5.

/******  Split screen for comparison ****/
#define SPLIT

/*********************************************************************/


/*********************  Add-on process activation  ******************/

#define ACTIVATE_TEXTURE

#define ACTIVATE_SHADOW
    /***    Shadow softness              ***/
    #define SH .2
    /***    Shadow darkness (0 to 1)     ***/
    #define SC 1.

#define ACTIVATE_REFLECT
    /***    Reflect softness             ***/
    #define RF 30.
    /***    Reflect coef  (0 to 1)       ***/
    #define RC .3

/********************************************************************/


// std needs
#define PI 3.141592653589793
#define PIdiv2 1.570796326794897
#define INFINI 1000.

// objects infos
#define SKY -1
#define SPH 1
#define XCYL 2
#define ZCYL 3
#define BOX 4
#define CONE 5

#define spO vec3(.0)
#define spR 1.
#define spCol vec3(1.000,0.839,0.830)

#define cyO vec2(.5)
#define cyR .4
#define cyCol vec3(0.928,1.000,0.932)

#define boxO vec3(.0,.1,.1)
#define boxD vec3(.2,1.2,.82)
#define boxCol vec3(0.890,0.879,1.000)

#define conO vec3(1.,-.1,.65)
#define conR .4
#define conH 1.5
#define conCol vec3(1.000,0.325,0.402)


// Classical raycast parameters
struct Type_Param{	float t;	// distance factor to the object
            		int hit;	// ID of hit object
            		vec3 n;		// normal of hit point
            		float d;	// max depth reach by ray
            	};


/************************  Standard cam setting  ************************/
vec3 getCamPos(in vec3 camTarget){
    vec3 cam;
    float 	rau = 3.,
    		alpha = iMouse.x/iResolution.x*4.*PI,
    		theta = iMouse.y/iResolution.y*PI+(PI/2.0001);
    if(iMouse.x==0.) cam = rau*normalize(vec3(.2,.4,.5));
    else cam = rau*vec3(-cos(theta)*sin(alpha),sin(theta),cos(theta)*cos(alpha))+camTarget;

    vec2 dcam = cam.xy-cyO;																	//
    if(length(dcam)<=cyR) cam.y = 1.1*sign(dcam.y)*sqrt(cyR*cyR-(dcam.x)*(dcam.x))+cyO.y;	//  cylinder anti-collision
    dcam = cam.zy-cyO;																		//
    if(length(dcam)<=cyR) cam.y = 1.1*sign(dcam.y)*sqrt(cyR*cyR-(dcam.x)*(dcam.x))+cyO.y;	//
    return cam;
}

vec3 getRay(in vec2 st, in vec3 pos, in vec3 camTarget){
    float 	focal = 1.;
    vec3 ww = normalize( camTarget - pos );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0)) ) ;
    vec3 vv = cross(uu,ww);
	// create view ray
	return normalize( st.x*uu + st.y*vv + focal*ww );
}


/**************************  Modified primitives  ***********************/

vec2 sphereImpact(in vec3 pos, in vec3 ray, in vec3 O, in float R, inout vec3 norm ){
    float d=0., t = INFINI;
    vec3 a = O - pos;
    float b = dot(a, ray);

    if (b >= 0.){	// check if object in frontside first (not behind screen)
        float c = dot(a,a) - R*R;
    	d = b*b - c;
    	if (d >= 0.){
        	float sd = sqrt(d);
            t = b - sd;
            if (t < 0.001) b + sd;
            norm = normalize(pos + t*ray - O);
        }
    }

    // The depth reach by the ray depends on the discriminant d.
    // The depth vary from 0 to large value. We consider transparency factor is d when 0.<d<1.
    // As AA concern only 1 or 2 pixels, we have to convert d related to 1 pixel.
    // if object is far, 1 pixel can represent a large amount of sphere radius
    // d must go from 0 to 1 in a pixel size.
    d = max(0.,d);
    return vec2(t, d*iResolution.x/R);
}

vec2 cylinderImpact(in vec2 pos,in vec2 ray, in vec2 O, in float R, inout vec2 norm){
    float t=INFINI;
    vec2 d = pos - O;
    float a = dot(ray,ray);
    float b = dot(d, ray);
    float c = dot(d,d) - R*R;
    float discr = b*b - a*c;
    if (discr >= 0.){
        t = (-b - sqrt(discr))/a;
        if (t < 0.001) t = (-b + sqrt(discr))/a;
        if (t < 0.001) t=INFINI;

        norm = normalize(pos + t*ray - O);
    }
    discr = max(0.,discr);
	return vec2(t,discr/a*iResolution.x/R);
}


vec2 boxImpact( in vec3 pos, in vec3 ray, in vec3 ctr, in vec3 dim, out vec3 norm) 
{
    vec3 m = 1.0/ray;
    vec3 n = m*(ctr-pos);
    vec3 k = abs(m)*dim;

    vec3 t1 = n - k;
    vec3 t2 = n + k;

	float tmax = max( max( t1.x, t1.y ), t1.z );
	float tmin = min( min( t2.x, t2.y ), t2.z );

	if( tmax > tmin || tmin < 0.0) return vec2(INFINI, 0.);

    norm = -sign(ray)*step(vec3(tmax),t1);

    /********************************** AA process ******************************/
    /*** visual outside contour ***/
    // (tmin-tmax) represents the depth reach by the ray into the shape. 
    // larger is the diff between tmax and tmin, deeper into the shape goes the ray
    // when d is small (<1), we use it as a transparency factor
    //
    // As the cube surface is flat and has no thickness (no transparency factor can be used when thickness = 0)
    // we must apply a correction (corr) when visual angle is flat to emphasis the AA effect.
    float corr = dot(ray,-norm);
    corr = (.05+corr)*(1.2-corr*corr*corr);
    float d = (tmin-tmax)*iResolution.x*corr;

    #ifdef SPLIT
    if(gl_FragCoord.x > iResolution.x/2.+2.) return vec2(tmax, d);
    #endif

    /*** inside the visual border's shape, where 2 faces connect (crest). ***/
    // normals must be rotated progressively from one face to the other
    // so the shading of each face are mixed when distance to the crest is small
    // half way rotation is done (other half way is done when other face is hit)
    t1 *= 1.-step(vec3(tmax),t1);
    float tmed = max( max( t1.x, t1.y ), t1.z );	// finding 2nd closest hit
    vec3 norMed = -sign(ray)*step(vec3(tmed),t1);	// normal of the adjacent face
    corr = dot(ray,-norMed);
    corr = (.05+corr)*(1.2-corr*corr*corr);			// flat angle correction
    norm = mix(norMed, norm, .5*min(1.,.5*(tmax-tmed)*corr*iResolution.x/tmax)+.5);	// mixing the 2 normals

    d = max(0.,d);
    return vec2(tmax, d);
}

// finished cone figure with bottom cap
vec2 coneImpact(in vec3 pos, in vec3 ray, in vec3 O, in float H, in float R, inout vec3 norm){
    vec3 tt = vec3(INFINI);
    float t = INFINI;
    float tmin,tmed,tmax;
    vec3 normy = vec3(0.,-1.,0.);
    float dmin = 0.;
    vec3 d = O - pos;
    float Dy = H + d.y;
    float r2 = R*R/(H*H);
    float a = dot(ray.xz,ray.xz) - r2*ray.y*ray.y;
    float b = -dot(d.xz, ray.xz) + r2*Dy*ray.y;
    float c = dot(d.xz,d.xz) - r2*Dy*Dy;
    float disc = b*b - a*c;
    if (disc >= 0.){
        float sqdis = sqrt(disc);
        tt.xy = (-b + vec2(sqdis,-sqdis))/a;		// cone intercept
        if(abs(ray.y) > 0.01) tt.z = d.y/ray.y;		// bottom intercept
        tt += step(tt,vec3(0.))*INFINI; 			// eliminate neg values

        												/*** AA process ***/
        tmax = max(max(tt.x,tt.y),tt.z);				// sort all tt (tmin, tmed, tmax)
        tmin = min(min(tt.x,tt.y),tt.z);				// needed for AA
        vec3 ttemp = step(tt, vec3(tmin))*INFINI + tt;	//
    	tmed = min(min(ttemp.x,ttemp.y),ttemp.z);		//

        vec2 ofc = Dy - ray.y*tt.xy;						// checking upper-lower bounary of the cone
        tt.xy *= step(vec2(0.),ofc)*step(ofc,vec2(H));
        tt.xy += step(tt.xy,vec2(0.))*INFINI;				// eliminate outboud interception of cone

        tt.z += step(R,length(d.xz-tt.z*ray.xz))*INFINI;	// eliminate outboud interception of bottom 

        t = min(min(tt.x,tt.y),tt.z);
        vec3 p = pos+t*ray;

        /********************************** AA process ******************************/
        // 2 situations to deal with, depending on cone or bottom hit.
        if(t != tt.z){		// not bottom, so... cone surface
        	norm = normalize(vec3(p.x-O.x, R*R/(H*H)*(H+O.y-p.y), p.z-O.z));

            // depth reached by the ray must be consider on the side of the cone or at the connection with the bottom cap
            // we keep the min.
            dmin = min( abs(disc/a)/(ofc.y*R/H),	// distance inside a cone (by the sides)
                       .5*(tt.z-t) )*iResolution.x;	// distance at the border of the bottom cut

            #ifdef SPLIT
    		if(gl_FragCoord.x < iResolution.x/2.) 
            #endif
            // mixing the cone and the bottom normals to anti-aliase the crest created by the junction of the two faces.
            if(tt.x != INFINI) norm = mix(normy, norm, .5*min(1.,.25*abs(tmed-tmin)*iResolution.x/TH)+.5);
        }
        else{	// bottom hit
            norm = normy;
            float corr = dot(ray,-norm);
    		corr = (.05+corr)*(1.7-corr*corr*corr);		// here we add correction (same issue as the cube flat surface)
            dmin = .5*(tt.x-t)*corr*iResolution.x;		// depth reach when entering by the bottom (exiting the blind face of the cone)
            #ifdef SPLIT
    		if(gl_FragCoord.x < iResolution.x/2.) 
            #endif
            // finallly, anti-aliase the crest created by the junction of the bottom with the cone.
            // mixing normals with the bottom and the cone surface.
            if(tt.x != INFINI) norm = mix( normalize(vec3(pos.x+tmed*ray.x-O.x, 0., pos.z+tmed*ray.z-O.z)) , norm, .5*min(1.,abs(tmed-tmin)*iResolution.x/t)+.5);
        }
    }
    dmin = max(0.,dmin);
	return vec2(t,dmin);
}


/****************************  AA process  ***************************/

void setParam(in vec2 i, in int objID, in vec3 norm, in vec3 ray, inout Type_Param STD, inout Type_Param AA){

    // The setParam() function fills the standard parameters for ray tracing (t, norm, objID) + max depth reach by ray
    // it fills also the AntiAliasing parameters AA.
    // AA contains same parameters as STD ones but for the object immediatly behind 

    if(i.x<STD.t){
        // a closer object is found, the previous object is stored in AA param
        // the new closest one in STD param
        AA = STD;
        float corr = max(.001,dot(ray,-norm));
        corr = (.05+corr)*(1.7-corr*corr*corr);
        STD.d = min( (STD.t-i.x)*iResolution.x     *corr,// flat angle correction
                   	  i.y);
        STD.t = i.x;
    	STD.hit = objID;
        STD.n = norm;
    }
    else if(i.x<AA.t){
        // No closest object hit but closest background one
        // AA must be updated
        AA.hit = objID;
        AA.t = i.x;
        AA.n = norm;
    }
}

void trace(in vec3 pos, in vec3 ray, inout Type_Param STD, inout Type_Param AA){
    // Raytrace all objects
    // i keep the returned information
    // i.x is t
    // i.y is d the max depth reach by ray
    // The setParam function fills the STD parameters and the AA parameters
    vec3 norm = vec3(0.);
    vec2 i;

    i = boxImpact(pos, ray, boxO, boxD, norm);
    setParam(i, BOX, norm, ray, STD, AA);

    i = coneImpact(pos, ray, conO, conH, conR, norm);
    setParam(i, CONE, norm, ray, STD, AA);

    i = sphereImpact(pos,ray,spO,spR, norm);
    setParam(i, SPH, norm, ray, STD, AA);

    i = cylinderImpact(pos.yz, ray.yz, cyO, cyR, norm.yz);
    setParam(i, XCYL, vec3(0.,norm.yz), ray, STD, AA);

    i = cylinderImpact(pos.xy, ray.xy, cyO, cyR, norm.xy);
    setParam(i, ZCYL, vec3(norm.xy,0.), ray, STD, AA);
}

// simplified raytrace for shadow
float shadowTrace(in vec3 pos, in vec3 ray){

    vec3 norm = vec3(0.);
    vec2 i;
    float shadow = 0., t = INFINI;

    // i.y is the depth reach by the ray.
	// Depth is used to smooth the shadow.
    // From all encountered object, the deeper (biggest i.y) value is kept.

    i = boxImpact(pos, ray, boxO, boxD, norm);
	if(i.x < INFINI) {shadow = max(shadow,i.y), t = min(t,i.x);}

    i = coneImpact(pos, ray, conO, conH, conR, norm);
	if(i.x < INFINI) {shadow = max(shadow,i.y), t = min(t,i.x);}

    i = sphereImpact(pos,ray,spO,spR, norm);
	if(i.x < INFINI) {shadow = max(shadow,i.y), t = min(t,i.x);}

    i = cylinderImpact(pos.yz, ray.yz, cyO, cyR, norm.yz);
	if(i.x < INFINI) {shadow = max(shadow,i.y), t = min(t,i.x);}

    i = cylinderImpact(pos.xy, ray.xy, cyO, cyR, norm.xy);
	if(i.x < INFINI) {shadow = max(shadow,i.y), t = min(t,i.x);}


    #ifdef SPLIT
    if(gl_FragCoord.x < iResolution.x/2.)
    #endif

    shadow /= max(20.,t*SH*iResolution.x);	// expanding same way as anti-aliasing

    return min(1.,shadow);
}

void setColor(inout vec3 color, in vec3 pos, in vec3 ray, in vec3 light, in Type_Param PARAM){
    // Set color to the front object...

    #ifdef ACTIVATE_TEXTURE
    vec3 p = pos+PARAM.t*ray;
    if(PARAM.hit == SKY) return;
    else if(PARAM.hit == SPH) 
        color = .7*spCol+.3*texture(iChannel0,vec2(atan(p.x,p.z),asin(p.y-spO.y))).rgb;
    else if(PARAM.hit == XCYL)
        color = cyCol*(1.- min(vec3(.2),texture(iChannel0, vec2(.5*p.x,atan(p.y,p.z))).rgb));
    else if(PARAM.hit == ZCYL)
        color = cyCol*(1.-min(vec3(.2),texture(iChannel0, vec2(.5*p.z,atan(p.x,p.y))).rgb));
    else if(PARAM.hit == BOX)
        color = boxCol*texture(iChannel2,vec2(p.x+p.z,p.y+p.x)).rgb;
    else if(PARAM.hit == CONE)
        color = .5*(conCol*texture(iChannel3,vec2(.15*atan(p.x-conO.x,p.z-conO.z),p.y)).rgb);

    #else
    if(PARAM.hit == SKY) return;
    else if(PARAM.hit == SPH)  color = spCol;
    else if(PARAM.hit == XCYL) color = cyCol;
    else if(PARAM.hit == ZCYL) color = cyCol;
    else if(PARAM.hit == BOX)  color = boxCol;
    else if(PARAM.hit == CONE) color = conCol;
    #endif

    color *= max(0.,dot(light,PARAM.n));		// light shading
}

float backGround(in vec3 light, in vec3 ray){	// background lighting
    float lit = .5*dot(light,ray)+.5;
    return .5*lit*lit+smoothstep(.99,1.,lit);
}

vec3 smoothReflect(in vec3 pos, in vec3 ray, in vec3 light, in Type_Param PARAM, in bool Noticeable, in vec3 stepOut){
    vec3 col = vec3(0.);
    vec3 p = pos + PARAM.t * ray + stepOut;	// a little step back to avoid black dithering
    vec3 refl = reflect(ray,PARAM.n);
    float bkg = backGround(light,refl);
    vec3 reflCol = vec3(bkg);
    vec3 reflColAA = vec3(bkg);

    Type_Param RFL = Type_Param(INFINI, SKY, vec3(0.), 1.);
    Type_Param RFLAA = Type_Param(INFINI, SKY, vec3(0.), 1.);

    trace(p, refl, RFL, RFLAA);
    setColor(reflCol, p, refl, light, RFL);
    setColor(reflColAA, p, refl, light, RFLAA);


    /*** shadow of reflection ***/

    // the reflection can be under shadow
    // so we have to extend the ray... only if noticeable!
    if(Noticeable){
        if(RFL.t<INFINI){
            vec3 p2 = p + RFL.t * refl + .001*(RFL.n+RFLAA.n);
            float shad = shadowTrace(p2, light);
            reflCol *= 1.-SC*shad;
        }
        if(RFLAA.t<INFINI){
            vec3 p2 = p + RFLAA.t * refl + .01*(RFL.n+RFLAA.n);
            float shad = shadowTrace(p2, light);
            reflColAA *= 1.-SC*shad;
        }
    }

    /****************************  demo option  ******************************/
    /**/ #ifdef SPLIT
    /**/ if(gl_FragCoord.x > iResolution.x/2.) col = RC*reflCol;
    /**/ else 
    /**/ #endif    
    /*************************************************************************/

    col = RC*(reflCol*min(1.,RFL.d/(RFL.t+PARAM.t)/RF) + reflColAA*max(0.,(1.-RFL.d/(RFL.t+PARAM.t)/RF)));

	return col;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 st = fragCoord.xy/iResolution.xy-.5;
    st.x *= iResolution.x/iResolution.y;

    // camera def
    vec3 camTarget = vec3(0.);
    vec3 pos = getCamPos(camTarget);
    vec3 ray = getRay(st, pos,camTarget);

    vec3 light = normalize(vec3(sin(iTime*.1),.3,cos(iTime*.1)));
    float bg = backGround(light, ray);// background color
    vec3 color = vec3(0.);
    vec3 col = vec3(bg), 	colAA = vec3(bg);

    // standard raytrace params (usually used)
    Type_Param STD = Type_Param(	INFINI,		// distance to hit: t
                      				SKY,		// hit object ID: hit
                      				vec3(0.),	// normal of surface object: n
                      				1.			// Max depth reach by the ray inside the object: d
                     			);

    // first order anti-aliasing params
    Type_Param AA = Type_Param(		INFINI,		// distance to hit: t
                      				SKY,		// hit object: hit
                      				vec3(0.),	// normal of surface object: n
                      				1.			// not used in AA
                     			);

    /***********************  Main process ************************/

    trace(pos, ray, STD, AA);
    setColor(col, pos, ray, light, STD); 		// Set color to the front object...
    setColor(colAA, pos, ray, light, AA);		//...and to the first background object (1st order AA)



    /*----------------  All the following (till the dashed line below) -------------*/					 
	/*				are visual options. not necessary to understand process         */
	/*------------------------------------------------------------------------------*/

    /**************************  Shadow  ***************************/

    // soft shadow = big anti-aliasing on shadow
    #ifdef ACTIVATE_SHADOW
    if(STD.t<INFINI){
    	vec3 p = pos + STD.t * ray + .001*(STD.n+AA.n);	// adding a little step-out
        float shad = shadowTrace(p, light);
        col *= 1.-SC*shad;
    }
	if(AA.t<INFINI){
    	vec3 p = pos + AA.t * ray + .01*(STD.n+AA.n);
        float shad = shadowTrace(p, light);
        colAA *= 1.-SC*shad;
    }
    #endif

    /**************************  reflection  ************************/

    // reflections are anti-aliased too
    #ifdef ACTIVATE_REFLECT
    if(STD.t<INFINI) col += smoothReflect(pos, ray, light, STD, true, .001*(STD.n+AA.n));	// last parameter used for a little step-out
    if(AA.t<INFINI) colAA += smoothReflect(pos, ray, light, AA, false, .01*(STD.n+AA.n));	// to avoid black dithering
    #endif

    /****************************  demo option  ******************************/
    /**/ #ifdef AA_AREA
    /**/ if(STD.hit == BOX){
    /**/ 	 if(floor(abs(STD.n)) == vec3(0.)) col = AREAcol;
    /**/ }
    /**/ else if(STD.hit == CONE){
	/**/	 if(STD.n.y/length(STD.n.xz)+.001 < conR/conH && STD.n.y != -1.) col = AREAcol;
    /**/ }
    /**/ colAA += float(STD.hit != SKY)*AREAcol;		// make visible the AA area involved
    /**/ #endif
	/************************************************************************/

    /*-----------------------------------------------------------------------------------*/
    /*									 end of options 								 */
    /*-----------------------------------------------------------------------------------*/


    float blur = max(1.,.2*STD.t);	// far distance blur (using enhanced AA depending on distance)

    // Finally, mix the color of front and background object
    // depending on ray depth inside the front object (STD.d)
    color = col*min(1.,STD.d/STD.t/TH/blur) + colAA*max(0.,(1.-STD.d/STD.t/TH/blur));



    /****************************  demo option  ******************************/
    /**/ #ifdef SPLIT
    /**/ if(gl_FragCoord.x < iResolution.x/2.) color = col*min(1.,STD.d/STD.t/TH/blur) + colAA*max(0.,(1.-STD.d/STD.t/TH/blur));
    /**/ else if(gl_FragCoord.x > iResolution.x/2.+2.) color = col;
    /**/ else color = vec3(0.995,0.829,0.006);
    /**/ #endif
	/************************************************************************/

    fragColor = vec4(color,1.0);
