#define PI 3.14159265358979
#define ACCELI 4.
#define ELI 4.
#define QUALITY 16

// 1/(1+2^x)
//--------------------------------------------------
// hash function
vec3 hash3( vec2 p ) {
    vec3 q = vec3( dot(p,vec2(127.1,311.7)),
                   dot(p,vec2(269.5,183.3)),
                   dot(p,vec2(419.2,371.9)) );
    return fract(sin(q)*43758.5453);
}
//--------------------------------------------------
// iqnoise //original algorithm by ÃÃ±igo QuÃ­lez
float iqnoise( in vec2 x) {
    vec2 p = floor(x);
    vec2 f = fract(x);
    float k = 1.;
    float va = 0.0;
    float wt = 0.0;
    for (int j=-2; j<=2; j++) {
        for (int i=-2; i<=2; i++) {
            float ww = pow( 1.0-smoothstep(0.0,1.414,
                    sqrt(dot(vec2(float(i),float(j)) - f +
                   hash3(p + vec2(float(i),float(j))).xy,
                             vec2(float(i),float(j)) - f +
                   hash3(p + vec2(float(i),float(j))).xy))), k );
            va +=  hash3(p + vec2(float(i),float(j))).z*ww;
            wt += ww;
        }
    }
    return va/wt;
}
//--------------------------------------------------
//fbm
float fbm (in vec2 x) {
	float f = 0.;
    float w = .5;
    for (int i=0; i<5; i++) {
        f += w*iqnoise(x*1.5/*+u_time*/); x *= 2.05;
        w *= .5;
    }
    return f;
}
//--------------------------------------------------
// sdSphere
float sdSphere(in vec3 p, in float size)
{
  return length(p)-size;
}
//--------------------------------------------------
// sdNoisePlane
// The part that generates the texture for the plane
float sdPlaneN(in vec3 p, in float height, in float nHeight, in vec2 nSeed) {
    return p.z-((fbm(vec2(p/*+u_time/5.*/))*nHeight)+height);
	// p.x+0./*u_time/*nSeed.x*/,p.y+u_time/10./*nSeed.y*/
}
// The way this works is interesting
// it checks whether or not it is within range of the texture,
// and if it is, it then returns the texture height
float sdPlane(in vec3 p, in float height, in float nHeight, in vec2 nSeed) {
    if (0.>p.z-(height+nHeight)) {
        return sdPlaneN(p,height,nHeight, nSeed);
    }
    return p.z-(height+nHeight);
}
//--------------------------------------------------
// converting radian trig functions to degrees
// sin
float degSin(in float x) {
    return sin(x*PI/180.);
}
// cos
float degCos(in float x) {
    return cos(x*PI/180.);
}
//--------------------------------------------------
// sigmoid
float sigmoid(in float x) {
    return 2./(1.+pow(1.05,x));
}
//--------------------------------------------------
// cast ray
float castRay(in vec3 cam, in vec2 uv) {
    float distance = 0.; // distance from the camera
    float radius = 0.; // distance from nearest shape
    float oRadius = radius;
    vec3 p = cam;
    uv = uv*2.-2.; // adjusting the viewing fulcrum
    vec3 mult = vec3(degSin(uv.x), // finding out the slope rates
                     degCos(uv.x),
                     degSin(uv.y));
    for (int i = 0; i < QUALITY; i++) {
        oRadius = radius;
        radius = sdPlane(p,-2.,1.,vec2(0.,0.)); // finding the distance to the plane
        if (radius < 0.) {
            for (int j = 0; j < 1; j++) {
                oRadius = abs(oRadius) * .5;
                radius = sdPlane(p,-2.,1.,vec2(0.,0.)); // finding the distance to the plane
                if (radius < 0.) {
                    oRadius *= -1.;
                }
                distance += oRadius; // getting closer to 0
                p = cam+(mult*distance); // moving the point
                if (abs(radius) < ACCELI) {
                    //return 1.-float(i+j)/float(QUALITY);
                    return sigmoid(distance); // returns more 'exact' distance
                }
            }
            //return 1.-float(i+8)/float(QUALITY);
        	return sigmoid(distance);
        }
		    if (radius < ELI) {
            radius = ELI;
        }
        distance += radius;
        p = cam+(mult*distance);
    }
	return 0.;
}
