}

// Grid marching from iq's Grid of Cylinders
// https://www.shadertoy.com/view/4dSGW1

const float streetDistance = 0.6;
const vec3 streetColor = vec3(4.0, 4.0, 4.0);

const float fogDensity = 0.5;
//const float fogDistance = 4.0;
const vec3 fogColor = vec3(0.5, 0.5, 0.5);

const float windowSize = 0.1;
const float windowDivergence = 0.2;
const vec3 windowColor = vec3(0.3, 0.3, 0.3);

const float beaconProb = 0.0003;
const float beaconFreq = 0.6;
const vec3 beaconColor = vec3(1.5, 0.2, 0.0);

const float c = 0.5f;
const float b = 0.5f;

const float tau = 6.283185;

float hash1(vec2 p2) {
    p2 = fract(p2 * vec2(5.3983, 5.4427));
    p2 += dot(p2.yx, p2.xy + vec2(21.5351, 14.3137));
    return fract(p2.x * p2.y * 95.4337);
}

float hash1(vec2 p2, float p) {
    vec3 p3 = fract(vec3(5.3983 * p2.x, 5.4427 * p2.y, 6.9371 * p));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash2(vec2 p2) {
    vec3 p3 = fract(vec3(5.3983 * p2.x, 5.4427 * p2.y, 6.9371 * p2.x));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.xx + p3.yz) * p3.zy);
}

vec2 hash2(vec2 p2, float p) {
    vec3 p3 = fract(vec3(5.3983 * p2.x, 5.4427 * p2.y, 6.9371 * p));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.xx + p3.yz) * p3.zy);
}

vec3 hash3(vec2 p2) {
    vec3 p3 = fract(vec3(p2.xyx) * vec3(5.3983, 5.4427, 6.9371));
    p3 += dot(p3, p3.yxz + 19.19);
    return fract((p3.xxy + p3.yzz) * p3.zyx);
}

float noise1(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(mix(hash1(i + vec2(0.0, 0.0)), 
                   hash1(i + vec2(1.0, 0.0)), u.x),
               mix(hash1(i + vec2(0.0, 1.0)), 
                   hash1(i + vec2(1.0, 1.0)), u.x), u.y);
}

vec4 castRay(vec3 eye, vec3 ray) {
    vec2 block = floor(eye.xy);
    vec3 ri = 1.0 / ray;
    vec3 rs = sign(ray);
    vec3 side = 0.5 + 0.5 * rs;
    vec2 ris = ri.xy * rs.xy;
    vec2 dis = (block - eye.xy + 0.5 + rs.xy * 0.5) * ri.xy;

    float beacon = 0.0;

    for (int i = 0; i < 200; ++i) {
        vec2 lo0 = vec2(block + 0.01);
        vec2 loX = vec2(0.1, 0.1);
        vec2 hi0 = vec2(block + 0.69);
        vec2 hiX = vec2(0.4, 0.4);
        float height = (0.5 + hash1(block)) * (2.0 + 4.0 * pow(noise1(0.1 * block), 2.5));

        float dist = 500.0;
        float face = 0.0;
        for (int j = 0; j < 3; ++j) {
            float top = height * (1.0 - 0.1 * float(j));
            vec3 lo = vec3(lo0 + loX * hash2(block, float(j)), 0.0);
            vec3 hi = vec3(hi0 + hiX * hash2(block, float(j) + 0.5), top);

            vec3 wall = mix(hi, lo, side);
            vec3 t = (wall - eye) * ri;

            vec3 dim = step(t.zxy, t) * step(t.yzx, t);            
            float maxT = dot(dim, t);
            float maxFace = 1.0 - dim.z;

            vec3 p = eye + maxT * ray;
            dim += step(lo, p) * step(p, hi);
            if (dim.x * dim.y * dim.z > 0.5 && maxT < dist) {
                dist = maxT;
                face = maxFace;
            }
        }

        float prob = beaconProb * pow(height, 3.0);
        vec2 h = hash2(block);
        if (h.x < prob) {
            vec3 center = vec3(block + 0.5, height + 0.2);
            float t = dot(center - eye, ray);
            if (t < dist) {
                vec3 p = eye + t * ray;
               // float fog = (exp(-p.z / fogDistance) - exp(-eye.z / fogDistance)) / ray.z;
               // fog = exp(fogDensity * fog);

                t = distance(center, p);
                //fog *= smoothstep(1.0, 0.5, cos(tau * (beaconFreq * iTime + h.y)));
                //beacon += fog * pow(clamp(1.0 - 2.0 * t, 0.0, 1.0), 4.0);
            }
        }

        if (dist < 400.0) {
            return vec4(dist, beacon, face, 1.0);
        }

        float t = eye.z * ri.z;
        vec3 p = eye - t * ray;
        vec2 g = p.xy - block;
        if (g.x > 0.0 && g.x < 1.0 && g.y > 0.0 && g.y < 1.0) {
            return vec4(-t, beacon, 0.0, 1.0);
        }

        vec2 dim = step(dis.xy, dis.yx); 
        dis += dim * ris;
        block += dim * rs.xy;
    }

    if (ray.z < 0.0) {
        return vec4(-eye.z * ri.z, beacon, 0.0, 1.0);
    }

    return vec4(0.0, beacon, 0.0, 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 m = vec2(0.66f, 0.33);
    if (iMouse.z > 0.0)
        m = iMouse.xy / iResolution.xy;
    m *= tau * vec2(1.0, 0.25);

    vec3 center = vec3(0.5, 0.5 * iTime, 3.0);
    float dist = 15.0;
    vec3 eye = center + vec3(dist * sin(m.x) * sin(m.y), dist * cos(m.x) * sin(m.y), dist * cos(m.y));
    float zoom = 4.0;

    vec3 forward = normalize(center - eye);
    vec3 right = normalize(cross(forward, vec3(0.0, 0.0, 1.0)));
    vec3 up = cross(right, forward);
    vec2 xy = 2.0 * fragCoord - iResolution.xy;
    zoom *= iResolution.y;
    vec3 ray = normalize(xy.x * right + xy.y * up + zoom * forward);

    vec4 res = castRay(eye, ray);
    if (res.w < 0.5) {
        fragColor = vec4(fogColor + res.y * beaconColor, 1.0);
        return;
    }

    vec3 p = eye + res.x * ray;

    vec2 block = floor(p.xy);
    vec3 window = floor(p / windowSize);
    float x = hash1(block, window.x);
    float y = hash1(block, window.y);
    float z = hash1(block, window.z);
    vec3 color = windowColor + windowDivergence * (hash3(block) - 0.5);
    color *= smoothstep(0.1, 0.9, fract(2.5 * (x * y * z)));
    color += streetColor * exp(-p.z / streetDistance);

    //vec3 buildingColor = vec3(0.1f, 0.1f, 0.1f);
    vec3 level = 0.25 * streetColor * exp(-4.0 * p.z / streetColor);
    color = clamp(mix(level, color, res.z), 0.0, 1.0);

    float fogDistance = max(1.0f, 4.6f * sin(p.z + iTime*1.1)) 
        			    * noise1(p.xy) + distance(p, eye) * 1.0;

    fogDistance += max(1.0f, 5.6f * sin(p.y + iTime*1.5)) 
        			    * noise1(p.xy) + distance(p, eye) * 1.0;

    fogDistance *= 0.5f;

    //float fog = (exp(-p.z / fogDistance) - exp(-eye.z / fogDistance)) / ray.z;


    float fog = 0.8f * exp(-eye.z*0.1f) * (1.0-exp( -fogDistance*ray.z*0.1f ))/ray.z;


   // fog = exp(fogDensity*0.005f * fog);


   // vec3  fogColor  = vec3(0.5,0.6,0.7);
   // return mix( rgb, fogColor, fogAmount );

    color = mix(color, fogColor, fog);

    color += res.y * beaconColor;
    color += pow(res.y, 2.0);

    fragColor = vec4(color, 1.0);
}

// Code by Flopine
// Thanks to wsmind and leon for teaching me! :)


#define ITER 80.
#define PI 3.141592
#define TAU 2.*PI

/////////////////// UTILITIES
mat2 rot (float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2 (c,s,-s,c);
}

vec2 moda (vec2 p, float per)
{
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a-per/2., per)-per/2.;
    return vec2(cos(a),sin(a))*l;
}

// iq's palette
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

/////////////////// PRIMARY SHAPES
float sdHexPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max((q.x*0.866025+q.y*0.5),q.y)-h.x);
}

float sphe (vec3 p, float r)
{
    return length(p)-r;
}

float cylY (vec3 p, float r)
{
    return length(p.xz)-r;
}

/////////////////// PRIMITIVES
float prim1(vec3 p)
{
    p.yz *= rot(PI/2.);
    return max(-sphe(vec3(p.x,p.y,p.z*0.9),1.06),sdHexPrism (p, vec2 (1.,0.5)));
}

float prim2 (vec3 p)
{
    p.xz *= rot(iTime+p.y);
    p.xz = moda(p.xz, TAU/6.);
    p.x -= .6;

    return cylY(p,.1);
}

float gear1 (vec3 p, float per)
{

    p.y = mod(p.y-per/2.,per)-per/2.;
    p.xz *= rot(iTime);
    return prim1(p);
}

float gear2 (vec3 p, float per)
{
    p.y -= per/2.;
    p.y = mod(p.y-per/2.,per)-per/2.;
    p.xz *= rot(-iTime);
    return prim1(p);
}

float pattern (vec3 p)
{
   return min(gear2(p,4.),min(gear1(p, 4.),prim2(p)));  
}


//////////////////// Raymarching field
float SDF (vec3 p)
{
    float per = 2.;

    p.xy *= rot(iTime*0.3+(p.z*0.2));
    p.z = mod(p.z-per/2.,per)-per/2.;
    p.xy = moda(p.xy, TAU/4.);
    p.x -= 4.;
   return pattern(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    vec3 p = vec3 (0.001,0.001,iTime);
    vec3 dir = normalize(vec3 (uv*2.,1.));

    float shad = 0.;
    vec3 col = vec3 (0.);

    for (float i=0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            shad = i/ITER;
            col = vec3(1.-shad)*palette(dir.z,
                          vec3 (0.,0.5,0.),
                          vec3 (0.,0.2,0.1),
                          vec3 (0.2),
                          vec3(iTime*0.3));;
            break;
        }

        p+=d*dir*0.7;
    }


    // Output to screen
    fragColor = vec4(pow(col,vec3(0.45)),1.0);
}

