#define AA  1    // antialiasing (0/1 - off/on)

float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
void HexVorInit ();
vec4 HexVor (vec2 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);

vec3 sunDir, vnCylOut;
vec2 csI, csI2, csD, csD2;
float tCur, dstFar, bCylRad, bCylHt, dCylOut;
const float pi = 3.14159, sqrt3 = 1.73205;

vec3 IcosSym (vec3 p)
{
  float a, w;
  w = 2. * pi / 3.;
  p.yz = Rot2Cs (vec2 (p.y, abs (p.z)), csI);
  p.x = - abs (p.x);
  for (int k = 0; k < 3; k ++) {
    if (dot (p.yz, csI) > 0.) p.zy = Rot2Cs (p.zy, csI2) * vec2 (1., -1.);
    p.xy = Rot2D (p.xy, - w);
  }
  if (dot (p.yz, csI) > 0.) p.zy = Rot2Cs (p.zy, csI2) * vec2 (1., -1.);
  a = mod (atan (p.x, p.y) + 0.5 * w, w) - 0.5 * w;
  p.yx = vec2 (cos (a), sin (a)) * length (p.xy);
  p.xz = - vec2 (abs (p.x), p.z);
  return p;
}

vec3 DodecSym (vec3 p)
{
  float a, w;
  w = 2. * pi / 5.;
  p.xz = Rot2Cs (vec2 (p.x, abs (p.z)), vec2 (csD.x, - csD.y));
  p.xy = Rot2D (p.xy, - 0.25 * w);
  p.x = - abs (p.x);
  for (int k = 0; k < 3; k ++) {
    if (dot (p.yz, csD) > 0.) p.zy = Rot2Cs (p.zy, csD2) * vec2 (1., -1.);
    p.xy = Rot2D (p.xy, - w);
  }
  if (dot (p.yz, csD) > 0.) p.zy = Rot2Cs (p.zy, csD2) * vec2 (1., -1.);
  a = mod (atan (p.x, p.y) + 0.5 * w, w) - 0.5 * w;
  p.yx = vec2 (cos (a), sin (a)) * length (p.xy);
  p.xz = - vec2 (abs (p.x), p.z);
  return p;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float d, a, polyRad, spRad, cylLen, cylRad;
  polyRad = 2.5;
  spRad = 0.4;
  cylRad = 0.15;
  p.y -= polyRad + spRad - bCylHt + 1.65;
  q = DodecSym (p);
  q.z += polyRad;
  d = PrSphDf (q, spRad);
  q = IcosSym (p);
  q.z += polyRad;
  d = min (d, PrSphDf (q, spRad));
  a = mod (atan (q.x, q.y) + pi / 3., 2. * pi / 3.);
  q.yx = vec2 (cos (a), sin (a)) * length (q.xy);
  q.yz = Rot2D (q.yz, -0.32);
  cylLen = 0.33 * polyRad - spRad;
  q.y -= cylLen + spRad;
  return SmoothMin (d, PrCylDf (q.xzy, cylRad, cylLen + 0.3), 0.1);
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

void InCylHit (vec3 ro, vec3 rd)
{
  vec3 s;
  float a, b, w, ws, srdy;
  dCylOut = dstFar;
  vnCylOut = vec3 (0.);
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - bCylRad * bCylRad);
  if (w > 0.) {
    ws = sqrt (w);
    srdy = sign (rd.y);
    if (a > 0.) {
      dCylOut = (- b + ws) / a;
      s = ro + dCylOut * rd;
    } else s.y = bCylHt;
    if (abs (s.y) < bCylHt) vnCylOut.xz = - s.xz / bCylRad;
    else {
      dCylOut = (- srdy * ro.y + bCylHt) / abs (rd.y);
      vnCylOut.y = - srdy;
    }
  }
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  float sd;
  rd = normalize (vec3 (rd.x, max (0.001, rd.y), rd.z));
  ro.xz += 5. * tCur;
  sd = max (dot (rd, sunDir), 0.);
  return mix (vec3 (0.2, 0.3, 0.65) + 0.1 * pow (sd, 16.) + 0.2 * pow (sd, 256.), vec3 (0.9),
     clamp (3. * (Fbm2 (0.01 * (rd.xz * (100. - ro.y) / rd.y + ro.xz)) - 0.5) * rd.y + 0.1, 0., 1.));
}

vec3 ShStagGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.8 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = 0; j < 40; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += min (0.2, 3. * h);
    if (sh < 0.001) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec4 vc;
  vec3 vn, col, rg;
  vec2 qw;
  float hy, hhy, hw, ww, b, a, sRotH, sRotV;
  InCylHit (ro + vec3 (0., - bCylHt + 1.25, 0.), rd);
  if (vnCylOut.y == 0.) {
    ro += dCylOut * rd;
    vn = vnCylOut;
    hy = (ro.y - 1.75) / bCylHt;
    hhy = abs (hy) - 0.43;
    a = atan (vn.x, - vn.z) / pi;
    sRotH = mod (64. * 0.5 * (1. + a) + 0.5, 1.) - 0.5;
    hw = 0.29;
    ww = 0.28;
    qw = abs (vec2 (sRotH, hhy));
    if (qw.x < ww && qw.y < hw) {
      qw = abs (qw - 0.5 * vec2 (ww, hw)) - vec2 (0.44 * ww, 0.47 * hw);
      if (max (qw.x, qw.y) < 0.) col = 0.7 * SkyCol (ro, reflect (rd, vn));
      else col = vec3 (0.5, 0.5, 0.4) * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) +
         0.5 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 128.);
    } else {
      col = vec3 (0.7, 0.72, 0.6);
      qw -= vec2 (ww, hw);
      if (abs (hy) > (1. - 0.85/16.) || max (qw.x, qw.y) < 0.02) {
        col *= 0.9;
        vn.xz = Rot2D (vn.xz, - pi * a);
        if (abs (hy) > (1. - 0.85/16.)) {
          sRotV = (1. - abs (2. * SmoothBump (1. - 0.9/16., 0.97, 0.03, abs (hy)) - 1.));
        } else {
          sRotV = (1. - abs (2. * SmoothBump (hw, hw + 0.02, 0.02, abs (hhy)) - 1.)) * sign (hhy);
          vn.xz = Rot2D (vn.xz, 0.4 * pi * sign (sRotH) *
             (1. - abs (2. * SmoothBump (- (ww + 0.02), ww + 0.02, 0.04, sRotH) - 1.)));
        }
        vn.yz = Rot2D (vn.yz, -0.2 * pi * sRotV * sign (hy));
        vn.xz = Rot2D (vn.xz, pi * a);
      } else {
        qw = 16. * vec2 (12. * a, hy);
        rg = ShStagGrid (qw);
        col *= rg.y * (1. - 0.3 * Noisefv2 (8. * qw));
        rg.xz *= sign ((abs (vn.x) > 0.5) ? vn.x : vn.z);
        if (abs (vn.x) > 0.5) {
          if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
          else vn.xz = Rot2D (vn.xz, rg.x);
        } else {
          if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
          else vn.zx = Rot2D (vn.zx, rg.x);
        }
      }
      col = col * (0.5 + 0.5 * max (dot (vn, sunDir), 0.)) +
         0.1 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 64.);
    }
  } else if (vnCylOut.y > 0.) {
    ro += dCylOut * rd;
    HexVorInit ();
    vc = HexVor (4. * ro.zx);
    b = 1. - smoothstep (-0.2, -0.05, rd.y) * smoothstep (0.4, 0.8, dCylOut / (1.6 * bCylRad));
    vn = mix (vec3 (0., 1., 0.), normalize (vec3 (vc.y, 2., vc.z)),
       b * SmoothBump (0.05, 0.13, 0.02, vc.x));
    col = vec3 (0.65, 0.62, 0.62) * (0.7 + 0.3 * b * step (0.06 + 0.03 * vc.w, vc.x) *
       (1. - 0.1 * Noisefv2 (128. * ro.xz)));
    col = col * (0.3 + 0.7 * ObjSShadow (ro, sunDir) * max (dot (vn, sunDir), 0.));
  } else col = SkyCol (ro, rd);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, rop, rdp;
  float dstObj, refFac, dihedIcos, dihedDodec;
  bCylRad = 16.;
  bCylHt = 3.;
  dihedIcos = 0.5 * acos (sqrt (5.) / 3.);
  csI = vec2 (cos (dihedIcos), - sin (dihedIcos));
  csI2 = vec2 (cos (2. * dihedIcos), - sin (2. * dihedIcos));
  dihedDodec = 0.5 * atan (2.);
  csD = vec2 (cos (dihedDodec), - sin (dihedDodec));
  csD2 = vec2 (cos (2. * dihedDodec), - sin (2. * dihedDodec));
  refFac = 1.;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    refFac *= 0.9;
    ro += dstObj * rd;
    vn = ObjNf (ro);
    rd = reflect (rd, vn);
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
    if (dstObj < dstFar) {
      refFac *= 0.9;
      ro += dstObj * rd;
      vn = ObjNf (ro);
      rd = reflect (rd, vn);
      rop = ro;
      rdp = rd;
    } else {
      rop = ro;
      rdp = rd;
    }
  } else {
    rop = ro;
    rdp = rd;
  }
  col = refFac * BgCol (rop, rdp);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvd, ori, ca, sa;
  float el, az, t, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.11 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
  } else {
    t = floor (0.3 * tCur) + smoothstep (0.85, 1., mod (0.3 * tCur, 1.));
    az = -0.75 * pi + (1.2/16.) * pi * t;
    el += 0.1 * pi * cos (0.2 * pi * t);
  }
  el = clamp (el, -0.2 * pi, -0.01 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 1.6, -15.);
  zmFac = 4.5 + 6. * abs (el);
  dstFar = 40.;
  sunDir = normalize (vec3 (1., 1., -1.));
  rd = vuMat * normalize (vec3 (uv, zmFac));
#if ! AA
  rd = vuMat * normalize (vec3 (uv, zmFac));
  col = ShowScene (ro, rd);
#else
  col = vec3 (0.);
  for (int iy = 0; iy <= 1; iy ++) {
    for (int ix = 0; ix <= 1; ix ++) {
      uvd = (0.5 / canvas.y) * (2. * vec2 (float (ix), float (iy)) - 1.);
      rd = vuMat * normalize (vec3 (uv + uvd, zmFac));
      col += ShowScene (ro, rd);
    }
  }
  col *= 0.25;
#endif
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1. / sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec2 gVec[7], hVec[7];

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.7;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

#define C 0.05
//N = number of sources 
//N > 1
#define N 48
#define F 5.0
#define PI 3.14159265358979323

float getSignalValue(float t, float f, float Phi){
    return cos(t*f*2.0*PI+Phi);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.y;
    if(uv.x < 0.18){
        fragColor = vec4(0.0f);
    }
    else{
        float signalSum = 0.0;
        float Psi = PI/8.0*sin(iTime);
        float deltaY = 0.2/float(N-1);
        float deltaPhi = (2.0*PI*deltaY*sin(Psi))/(C/F);
        for(int i = 0; i < N; i++){
            vec2 pos = vec2(0.2, 0.4)
                +vec2(0.0, float(i)*deltaY);
            float d = length(uv-pos);
            float t = d/C;
            float Phi = float(i)*deltaPhi;
            signalSum += getSignalValue(iTime-t, F, Phi)*
        		 (0.42659-
                 0.49656*cos(2.*PI*float(i)/float(N-1))+
                 0.076849*cos(4.*PI*float(i)/float(N-1))
                 );

        }
        fragColor = vec4(signalSum);
    }
}

float oval(vec2 center, vec2 p) {
	return distance(center, p);
}

float channel(vec2 c, vec2 p, float s1, float s2) {
	return smoothstep(  oval(c, p), s1, s2);
}


// from https://github.com/msfeldstein/glsl-map
float map(float value, float inMin, float inMax, float outMin, float outMax) {
  return outMin + (outMax - outMin) * (value - inMin) / (inMax - inMin);
}

vec2 path(float t, float phi) {
    float bound = 0.333;
    return vec2( map(sin(iTime + phi), -bound, bound, 0.1, 0.9)
               , map(cos(iTime + phi), -bound, bound, 0.1, 0.9));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5* iResolution.xy ) / iResolution.y;

    float q = distance(uv, vec2(0.5, 0.5));

    vec3 col = (vec3( channel( path(iTime, 0.33) * cos(q * 5.0), uv, q, q + cos(iTime)), 
                     channel( path(iTime, 0.5) * cos(q * 4.0) + 0.5, uv, q, q + cos(iTime * 0.73)), 
                     channel( path(iTime, 0.6) * cos(q * 3.0)+ 0.7, uv, q, q + cos(iTime * 0.34))) / 2.0);

    col += (vec3( channel( path(iTime, 0.1) * cos(q * 1.1), uv, q, q + cos(iTime)), 
                 channel( path(iTime, 0.2) * cos(q * 1.8), uv, q, q + cos(iTime * 0.73)), 
                 channel( path(iTime, 0.9) * cos(q * 0.9), uv, q, q + cos(iTime * 0.34))) / 2.0);

    fragColor = sqrt(mix(texture(iChannel0, uv + ((col.rb - 0.5) / 5.0)), vec4(col, 1.0), (length(col + 0.5))));
}

// https://stackoverflow.com/a/37221804/1102705
int cash(int x, int y, int seed){   
    int h = seed + x*374761393 + y*668265263; //all constants are prime
    h = (h^(h >> 13))*1274126177;
    return h^(h >> 16);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int intensity = cash(int(fragCoord.x), int(fragCoord.y), int(iTime*1000.));
    float i1 = float(intensity&0xff);
    float i2 = float(intensity>>8 & 0xff);
    float i3 = float(intensity>>16 & 0xff);
    float i4 = float(intensity>>24 & 0xff);
    fragColor = vec4(i1,i2,i3,i4)/float(0xff);

}

