float soc(vec3 p) {
    vec3 n = normalize(sign(p+1e6));
    return min(min(dot(p.xy, n.xy), dot(p.yz, n.yz)), dot(p.xz, n.xz));
}

mat2 r2d(float a) {
    float sa=sin(a);
    float ca=cos(a);
    return mat2(ca,sa,-sa,ca);
}

vec2 amod(vec2 p, float m) {
    float a=mod(atan(p.x,p.y), m)-m*.5;
    return vec2(cos(a), sin(a))*length(p);
}

float map(vec3 p) {
    float s = ((-iTime) * 9.2) + sin(iTime * .27) * 9.2;
    float d = 1.0; vec3 o = p;
    float a=mod(o.y+5.0, (20.))-10.; a = abs(o.y);
    p.yz *= r2d(sign(a)* -5.);
    p.xz *= r2d(sign(a)*(s * .04));
    p.xz = amod(p.xz, 3.7);
    p.xz = max(abs(p.xz) + 0.0808, -3.3221);
    p.z = mod(p.z, 5.)-2.5;
    p.x = mod(p.x, 26.)-13.;
    p.y = mod(p.y+s, 18.)-5.;
    d = min(d, soc(max(abs(p)-.3999, .3565)));
    return (length(p*-.0168)-.0097)* -3. - (d * -1.);
}

vec3 b(vec3 c1, vec3 c2){
    return vec3((c1.r > 0.5) ? max(c2.r, 2.0 * (c1.r - 0.5)) : min(c2.r, 2.0 * c1.r), (c1.r > 0.5) ? max(c2.g, 2.0 * (c1.g - 0.5)) : min(c2.g, 2.0 * c1.g),(c1.b > 0.5) ? max(c2.b, 2.0 * (c1.b - 0.5)) : min(c2.b, 2.0 * c1.b));                                                                      { return c1 + c2; }                               // Add (default)
}

vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.05;
    return normalize( e.xyy * map(p + e.xyy) + e.yyx * map(p + e.yyx) + e.yxy * map(p + e.yxy) + e.xxx * map(p + e.xxx));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec3 l = vec3(-10);
    vec2 st = (fragCoord.xy/iResolution.xy)*2.5-1.;
    st.x *= 1.7;
    vec3 ro = vec3(st, 5. + sin(iTime * .8) * 3.5);
    vec3 rd = normalize(vec3(st+vec2(0.), .3662));
    vec3 mp; mp = ro; float md;
    for (int i=0; i<50; i++) { md = map(mp); if (md <.001) break; mp += rd*md; }
    vec3 c1 = vec3(-0.3805 - (length(ro-mp) * (.035 + sin(iTime * .8) * .006)) * -1.8547), c2, c3;
    vec3 p = ro + rd * (mp);
    if (md > -0.7875) {
        float dif = clamp(dot(calcNormal(p), normalize(l - p)), 0., 1.);
        dif *= 5. / dot(l - p, l - p);
        c2 = vec3(pow(dif, .053)); 
    } else {
        c3 = vec3(p.x * .15);
    }
    c2 = b(c2, vec3((mp.z * 2.5) * (md * .05), 0, 0));
    fragColor = vec4(abs((c2 + c3) - c1), 1.);
}

#define PI 3.14159265358979323846

vec2 rotate2D (vec2 _st, float _angle) {
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

vec2 tile (vec2 _st, float _zoom) {
    _st *= _zoom;
    return fract(_st);
}

vec2 rotateTilePattern(vec2 _st){

    //  Scale the coordinate system by 2x2
    _st *= 4.0;

    //  Give each cell an index number
    //  according to its position
    float index = 0.0;
    index += step(1., mod(_st.x,2.0));
    index += step(1., mod(_st.y,2.0))*2.0;

    _st = fract(_st);

    // Rotate each cell according to the index
    if(index == 1.0){
        _st = rotate2D(_st,PI*0.75);
    } else if(index == 2.0){
        _st = rotate2D(_st,PI*-0.25);
    } else if(index == 3.0){
        _st = rotate2D(_st,PI*0.25);
    } else {
      _st = rotate2D(_st,PI*-0.75);
    }

    return _st;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

   st = tile( st - fract(sin(iTime)/50.0)*0.2, 2.0 - fract(sin(iTime)/50.0)*2.8);
   st = rotateTilePattern(st + fract(sin(iTime)/10.0)*0.2);
   vec2 p = 2.0*st - vec2(1.0);
   p.y -= 0.25;

   // background color
   float tt = mod(iTime,8.0)/2.0;
   vec3 bcol = vec3(0.1+0.5*cos(iTime)*0.2);

   // animate
   p *= vec2(0.5,1.5) + tt*vec2(1.0,-1.0);

   // shape
   float a = atan(p.x,p.y)/PI;
   float r = length(p);
   float h = abs(a);
   float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);

   // color
   float s = 1.0-0.5*clamp(r/d,0.0,1.0);
   s = 0.75 + 0.75*p.x;
   s *= 1.0-0.25*r;
   s = 0.5 + 0.6*s;
   s *= 0.5+0.5*pow( 1.0-clamp(r/d, 0.0, 1.0 ), 0.1 );
   //vec3 hcol = vec3(0.3*r,0.1*s,0.5*s)*r*3.0;
    vec3 hcol = vec3(1.0)*s;


   vec3 col = mix( bcol, hcol, smoothstep( -0.1, 0.01, d-r) );
   fragColor = vec4(col,1.0);
}

#define opRep(p, c) mod(p,c)-.5*c

float sdTorus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
mat3 rotMatrix(vec3 axis, float angle) {
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;
    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}
float map(vec3 p) {
    mat3 rot = rotMatrix(vec3(1.,0.,0.), 190.);
    rot *= rotMatrix(vec3(0.,1.,0.), sin(iTime)*3.);
    p*=rot;
    p = opRep(p, vec3(.5));
	return sdTorus(p, vec2(.3, .02));
}
vec2 trace (vec3 o, vec3 r) {
	float t = 0.;
    float i;
    float precis = .01;
    for (i = 0.; i < 64.; ++i) {
        o.z *= sin(iTime);
    	vec3 p = o + r * t;
        float d = map(p);
        if (d<precis) break;
        t += d;
    }
    return vec2(t, i);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord-iResolution.xy )/iResolution.y;
	vec3 o = vec3(0.0, -0.015, -0.2);
    vec3 r = normalize(vec3(uv, 1.));
    vec2 t = trace(o, r);

    vec3 col = vec3(.2,.1,.4)*cos(iTime)*0.5+0.1;

    fragColor = vec4(col,1.0)*t.x;
}

//Setup=========
const int n = 8;
const float rotSpeed = 0.5;
const float circleBorder = 0.75;
const float lineBorder = 0.025 / float(n);
const vec3 backCol = vec3(0.1, 0.1, 0.1);
const vec3 lineCol = vec3(0.0, 0.75, 0.0);
const vec3 circCol = vec3(0.75, 0.0, 1.0);
//==============

const float nf = float(n);
const float r = 1.0 / float(n * 2);
const float rSqr = r * r;
const float rSqr2 = rSqr * circleBorder;

#define M_PI 3.141593

vec3 drawCircles(vec2 uv)
{
    // Tiles and shifts the coordinate system
    uv = (fract(uv * nf) - 0.5) / nf;

    float t  = iTime,
    // Outer Radius
      rtime  = abs(cos(t * rotSpeed)) * rSqr,

    // Inner Radius
      rtime2 = abs(cos(t * rotSpeed)) * rSqr2,

    // Short for uv.x * uv.x + uv.y * uv.y
      circle = dot(uv,uv);

    // Rotates the coordinate system so the lines rotate
    uv *= mat2(cos(t),-sin(t),sin(t),cos(t));

    // First step gives 1.0 for every coord outside of the 
    // circle minus border thickness

    // Second step gives 1.0 for every coord inside the circle

    // Multiplied together they give 1.0 only for coords
    // on the border of the circle
#define CircleStep(v) step(rtime2, abs(v)) * step(abs(v), rtime) 

    // I don't fully understand why this works
#define LineStep(v) step(abs(v), lineBorder)

    float lines = LineStep(uv.x)
        	    + LineStep(uv.y)
                + LineStep((uv.x+uv.y)*.707)
                + LineStep((uv.x-uv.y)*.707);

    // Clamping prevents lines from drawing over each other
    lines = clamp(lines, 0.0, 1.0);

    // Returns 1.0 only if the current coord is inside radius
#define isInsideCircle(r) step(0.0, r - circle)

    return backCol
         + circCol * CircleStep(circle)
         + lineCol * isInsideCircle(rtime2)
                   * lines;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.x;

    vec3 col = drawCircles(uv);

    fragColor = vec4(col,1.0);
}

float mapw = 0.0;
float mapc = 0.0;
float mapl = 0.0;
float mapv = 0.0;

mat2 rot(float x) {
    return mat2(cos(x), sin(x), -sin(x), cos(x));
}

float bump(vec3 p) {
    vec2 uv = (p.xz - vec2(0.0, iTime * 1.2)) * 0.05;
    uv.y -= cos(p.x * 3.141592 * 0.5) * 0.02;
    float tex = dot(texture(iChannel1, uv).xyz, vec3(1.0)) / 3.0;
    return (tex * 2.0 - 1.0) * 0.005;
}

vec2 wiremap(vec3 p) {
    return p.xy * rot(0.6 + sin(p.z / 3.141592 * 4.0) * 0.125);
}

float vent(vec3 p) {
	return max(abs(abs(p.x) - 2.0) - 0.02, abs((fract(p.y * 8.0 - 0.3) - 0.5) / 8.0) - 0.02);
}

float map(vec3 p) {
    p.x += sin(p.z) * 0.5;
	float t = 2.0 - length(p.xy);
    float f = p.y + bump(p);
    float e = max(abs(abs(p.x) - 20.0) - 18.7, abs(p.y) - 0.3);
    float c = length(vec2(p.y - 0.5, (fract(p.z / 3.0) - 0.5) * 3.0)) - 0.75;
    vec2 k = wiremap(p);
    float s = length(vec2(k.y - 1.9, abs(k.x) - 0.025)) - 0.015;
    float l = length(vec3(k.y - 1.85, k.x, (fract(p.z / 4.0) - 0.5) * 4.0)) - 0.05;
    float v = min(vent(p), vent(p.xzy));
    t = max(t, -c);
    t = min(t, e);
    mapv = max(sign(t - v), 0.0);
    t = min(t, v);
    mapw = max(t - f, 0.0);
    t = min(t, f);
    mapc = max(sign(t - s), 0.0);
    t = min(t, s);
    mapl = max(sign(t - l), 0.0);
    return min(t, l);
}

vec2 mapuv(vec3 p, vec3 sn) {
    p.x += sin(p.z) * 0.5;
	float t = 2.0 - length(p.xy);
    float f = p.y + bump(p);
    float e = max(abs(abs(p.x) - 20.0) - 18.7, abs(p.y) - 0.3);
    float c = length(vec2(p.y - 0.5, (fract(p.z / 3.0) - 0.5) * 3.0)) - 0.75;
    float v = min(vent(p), vent(p.xzy));
    t = max(t, -c);
    vec2 bt = mix(p.yz, p.xz, max(sign(sn.y), 0.0));
    t = min(t, f);
    bt = mix(p.xz, bt, max(sign(t - e), 0.0));
    t = min(min(t, f), e);
    bt = mix(p.yz, bt, max(sign(v - t), 0.0));
    return bt;
}

vec3 normal(vec3 p)
{
	vec3 o = vec3(0.01, 0.0, 0.0);
    return normalize(vec3(map(p+o.xyy) - map(p-o.xyy),
                          map(p+o.yxy) - map(p-o.yxy),
                          map(p+o.yyx) - map(p-o.yyx)));
}

float trace(vec3 o, vec3 r) {
    float t = 0.0;
    for (int i = 0; i < 32; ++i) {
        t += map(o + r * t);
    }
    return t;
}

float fog(vec3 w, vec3 sn, float f) {
    w.x += sin(w.z) * 0.5;
    float h = max(2.3 - abs(w.x), 0.0);
    return f * h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 1.2));
    vec3 o = vec3(0.0, 1.0, iTime * 0.5);

    o.x -= sin(o.z) * 0.5;
    o.y += sin(o.z * 4.0) * 0.02;
    float ms = (iMouse.x / iResolution.x * 2.0 - 1.0) * sign(iMouse.z);
    r.xz *= rot(sin(o.z) * 0.5 + ms);
    r.xy *= rot(-sin(o.z) * 0.1);

    vec3 lcol = vec3(2.0, 2.0, 2.0);

    float t = trace(o, r);
    float tw = mapw;
    float tc = mapc;
    float tl = mapl;
    float tv = mapv;
    vec3 w = o + r * t;
    vec3 sn = normal(w);
    vec3 tex = texture(iChannel0, mapuv(w, sn)).xyz;
    tex *= tex;
    tex = mix(tex, tex.xxx * 0.125, tv);
    tex = mix(tex, vec3(0.7, 0.7, 0.0), tc);
    tex *= max(dot(-r, sn), 0.0);
    tex = mix(tex, lcol, tl);
    float aoc = map(w + sn * 1.1);
    tex *= 0.5 + aoc;
    float f = 1.0 / (1.0 + t * t * 0.1);

	vec3 rr = reflect(r, sn);
    float rt = trace(w + rr * 0.01, rr);
    float rtw = mapw;
    float rtc = mapc;
    float rtl = mapl;
    float rtv = mapv;
    vec3 rw = w + rr * rt;
    vec3 rsn = normal(rw);
    vec3 rtex = texture(iChannel0, mapuv(rw, rsn)).xyz;
    rtex *= rtex;
    rtex = mix(rtex, rtex.xxx * 0.125, rtv);
    rtex = mix(rtex, vec3(0.7, 0.7, 0.0), rtc);
    rtex = mix(vec3(0.0, 0.0, 0.0), rtex, 1.0-abs(dot(r,sn)));
    rtex *= max(dot(-rr, rsn), 0.0);
    rtex = mix(rtex, lcol, rtl);
    float raoc = map(rw + rsn * 1.1);
    rtex *= 0.5 + raoc;
    float rf = 1.0 / (1.0 + rt * rt * 0.1);

	tex = mix(vec3(0.0), tex, fog(w, sn, f));
	rtex = mix(vec3(0.0), rtex, fog(rw, rsn, rf * f));

    float twf = 1.0 / (1.0 + tw * 20.0);
    vec3 wtex = texture(iChannel1, mapuv(w, sn)).xyz;
    wtex *= wtex;
    vec3 rfc = mix(rtex, wtex, pow(abs(dot(-r, sn)), 3.0));
    vec3 fc = mix(rfc * vec3(0.2, 0.2, 0.1) * 8.0, tex, twf);

    float lt = 0.0;
    for (int i = 0; i < 16; ++i) {
        vec3 p = o + r * lt;
        p.x += sin(p.z) * 0.5;
        p.xy = wiremap(p);
        p.z = (fract(p.z / 4.0) - 0.5) * 4.0;
        float ld = length(vec3(p.x, p.y - 1.85, p.z)) - 0.0125;
        lt += ld * 0.4;
    }
    fc = mix(fc, lcol, 1.0 / (1.0 + lt * lt * 0.5));

    fragColor = vec4(sqrt(fc), 1.0);
}

//------------------------------------------------------------------------
// Camera
//
// Move the camera. In this case it's using time and the mouse position
// to orbitate the camera around the origin of the world (0,0,0), where
// the yellow sphere is.
//------------------------------------------------------------------------
void doCamera( out vec3 camPos, out vec3 camTar, in float time, in float mouseX )
{
    float an = 0.3*iTime + 10.0*mouseX;
	camPos = vec3(3.5*sin(an),1.0,3.5*cos(an));
    camTar = vec3(0.0,0.0,0.0);
}


//------------------------------------------------------------------------
// Background 
//
// The background color. In this case it's just a black color.
//------------------------------------------------------------------------
vec3 doBackground( void )
{
    return vec3( 0.0, 0.0, 0.0);
}

//------------------------------------------------------------------------
// Modelling 
//
// Defines the shapes (a sphere in this case) through a distance field, in
// this case it's a sphere of radius 1.
//------------------------------------------------------------------------
float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}

float opS( float d1, float d2 )
{
    return max(-d1,d2);
}


float doModel( vec3 p )
{
    //return length(p) - 1.0;
    // return length(max(abs(p)-1.0,0.0));
    float a=sdSphere(p,1.5);
    float b=udBox(p,vec3(1.0));
    float c=opS(a,b);
    return c;


}

//------------------------------------------------------------------------
// Material 
//
// Defines the material (colors, shading, pattern, texturing) of the model
// at every point based on its position and normal. In this case, it simply
// returns a constant yellow color.
//------------------------------------------------------------------------
vec3 doMaterial( in vec3 pos, in vec3 nor )
{
    return vec3(0.2,0.07,0.01);
}

//------------------------------------------------------------------------
// Lighting
//------------------------------------------------------------------------
float calcSoftshadow( in vec3 ro, in vec3 rd );

vec3 doLighting( in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in vec3 mal )
{
    vec3 lin = vec3(0.0);

    // key light
    //-----------------------------
    vec3  lig = normalize(vec3(1.0,0.7,0.9));
    float dif = max(dot(nor,lig),0.0);
    float sha = 0.0; if( dif>0.01 ) sha=calcSoftshadow( pos+0.01*nor, lig );
    lin += dif*vec3(4.00,4.00,4.00)*sha;

    // ambient light
    //-----------------------------
    lin += vec3(0.50,0.50,0.50);


    // surface-light interacion
    //-----------------------------
    vec3 col = mal*lin;


    // fog    
    //-----------------------------
	col *= exp(-0.01*dis*dis);

    return col;
}

float calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 20.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
	float res = -1.0;
    for( int i=0; i<90; i++ )          // max number of raymarching iterations is 90
    {
        if( h<precis||t>maxd ) break;
	    h = doModel( ro+rd*t );
        t += h;
    }

    if( t<maxd ) res = t;
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ) + 
					  v2*doModel( pos + v2*eps ) + 
					  v3*doModel( pos + v3*eps ) + 
					  v4*doModel( pos + v4*eps ) );
}

float calcSoftshadow( in vec3 ro, in vec3 rd )
{
    float res = 1.0;
    float t = 0.0005;                 // selfintersection avoidance distance
	float h = 1.0;
    for( int i=0; i<40; i++ )         // 40 is the max numnber of raymarching steps
    {
        h = doModel(ro + rd*t);
        res = min( res, 64.0*h/t );   // 64 is the hardness of the shadows
		t += clamp( h, 0.02, 2.0 );   // limit the max and min stepping distances
    }
    return clamp(res,0.0,1.0);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m.x );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = doBackground();

	// raymarch
    float t = calcIntersection( ro, rd );
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);

        // materials
        vec3 mal = doMaterial( pos, nor );

        col = doLighting( pos, nor, rd, t, mal );
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.4545) );

    fragColor = vec4( col, 1.0 );
}

// as if by magic... snow + beer
// Del 01/03/2018

float Bub(vec2 uv,float scale)
{
    float time = iTime*0.75;
    uv.y-=time*2./scale;
    uv.x+=sin(uv.y+time*.95)*0.025;	///scale;
	uv*=scale;
    vec2 s=floor(uv);
    vec2 f=fract(uv);
    float k=3.0;
	vec2 p =.5+.35*sin(11.*fract(sin((s+scale)*mat2(7.0,3.0,6.0,5.0))*5.))-f;
    float d=length(p);
    d-= sin(f.y+time)*0.005;
    k=min(d,k);
	k=smoothstep(0.0,k,sin(f.x+f.y)*0.01);
   	return k;
}

vec3 Bubbles(vec2 uv,float head)
{
	float c = Bub(uv,30.)*.3;
	c+=Bub(uv,20.)*.5;
	c+=Bub(uv,15.)*.8;
	c+=Bub(uv,10.);
	c+=Bub(uv,8.);
	c+=Bub(uv,6.);
	c+=Bub(uv,5.);
    c = clamp(c,0.0,1.0);

    vec3 background = vec3(0.75,0.65,0.2);

    head*=2.0;
    head = clamp(head,-0.2,1.0);
    background = mix(background,vec3(1.0),head);

    float bub_brightness = 1.6;

    vec3 scol = background * bub_brightness; //vec3(1.0,1.0,1.0);
    scol = mix(background,scol,c);
    scol = clamp(scol,0.0,1.0);
    return scol;
}


//snow original -> http://glslsandbox.com/e#36547.1
float snow(vec2 uv,float scale)
{
    float time = iTime*0.75;
	uv+=time/scale;
    uv.y+=time*2./scale;
    uv.x+=sin(uv.y+time*.5)/scale;
	uv*=scale;
    vec2 s=floor(uv);
    vec2 f=fract(uv);
    float k=3.0;
	vec2 p =.5+.35*sin(11.*fract(sin((s+scale)*mat2(7.0,3.0,6.0,5.0))*5.))-f;
    float d=length(p);
    k=min(d,k);
	k=smoothstep(0.,k,sin(f.x+f.y)*0.01);
   	return k;
}


vec3 _Snow(vec2 uv,vec3 background)
{
	float c = snow(uv,30.)*.3;
	c+=snow(uv,20.)*.5;
	c+=snow(uv,15.)*.8;
	c+=snow(uv,10.);
	c+=snow(uv,8.);
	c+=snow(uv,6.);
	c+=snow(uv,5.);
    c = clamp(c,0.0,1.0);
    vec3 scol = vec3(1.0,1.0,1.0);
    scol = mix(background,scol,c);
	return scol;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy - iResolution.xy)/ iResolution.y;

    vec2 uv1 = uv+vec2(0.0,1.0);
    float d = 0.25+length(uv1)*0.4;
    vec3 col = vec3(0.3,0.2,0.25)*d*d;
    col = _Snow(uv1*0.5,col); 

    uv.y -= 1.0;
    float scale = 2.9-uv.y;
    if (scale<3.5)
        scale=3.5;

    uv.x *= scale*0.55;
    float c = abs(uv.x);		// dirty shading bodge...
    if (c-0.5 < 0.5 && uv.y <-0.5)
    {
		uv.x = acos(uv.x)/3.14159;
        c = acos(c);
        float head = 0.9+(uv.y);
        float headmod = 0.5+sin(uv.x*11.0 + iTime)*0.5;
        head+=headmod*0.1;
        float c2 = smoothstep(0.0,1.0,c);
        col = Bubbles(uv*2.0, head)*(c2+0.4);
    }
    fragColor =vec4(col,1.0);
}

// moon, now with a slightly less rubbish (twinkling) starfield.

float _Stars(vec2 uv,float scale)
{
    uv.y+=2./scale;
    uv.x+=sin(uv.y)/scale;
	uv*=scale;
    vec2 s=floor(uv);
    vec2 f=fract(uv);
	vec2 p =.5+.35*sin(11.*fract(sin((s+scale)*mat2(7.0,3.0,6.0,5.0))*5.))-f;
    float d=length(p);
    float k=min(d,3.0);
	k=smoothstep(0.,k,sin(f.x+f.y)*0.01);
   	return k;
}

vec3 NewStars(vec2 uv,vec3 background)
{
    float twinkle = 0.5+sin(iTime*1.61+uv.x + uv.y*18.0)*0.5;
    twinkle = (twinkle*0.35)+0.65;
	float c = _Stars(uv,30.)*.3;
	c+=_Stars(uv,16.6);
	c+=_Stars(uv,7.8);
    vec3 scol = vec3(0.8,0.8,1.0)*twinkle;
    scol = mix(background,scol,c);
	return scol;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float speed = iTime*0.01;
    float spheresize = 0.2;
    float texturescale = 0.31;
	vec2 p = ( fragCoord -.5*iResolution.xy ) / iResolution.y;
	vec4 col = vec4(0.0);
    col.rgb = NewStars(p*0.51,col.rgb);
    p.x-=0.275*(iResolution.x/iResolution.y);			// offset sphere

    float l = spheresize-dot(p,p);
