#define SIZE 1.
#define FACTOR 2.3
#define PI 3.1415926535897932384626433832795
#define SCALE 0.1
#define POSX 0.81
#define POSY 0.46


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = SCALE + 600.;
    if (exp(iTime/3.) < 600.)
    	scale = SCALE + exp(iTime/3.) - 1.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 xy = (uv - 0.5) * 2.0;
    xy.x *= iResolution.x/iResolution.y;
    xy /= scale;
    xy += vec2(POSX, POSY);
    vec2 center = vec2(0.0, 0.0);

    vec3 col = vec3(0., 0., 0.);
    float size = SIZE;

    for(int i =0; i < 100; i++)
    {
        float x = step(center.x - size/2., xy.x) - step(center.x + size/2., xy.x);
        float y = step(center.y - size/2., xy.y) - step(center.y + size/2., xy.y);
        if (x*y == 1.)
        {

            col = vec3(sin(iTime)*length(xy)/2., length(xy)/2., length(xy)/2.);
            break;
        }

        float i_float = float(i) + 1.;
        float xDist = xy.x - center.x;
        float yDist = xy.y - center.y;
        float xRot = xDist * cos(-PI/4.) - yDist * sin(-PI/4.);
        float yRot = xDist * sin(-PI/4.) + yDist * cos(-PI/4.);

        if (sign(xRot) * sign(yRot) == 1.)
        {
        	center.y = center.y + sign(xRot)*(size/2. + size/(2.*FACTOR));
            //col = vec3(10./i_float, 0., 0.);
        }
        else if (sign(xRot) * sign(yRot) == -1.)
        {
            center.x = center.x + sign(xRot)*(size/2. + size/(2.*FACTOR));
            //col = vec3(0., 10./i_float, 0.);
        }
        else
            col += vec3(0., 0., 0.);

        size = size/FACTOR;
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

