#define LAYER 22.0
#define lerp mix
float Wave(float layer,vec2 uv,float val){
	float amplitude =  layer*layer*0.00004;
	float frequency = val*200.*uv.x/layer;
	float phase = 9.*layer+ iTime/val;
	return amplitude*sin(frequency+phase); 
}
float Circle(vec2 uv,vec2 center,float size,float blur){
	uv = uv - center;
	uv /= size;
	float len = length(uv);
	return smoothstep(1.,1.-blur,len);
}
float AngleCircle(vec2 uv,vec2 center,float size,float blur){
	uv = uv - center;
	uv /= size;
	float deg = atan(uv.y,uv.x) + iTime * -0.1;
	float len = length(uv);
	float offs =( sin(deg*9.)*3.+sin(deg*11.+sin(iTime*6.)*.5))*0.05;
	return smoothstep(1.+offs,1.-blur+offs,len);
}
float Remap(float a,float b,float c,float d,float val){
	return (val-a)/(b-a) * (d-c) + c;
}
float DrawCloud(vec2 uv,vec2 center,float size){
	uv = uv - center;
	uv /= size;
	float col = Circle(uv,vec2(0.,0.),0.2,0.05);
	col =col *  smoothstep(-0.1,-0.1+0.01,uv.y);
	col += Circle(uv,vec2(0.15,-0.05),0.1,0.05);
	col += Circle(uv,vec2(0.,-0.1),0.11,0.05);
	col += Circle(uv,vec2(-0.15,-0.1),0.1,0.05);
	col += Circle(uv,vec2(-0.3,-0.08),0.1,0.05);
	col += Circle(uv,vec2(-0.2,0.),0.15,0.05);
	return col;
}
float DrawClouds(vec2 uv){
	uv.x += 0.03*iTime;
	uv.x = fract(uv.x+0.5) - 0.5;
	float col = DrawCloud( uv,vec2(-0.4,0.3),0.2);
	col += DrawCloud( uv,vec2(-0.2,0.42),0.2);
	col += DrawCloud( uv,vec2(0.0,0.4),0.2);
	col += DrawCloud( uv,vec2(0.15,0.3),0.2);
	col += DrawCloud( uv,vec2(0.45,0.45),0.2);
	return col;
}
vec4 DrawSun(vec2 uv,vec2 center){
	vec2 sunPos = center;
	float sun = Circle(uv,sunPos,0.06,0.05);
	float sunCircle = AngleCircle(uv,sunPos,0.08,0.05);
	vec3 col = sunCircle * vec3(0.9,0.6,0.15);
	col = lerp( col ,vec3(0.98,0.9,0.1),sun);
    return vec4(col,clamp(sun+sunCircle,0.,1.));
}
