float fog(vec3 p, vec3 e){
    bool useFogCoordinate = true;
    float fdepth;
    if(useFogCoordinate)
        fdepth = length(p-e);
    else
        fdepth = abs(p.z);

    float fog;
    float density = 0.02;
    fog = exp(-density * fdepth);
    return clamp(fog, 0.0, 1.0);
}

float cube(vec3 p){
    float s = 1.0;
    vec3 d = abs(p) - vec3(s);
    return min(max(d.x, max(d.y,d.z)), 0.0)
        + length(max(d,0.0));
}

float sphere(vec3 p){
    return length(vec3(1.0) - p) - 1.0;
}

float plane(vec3 p){
    float s = -1.0;
    float d = abs(p.z - s);
    return max(d,0.0);
}

float distanceToNearestSurface(vec3 p){

    float dist = 10.0;
   	vec3 q = vec3(mod(p.x, dist) - dist/2.0, p.yz);
//    return min(min(cube(q), sphere(q)),plane(q));
    return min(cube(q), sphere(q));
    //return plane(p);
    //return cube(p)/sphere(p);
	//return max(-sphere(p),cube(p));
}

vec3 computeSurfaceNormal(vec3 p){
    float d = distanceToNearestSurface(p);
    return normalize(vec3(
        distanceToNearestSurface(p+deltax)-d,
        distanceToNearestSurface(p+deltay)-d,
        distanceToNearestSurface(p+deltaz)-d
    ));
}

vec3 computeLambert(vec3 p, vec3 n, vec3 l, vec3 e){


    vec3 lightVector = normalize(l-p);
    vec3 eyeVector = normalize(e-p);
    vec3 halfVector = normalize(lightVector + eyeVector);

    float nDotLV = max(0.0, dot(lightVector, n));
    float nDotHV = max(0.0, dot(halfVector, n));

    float attenuation = 1.0;
    float shininess = 10.0;
    float pf;
    if(nDotLV == 0.0)
        pf = 0.0;
 	else
        pf = pow(nDotHV, shininess);

    vec3 ambient = vec3(0.1) * attenuation;
    vec3 diffuse = vec3(1.0,0,0) * nDotLV * attenuation;
    vec3 specular = vec3(1.0,1.0,1.0) * pf * attenuation*1.0;
    return min(ambient + diffuse + specular, vec3(1.0));
}

vec3 intersectsWithWorld(vec3 p, vec3 dir){
  float dist = 0.0;
  for(int i = 0; i < 20; i++){
    float nearest = distanceToNearestSurface(p + dir*dist);
    if(nearest < 0.01)
    {
        //return (vec3(1.0)+computeSurfaceNormal(p+dir*dist))/2.0;
    	vec3 hit = p+dir*dist;
                //vec3 light = vec3(100.0,
                  //          30.0,
                    //        50.0);
        vec3 light = vec3(100.0*sin(iTime),
                            30.0*cos(iTime),
                            50.0*(1.5+cos(iTime)));
        vec3 pixelColour = computeLambert(hit, computeSurfaceNormal(hit), light, p);

    	return mix(vec3(0.1), pixelColour, fog(hit, p));
    }
    dist += nearest;
  }
  return vec3(0.0);
}


