// list of cylinders
// Each cylinder is decribed by a pair of vec4's:
// vec4( start position, radius ), vec4( end pos, ignored )
vec4 Scene[] = vec4[](
    vec4(0,-1.3,0,10), vec4(0,-2,0,0),
    vec4(0,-1,0,3.5), vec4(0,-2,0,0),
    vec4(-.7071,-1.+.7071,0,1), vec4(.7071,-1.+3.*.7071,0,0),
    vec4(0,-1,3,.2), vec4(0,4,3,0),
    vec4(2.12,-1,2.12,.2), vec4(2.12,4,2.12,0),
    vec4(3,-1,0,.2), vec4(3,4,0,0),
    vec4(2.12,-1,-2.12,.2), vec4(2.12,4,-2.12,0),
    vec4(0,-1,-3,.2), vec4(0,4,-3,0),
    vec4(-2.12,-1,-2.12,.2), vec4(-2.12,4,-2.12,0),
    vec4(-3,-1,0,.2), vec4(-3,4,0,0),
    vec4(-2.12,-1,2.12,.2), vec4(-2.12,4,2.12,0),
    vec4(0,4,0,3.5), vec4(0,5,0,0)
    );


vec3 Sky( vec3 ray )
{
    return vec3(.01);
}



#define FLT_MAX 1e38

float IntersectCylinder( out vec3 normal, vec4 a, vec4 b, vec3 pos, vec3 ray )
{
    vec3 m = (a.xyz+b.xyz)/2.;

    pos -= m; // centre all maths on the cylinder

    float radius = a.w;
    vec3 n = (a.xyz-b.xyz)/2.;
    float l = length(n);
    n /= l;


    // intersect infinite cylinder
    // flatten everything along the axis
    vec3 r = ray-dot(ray,n)*n;
    vec3 p = pos-dot(pos,n)*n;
    float rl = length(r);
    r /= rl;;
    float rdp = dot(r,p);
    float pp = dot(p,p);
    float q = pp - rdp*rdp;
    if ( q >= radius*radius ) return FLT_MAX;

    float d = sqrt(radius*radius-q);
    float front = (-rdp - d)/rl;
    float back = (-rdp + d)/rl;
    normal = normalize(p+front*r*rl);


    // intersect facing plane
    float rdn = dot(ray,n);
    float pdn = dot(pos,n);

    if( rdn < 0. ) { n = -n; rdn = -rdn; pdn = -pdn; }

    float front2 = (-l-pdn)/rdn;

    if ( front2 > front )
    {
        front = front2;
        normal = -n;
    }


    // clip the back sides
    if (
        front > back || // clip to back of cylinder
    	pdn+rdn*front > l // clip to back plane
        )
        return FLT_MAX;

    return front;
}


float Trace( out vec3 normal, vec3 pos, vec3 ray )
{
    normal = vec3(0);
    float t = FLT_MAX;
    for ( int i=0; i < Scene.length(); i += 2 )
    {
        vec3 n;
        float f = IntersectCylinder( n, Scene[i], Scene[i+1], pos, ray );
        if ( f > 0. && f < t )
        {
            t = f;
            normal = n;
            // todo: remember object index for material lookup
        }
    }
    return t;
}


void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
	vec3 ray = vec3( (fragCoord-iResolution.xy*.5)/iResolution.y, 50./36. );
    ray = normalize(ray);

    vec2 a = vec2(sin(iTime*.313)*.3+.08,iTime*.5);
	vec3 cam = 5.*vec3(sin(a.y)*cos(a.x),sin(a.x),cos(a.y)*cos(a.x));
    vec3 target = vec3(-.5,.5,0);

    vec3 camk = normalize(target-cam);
    vec3 cami = normalize(cross(vec3(0,1,0),camk));
    vec3 camj = cross(camk,cami);

    ray = ray.x*cami + ray.y*camj + ray.z*camk;

    vec3 normal = vec3(0);
    float t = Trace(normal,cam,ray);

    vec3 col = Sky(ray);
    if ( length(normal) > 0. )
    {
        vec3 sun = normalize(vec3(-1,2,3));
        vec3 testn = vec3(0);
        Trace(testn,cam+ray*t,sun);

        vec3 light = vec3(.05,.065,.09)*(normal.y*.3+1.); // ambient
        if ( length(testn) == 0. )
        {
        	light += max(0.,dot(normal,sun)) * vec3(1,.95,.92);
        }

        col = light*vec3(1);
        col *= smoothstep(.01,.02,abs(fract( cam+t*ray+.001 )-.5));
    }

    fragColour = vec4(pow(col,vec3(1./2.2)),1.0);
}

