void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float mask = smoothstep( 0.3, 0.31, texture( iChannel0, uv ).x );
	mask = 1.0-mask;


    vec2 sunpos = vec2( sin(iTime*0.2)+1.0, sin(iTime*0.4)+1.0 )*0.5;

    //float sunl = texture( iChannel1, uv ).x;
    float sunl = 1.0-distance( uv, sunpos );
    sunl = smoothstep( 0.2, 0.97, pow(sunl,5.0) );
    float sun = min( mask, smoothstep(0.98,0.99,sunl) );

    float dy = distance( uv.y,sunpos.y );
    float dx = distance( uv.x,sunpos.x );
    float dd = pow(1.0-distance( uv,sunpos ),5.0);

    float z1 = 1.0-smoothstep( 0.3, 0.35, texture( iChannel0, vec2(sunpos.x,sunpos.y+0.01) ).x );
    float z2 = 1.0-smoothstep( 0.3, 0.35, texture( iChannel0, vec2(sunpos.x,sunpos.y-0.01) ).x );
	float sun1 = (z2*0.5+z1*0.5) * smoothstep( 0.98, 0.99, 1.0-distance(uv.x,sunpos.x) ) * dd;


    float q1 = 1.0-smoothstep( 0.3, 0.35, texture( iChannel0, vec2(sunpos.x-0.01,sunpos.y-0.01) ).x );
    float q2 = 1.0-smoothstep( 0.3, 0.35, texture( iChannel0, vec2(sunpos.x+0.01,sunpos.y-0.01) ).x );
	float sun2 = (q2*0.5+q1*0.5) * smoothstep( 0.97, 0.99, 1.0-distance(uv.y+dx,sunpos.y) ) * dd;

    float w1 = 1.0-smoothstep( 0.3, 0.35, texture( iChannel0, vec2(sunpos.x-0.01,sunpos.y+0.01) ).x );
    float w2 = 1.0-smoothstep( 0.3, 0.35, texture( iChannel0, vec2(sunpos.x+0.01,sunpos.y+0.01) ).x );
	float sun3 = (w2*0.5+w1*0.5) * smoothstep( 0.97, 0.99, 1.0-distance(uv.y-dx,sunpos.y) ) * dd;


    float sunadd = pow(sunl,2.0)*(z2*0.2+z1*0.2+q2*0.2+q1*0.2+w2*0.2+w1*0.2)*0.7 + (sun1+sun2+sun3)*0.3;
    //sunadd = pow(sunadd,5.0 );

 //   sun = max( sun, sun1 );
  //  sun = max( sun, sun2 );
   // sun = max( sun, sun3 );

    float cld = (smoothstep( 0.3, 0.5, texture( iChannel0, uv ).x ))*0.5;
    float cll = dd * (1.0-smoothstep( 0.3, 0.5, texture( iChannel0, uv ).x ))*(1.0-mask);

    float col = sun*0.5 + sunadd*0.9;

    vec3 sky = vec3( 0.0, 0.5, 0.7 );
    vec3 skc = vec3( 0.0, 0.0, 0.0 );
    skc = mix( skc, vec3(3.5)*(cll*3.0+cld), cld );
    skc = mix( skc, vec3(1.0), col );
    sky = skc+col*0.3 + sky*smoothstep( 0.9,0.0,skc );

    // Output to screen
    fragColor = vec4( sky, 1.0 );
}

