vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec3 opTwist( vec3 p )
{
    p.y += 0.5;
    float scale = sin(iTime*1.25)*0.15;

    float  c = cos(scale*p.y+scale);
    float  s = sin(scale*p.y+scale);
    mat2   m = mat2(c,-s,s,c);
    p = vec3(m*p.zy,p.x);
    p.y -= 0.5;
    return p;
}


vec3 rotateX(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(p.x, ca * p.y - sa * p.z, sa * p.y + ca * p.z);
}
vec3 rotateY(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x + sa * p.z, p.y, -sa * p.x + ca * p.z);
}
vec3 rotateZ(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x - sa * p.y, sa * p.x + ca * p.y, p.z);
}



vec2 doModel( vec3 p )
{
    vec2 res = vec2( sdPlane(p+vec3(0.0,0.45,0.0)), 1.0);	// floor...

    float t1 = fract(iTime*0.15)*TAU;
    float t2 = fract(iTime*0.31)*TAU;

    vec3 r1 = rotateY(p,t1)	;//iTime*0.8);
    if (iMouse.w>0.5)
    {
		r1 = rotateY(r1, sin(t2+p.y*1.25));
    	r1 = opTwist(r1);
    }

	float symbolDist = NewSymbol(r1.xy);
    float dep = 0.02;
    vec2 e = vec2( symbolDist, abs(r1.z) - dep );
    symbolDist = min(max(e.x,e.y),0.0) + length(max(e,0.0));
    symbolDist -= 0.01;        

    res = opU(res,vec2(symbolDist,2.0));
    return res;
}

