float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrTorusDf (vec3 p, float ri, float rc);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);

vec3 qHit;
float tCur, dstFar;
int idObj;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, t, w;
  dMin = dstFar;
  q = p;
  d = PrSphDf (q, 0.1);
  DMINQ (4);
  for (int k = 0; k < 3; k ++) {
    q = p;
    if (k == 1) q.xy = Rot2D (q.xy, 0.5 * pi);
    else if (k == 2) q.yz = Rot2D (q.yz, -0.5 * pi);
    d = PrTorusDf (q.xzy, 0.04, 2.);
    DMINQ (4);
    t = 1.5 * tCur;
    if (k == 1) t += (2./3.) * pi;
    else if (k == 2) t += (4./3.) * pi;
    q.xz = Rot2D (q.xz, t);
    w = q.x + 0.5 * abs (q.z) + 0.07;
    d = max (max (PrCylDf (q.xzy, 1.93, 0.03 * (0.1 + 0.9 * min (1., -10. * w))), w),
       0.2 - length (q.xz + vec2 (1.2, 0.)));
    DMINQ (k + 1);
  }
  return 0.6 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 250; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec2 BlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u, qnBlk;
  vec2 qBlk;
  float dn, df;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = 32. / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  if (df > 0. && dn < df) {
    qnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, qnBlk), dot (u.yzx, qnBlk));
  }
  return qBlk;
}

vec3 BgCol (vec3 rd)
{
  return vec3 (0.9, 0.9, 1.) * (0.2 + 0.2 * (rd.y + 1.) * (rd.y + 1.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 ltPos[4], ltCol[4], ltDir, dfSumC, spSumC, col, vn;
  vec2 qBlk;
  float dstObj, dfSum, spSum, at, t;
  ltPos[0] = 5. * vec3 (1., 1., 1.);
  ltPos[1] = 5. * vec3 (-1., 1., -1.);
  ltPos[2] = 5. * vec3 (1., -1., -1.);
  ltPos[3] = 5. * vec3 (-1., -1., 1.);
  t = 0.4 * tCur;
  for (int k = 0; k < 4; k ++) {
    ltPos[k].xy = Rot2D (ltPos[k].xy, t);
    ltPos[k].yz = Rot2D (ltPos[k].yz, t);
    ltPos[k].zx = Rot2D (ltPos[k].zx, t);
  }
  ltCol[0] = vec3 (1., 1., 0.3);
  ltCol[1] = vec3 (1., 0.3, 0.3);
  ltCol[2] = ltCol[1].zxy;
  ltCol[3] = ltCol[1].yzx;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) col = ltCol[0];
    else if (idObj == 2) col = ltCol[1];
    else if (idObj == 3) col = ltCol[2];
    else if (idObj == 4) col = ltCol[3];
    if (idObj < 4) {
      qBlk = step (0.1, mod (0.6 * BlkHit (ro, reflect (rd, vn)) + 0.1/2., 1.));
      col4 = vec4 (col, 0.5) * mix (0.5 * mix (1., 0.7 + 0.3 * qBlk.x * qBlk.y,
         1. - smoothstep (0.5, 0.7, - dot (rd, vn))), 1., 
         0.5 + 0.5 * SmoothBump (0.3, 0.7, 0.01, mod (4. * length (qHit.xz), 1.)));
    } else col4 = vec4 (col * (0.9 + 0.1 * SmoothBump (0.25, 0.75, 0.1,
       mod (128. * atan (qHit.z, qHit.x) / pi, 1.))), 0.5);
    dfSum = 0.;
    spSum = 0.;
    dfSumC = vec3 (0.);
    spSumC = vec3 (0.);
    for (int k = 0; k < 4; k ++) {
      ltDir = normalize (ltPos[k]);
      at = smoothstep (0.7, 0.8, dot (normalize (ltPos[k] - ro), ltDir));
      t = at * max (dot (vn, ltDir), 0.);
      dfSum += t;
      dfSumC += ltCol[k] * t;
      t = at * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
      spSum += t;
      spSumC += ltCol[k] * t;
    }
    col = mix (col4.rgb * (0.05 + 0.7 * dfSum) + 0.1 * spSum,
       0.05 + 0.5 * dfSumC + 0.1 * spSumC, col4.a);
  } else {
    col = BgCol (rd);
    qBlk = step (0.1, mod (0.5 * BlkHit (ro, rd) + 0.1/2., 1.));
    col *= 0.95 + 0.05 * qBlk.x * qBlk.y;
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvs, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.1 * pi;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += 1.5 * pi * mPtr.y;
  } else {
    az += 0.03 * pi * tCur;
    el += 0.2 * pi * sin (0.04 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -8.);
  rd = vuMat * normalize (vec3 (uv, 3.5));
  dstFar = 25.;
  col = ShowScene (ro, rd);
  uvs *= uvs * uvs;
  col *= mix (0.8, 1., pow (1. - 0.5 * length (uvs * uvs), 4.));
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

#define INFINITY 10.0
#define MAX_ITERATIONS 128

//http://www.skytopia.com/project/fractal/mandelbulb.html
int mandel(float x0, float y0, float z0) {
    float x;
    float y;
    float z;
    float r;
    float theta;
    float phi;
    float xnew;
    float ynew;
    float znew;

    x = x0; y = y0; z = z0;

    // play with this
    float n = 8.0;

    for (int i = 0; i < MAX_ITERATIONS; ++i) {

        r = sqrt(x*x + y*y + z*z);
        theta = atan(sqrt(x*x + y*y), z);
        phi = atan(y, x);

        xnew = x0 + pow(r, n) * sin(theta * n) * cos(phi * n);
        ynew = y0 + pow(r, n) * sin(theta * n) * sin(phi * n);
        znew = z0 + pow(r, n) * cos(theta * n);

        if (xnew*xnew + ynew*ynew + znew*znew > INFINITY) {
            return i;
        }

        x = xnew;
        y = ynew;
        z = znew;
    }

    return MAX_ITERATIONS;
}

// http://www.iquilezles.org/www/articles/palettes/palettes.htm
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
