    fragColor = vec4(0.0);

    // regular
    z = sin(0.5 * iTime);
    t = float(mandel(x, y, z)) / float(MAX_ITERATIONS);            
    fragColor.rgb += palette(0.5+0.5*cos(iTime), vec3(0.8,0.5,0.4),vec3(0.4,0.2,0.4),vec3(1.0,2.0,1.0),vec3(0.0,0.25,0.25) );
    if (t < 1.0) {
        fragColor.rgb *= 0.8;
    }


    // upside down and backwards... ish
	z = cos(0.5 * iTime);
    t = float(mandel(y, x, z)) / float(MAX_ITERATIONS);            
  	fragColor.rgb += palette(0.5+0.5*sin(iTime), vec3(0.8,0.5,0.4),vec3(0.2,0.4,0.2),vec3(2.0,1.0,1.0),vec3(0.0,0.25,0.25) );
