//wang from https://www.shadertoy.com/view/ldjczd
float wang(uint u)
{
    uint seed = (u*1664525u);

    seed  = (seed ^ 61u) ^(seed >> 16u);
    seed *= 9u;
    seed  = seed ^(seed >> 4u);
    seed *= uint(0x27d4eb2d);
    seed  = seed ^(seed >> 15u);

    float value = float(seed) / (4294967296.0);
    return value;
}

vec2 pointgen(uint i)
{
    return vec2(wang(i), wang(i+10000u));
}

#define MAXPOINTS 512u

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y*=(iResolution.y/iResolution.x);

    uint minknn = MAXPOINTS;
    float minval = 100000.0;

    float parameter1 = iMouse.x/iResolution.x;
    float parameter2 = iMouse.y/iResolution.y + 1.0;

    uint escape = 128u;

    if(parameter1>0.0)
        escape = uint(parameter1*parameter1*512.0f);

    for(uint i = 0u; i<MAXPOINTS; i++)
    {
        vec2 pos = pointgen(i);
        pos = cos(pos*(iTime*0.05+16.0));
        float len = cos(length(pos-uv)*parameter2*3.0 + parameter1*2.0); 
        if(len<minval)
        {
            minval = len;
            minknn = i;
        }

        if(i>escape)
            break;
    }

    // Time varying pixel color
    vec3 col = vec3(pointgen(minknn),1.0);
	col = clamp(col+vec3(0.1),0.0,1.0);
    // Output to screen
    fragColor = vec4(col,1.0);
}

